INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1586, '201210/Terminator 2 Den zuctovani -- The Judgement Day.m4v', 'Terminator 2 Den zuctovani', 4, 29, 'cz', NULL, 'en', 'Terminator 2 The Judgement Day', 'http://www.csfd.cz/film/1248-terminator-2-den-zuctovani/', 'Terminator 2 Den zuctovani -- The Judgement Day.jpg', 12, 'Deset let po neúspěšném pokusu zabít Sarah Connorovou, matku budoucího vůdce lidstva ve válce proti strojům, se do Los Angeles vrací z budoucnosti nový Terminátor, typ T-1000, aby zlikvidoval už narozeného Johna Connora. Dospělý Connor v roce 2029 na svoji ochranu posílá do minulosti druhého terminátora T-800. Oba stroje nezávisle na sobě pátrají po malém Johnovi. Chlapec zjišťuje, že T-800 je naprogramován na jeho ochranu a s jeho pomocí osvobodí svou matku z psychiatrického ústavu, kde je léčena z údajné schizofrenie. T-800 oběma vysvětluje, co přinese budoucnost. Tu je však možné změnit...a při tom všem hrdiny stíhá nemilosrdný a zřejmě nezničitelný T-1000.(oficiální text distributora)', NULL, 1991, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1587, '201210/Terminator 4 Temny pociatok -- Salvation en.m4v', 'Terminator 4 Salvation Cartoon', 4, 29, 'en', 'cz', 'en', 'Terminator 4 Salvation', 'http://www.imdb.com/title/tt0438488/', 'Terminator 4 Temny pociatok -- Salvation en.jpg', 9, 'Directed by McG. With Christian Bale, Sam Worthington, Anton Yelchin, Moon Bloodgood. After Skynet has destroyed much of humanity in a nuclear holocaust, a group of survivors led by John Connor struggles to keep the machines from finishing the job.', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1588, '201210/Terminator 4 Temny pociatok -- Salvation.m4v', 'Terminator 4 Temny pociatok Cartoon', 4, 29, 'cz', NULL, 'en', 'Terminator 4 Salvation', 'http://www.csfd.cz/film/227774-terminator-salvation/', 'Terminator 4 Temny pociatok -- Salvation.jpg', 19, 'Film „Terminator Salvation“ je zasazen do post-apokalyptického roku 2018. Christian Bale hraje Johna Connora, muže určeného osudem k tomu, aby vedl lidský odpor vůči Skynetu a jeho armádě Terminátorů. Ale budoucnost, na kterou byl Conner připravován, se částečně změní tím, že se objeví Marcus Wright (Sam Worthington), cizinec, jehož poslední vzpomínkou je, že byl mezi mrtvými. Conner musí rozhodnout, zda byl Marcus poslán z budoucnosti nebo zachráněn z minulosti. Jak Skynet připravuje svůj konečný útok, Conner a Marcus se oba vydávají na odyseu, která je přivede do srdce Skynetu, kde se dozvídají hrozivé tajemství, které stojí za možným vyvražděním lidstva.(oficiální text distributora)', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1589, '201210/X-MEN Prvni trida -- First Class en.m4v', 'X-MEN First Class', 4, 28, 'en', 'cz', 'en', 'X-MEN First Class', 'http://www.imdb.com/title/tt1270798/', 'X-MEN Prvni trida -- First Class en.jpg', 5, 'Directed by Matthew Vaughn.  With James McAvoy, Michael Fassbender, Jennifer Lawrence, Kevin Bacon. In 1962, the United States government enlists the help of Mutants with superhuman abilities to stop a malicious dictator who is determined to start world war III.', NULL, 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1590, '201210/X-MEN Prvni trida -- First Class.m4v', 'X-MEN Prvni trida', 4, 28, 'cz', NULL, 'en', 'X-MEN First Class', 'http://www.csfd.cz/film/251188-x-men-prvni-trida/', 'X-MEN Prvni trida -- First Class.jpg', 25, 'X-Men: První třída se dějově se vrací ještě před film X-Men Origins: Wolverine a odehrává se v polovině minulého století. V době, kdy mladý Charles Xavier (James McAvoy) a Erik Lensherr (Michael Fassbender) byli velmi blízcí přátelé a teprve odkrývali své jedinečné schopnosti. V době studené války, kdy společně s dalšími mutanty museli čelit největší hrozbě, jakou kdy zatím lidstvo poznalo. Proč se ale z nejlepších přátel stali nepřátelé na život a na smrt? Co předcházelo okamžiku, kdy se z Charlese stal Profesor X a z Erika Magneto? Válka mezi X-Meny a Bratrstvem začíná!(oficiální text distributora)', NULL, 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1591, '201210/12 opic -- 12 monkeys en.m4v', '12 monkeys', 4, 0, 'en', 'cz', 'en', '12 Monkeys', 'http://www.imdb.com/title/tt0114746/', '12 opic -- 12 monkeys en.jpg', NULL, 'Directed by Terry Gilliam.  With Bruce Willis, Madeleine Stowe, Brad Pitt, Joseph Melito. In a future world devastated by disease, a convict is sent back in time to gather information about the man-made virus that wiped out most of the human population on the planet.', NULL, 1996, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1592, '201210/12 opic -- 12 monkeys.m4v', '12 opic', 4, 0, 'cz', NULL, 'en', '12 Monkeys', 'http://www.csfd.cz/film/3311-12-opic/N', '12 opic -- 12 monkeys.jpg', NULL, 'James Cole (Bruce Willis) prožil převážnou většinu svého života v podzemí. Nyní, v roce 2025, žije v ponuré, tmavé, zasněžené přítomnosti. Začalo to už na konci dvacátého století, kdy během dvou let (1996 a 1997) zemřelo přičiněním neznámého bídáka, který vypustil do ovzduší nebezpečný vir, pět miliard obyvatel zeměkoule. Zůstalo jen jedno procento lidí, kteří se teď krčí jako krtci pod zemí. Hrstka lidí se s tím ale nehodlá jenom tak smířit, a proto vysílá do minulosti prostřednictvím stroje času "nedobrovolné dobrovolníky" z řad vězňů. Jejich úkolem v podstatě není spasit svět a odvrátit neštěstí - co se stalo, nejde odestát. Mají jen pozorovat, sbírat vzorky a odhalit zdroj nákazy, původ všeho zla. Vědci už pak zajistí vše ostatní, vyvinou protilátku a zařídí, aby se lidstvo jednadvacátého století mohlo vrátit zpátky na povrch zemský...(oficiální text distributora)', NULL, 1995, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1595, '201210/Pelisky.m4v', 'Pelisky', 9, 0, 'cz', NULL, 'cz', 'Pelisky', 'http://www.csfd.cz/film/4570-pelisky/', 'Pelisky.jpg', NULL, 'Příběhy jedné historické generace - stárnoucích rodičů, dospívající mládeže a malých dětí. Děj je zasazen do konce šedesátých let - podzim 67 až léto 68 s krátkým epilogem přesahujícím do let sedmdesátých. Pražská vilová čtvrť Hanspaulka, jemná poetika a humorná nadsázka jsou charakteristické pro mozaikové vyprávění paralelních životních osudů tří generací mužů a žen ve zvláštním období našich dějin v roce 1968.(oficiální text distributora)', NULL, 1999, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1596, '201210/Prometheus en.m4v', 'Prometheus', 4, 0, 'en', 'cz', 'en', 'Prometheus', 'http://www.imdb.com/title/tt1446714/', 'Prometheus en.jpg', NULL, 'Directed by Ridley Scott.  With Noomi Rapace, Logan Marshall-Green, Michael Fassbender, Charlize Theron. A team of explorers discover a clue to the origins of mankind on Earth, leading them on a journey to the darkest corners of the universe. There, they must fight a terrifying battle to save the future of the human race.', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2004, '201301/Osviceni -- The Shining en.m4v', 'The Shining', 2, 0, 'en', 'cz', 'en', 'The Shining', 'http://www.imdb.com/title/tt0081505/?ref_=fn_al_tt_1', 'Osviceni -- The Shining en.jpg', NULL, 'Directed by Stanley Kubrick.  With Jack Nicholson, Shelley Duvall, Danny Lloyd, Scatman Crothers. A family heads to an isolated hotel for the winter where an evil and spiritual presence influences the father into violence, while his psychic son sees horrific forebodings from the past and of the future.', NULL, 1980, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2027, '201303/2012 James Bond - Skyfall en.m4v', '2012 James Bond - Skyfall', 15, 15, 'en', 'cz', 'en', 'Skyfall', 'http://www.imdb.com/title/tt1074638/?ref_=fn_al_tt_1', '2012 James Bond - Skyfall en.jpg', 23, 'Directed by Sam Mendes.  With Daniel Craig, Javier Bardem, Naomie Harris, Judi Dench. Bond''s loyalty to M is tested when her past comes back to haunt her. Whilst MI6 comes under attack, 007 must track down and destroy the threat, no matter how personal the cost.', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1597, '201210/Prometheus.m4v', 'Prometheus', 4, 0, 'cz', NULL, 'en', 'Prometheus', 'http://www.csfd.cz/film/290958-prometheus/', 'Prometheus.jpg', NULL, 'Dva mladí, úspěšní archeologové Elisabeth Shaw (Noomi Rapace) a Charlie Holloway (Logan Marshall-Green) objevili na různých místech Země několik jeskynních piktogramů z dávných civilizací a postupně se jim podařilo rozkrýt, že všechny piktogramy poukazují na jedno jediné místo ve vzdáleném vesmíru. S týmem dalších badatelů se tak na palubě kosmické lodi Prometheus vydávají na cestu, na jejímž konci chtějí nalézt odpovědi na nejzákladnější a nejniternější otázky lidského bytí. Oba však mají poněkud rozdílnou motivaci, proč se expedice účastní. Elisabeth věří, že lidstvo bylo stvořeno vyšší mocí a chce se se stvořiteli setkat a pochopit původ lidské existence, zatímco Holloway se převážně snaží uvést věci na pravou míru. Nikdo z posádky ovšem není připraven na nepředstavitelnou hrůzu, která na ně čeká a netuší, že právě oni budou muset svést děsivý boj o záchranu budoucnosti lidské rasy.(oficiální text distributora)', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1598, '201210/Rain Man.m4v', 'Rain Man', 15, 0, 'cz', '  ', 'en', 'Rain Man', 'http://www.csfd.cz/film/5954-rain-man/N', 'Rain Man.jpg', 0, 'Charlie Babbitt (Cruise) právě zjistil, že má autistického bratra Raymonda (Hoffman) a čeká ho fantastická jízda jeho života. Nebo okružní výlet? Všechno začne ve chvíli, kdy Raymond odmítne letět letadlem a tak nezbývá než jet autem. Poprvé totiž horkokrevného Charlieho přivede na pokraj jeho trpělivosti a pak ho kompletně vtáhne do svého egocentrického světa. Nesentimentální cesta však pro oba bratry Babbittovy znamená víc, než jen překonání vzdálenosti mezi dvěma místy. Jde o sblížení dvou nesmírně odlišných lidí v "hluboce dojímavém a silném filmu" (Joel Siegel, ABC-TV)!(oficiální text distributora)', NULL, 1988, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (1599, '201210/Schindleruv seznam -- Schindler''s List.m4v', 'Schindleruv seznam', 15, 0, 'cz', NULL, 'en', 'Schindler''s List', 'http://www.csfd.cz/film/8653-schindleruv-seznam/', 'Schindleruv seznam -- Schindler''s List.jpg', NULL, 'Hluboce šokující, nemilosrdné drama o noční můře holocaustu, založené na skutečných událostech. Na záchranu 1100 životů obětoval Oskar Schindler veškeré své jmění... Polský Krakov 1939. Židovská obec je vystavována stále se zvyšujícímu tlaku ze strany nacistů. Do této vřavy vstupuje obchodník Oskar Schindler, člen Nacistické strany, velký svůdník a muže vydělávající na válečném utrpení, který dokázal najít v sobě člověka a zachránit ty, kterým už zdánlivě nebylo pomoci... Film Stevena Spielberga je stěžejní dílo světové kinematografie a vítěz sedmi Oscarů, včetně ocenění za Nejlepší film roku a Nejlepší režii.(oficiální text distributora)', NULL, 1993, '');
