INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2006, '201301/Temny Rytir Povstal -- Dark Knight Rises.m4v', 'Temny Rytir Povstal', 15, 27, 'cz', '  ', 'en', 'Dark Knight Rises', 'http://www.csfd.cz/film/252669-temny-rytir-povstal/', 'Temny Rytir Povstal -- Dark Knight Rises.jpg', 3, 'Je tomu již osm let, co se Batman vytratil do noci, čímž se tak z hrdiny změnil v uprchlíka. Tím, že za něj padla vina za smrt Harveyho Denta, obětoval temný rytíř všechno pro to, co společně s komisařem Gordonem považoval za vyšší dobro. A na čas tato lež skutečně zabírala, když byla kriminální aktivita v Gothamu rozdrcena silou Dentova odkazu. Všechno se ale mění s příchodem prohnané zlodějky a jejích tajemných zájmů. Avšak ještě daleko nebezpečnější je nástup Banea, maskovaného teroristy, jehož nelítostný plán proti Gothamu vyžene Bruce z jeho dobrovolného exilu. Avšak ani pokud si znovu nasadí svou masku a temnou kápi, nemusí jeho síly proti Baneovi stačit.(oficiální text distributora)', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2007, '201301/The Godfather 3.m4v', 'The Godfather 3', 15, 1003, 'en', 'cz', 'en', 'The Godfather 3', 'http://www.imdb.com/title/tt0099674/', 'The Godfather 3.jpg', 3, 'Directed by Francis Ford Coppola.  With Al Pacino, Diane Keaton, Andy Garcia, Talia Shire. In the midst of trying to legitimize his business dealings in 1979 New York and Italy, aging mafia don Michael Corleone seeks to vow for his sins while taking a young protégé under his wing.', NULL, 1990, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2008, '201301/WALLE cz.m4v', 'Wall-E', 3, 0, 'cz', NULL, 'en', 'Wall-E', 'http://www.csfd.cz/film/231646-vall-i/', 'WALLE cz.jpg', NULL, 'Film začíná v roce 2700. Poslední operační stroj VALL-I stále plní úkol čištění Země se svým jediným mazlíčkem – švábem Halem, který mu dělá společnost. Malý robot, roztomilý a působivý, si za ta léta docela vyvinul svou osobnost. Bydlí v maringotce plné zvláštností a artefaktů z lidského života jako je Rubikova kostka, kuchyňský šlehač a jeho největší poklad – stará video kazeta s filmem Hello Dolly. Během začátku filmu si přidá do své sbírky novou věc – malou rostlinku, kterou zasadí do staré boty. Jednoho dne jde VALL-I po své obvyklé práci, ale blízko přistane kosmická loď. Kosmická loď vyšle malou, létající robotku Eve, která začíná na povrchu planety něco hledat. VALL-I se beznadějně zamiluje. Rodinná animovaná pohádka ze studia Pixar.(oficiální text distributora)', NULL, 2008, '');
