INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2014, '201301/Doba ledova 1 -- Ice Age 1.m4v', 'Doba ledova 1', 3, 31, 'cz', NULL, 'en', 'Ice Age 1', 'http://www.csfd.cz/film/19838-doba-ledova/', 'Doba ledova 1 -- Ice Age 1.jpg', 200, 'Bylo nebylo. Ne tak dávno, sotva před 20 000 lety, v prehistorickém světě, kdy mrzne až praští, začíná příběh o mi-mi-migraci. Jak má jeden ale poznat, že jde o Dobu ledovou a ne třeba o Mrazivé časy nebo Velký chlad? No, tak se na naše hrdiny podívejte! Obličeje mají vyloženě ledové. A s tím přesunem je docela honička, zvlášť když zvířecí tým - mamut, tygr a lenochod - najdou lidské mládě, moc roztomilého človíčka, a usmyslí si, že prcka vrátí rodičům. Během dobrodružné cesty mají zvířata spoustu starostí jak se uživit. Mamut musí dávat pozor, aby mu věčně někdo nešlapal na chobot. A musí hájit svou tělnatost. Není přece tlustý, to ty chlupy dělají, že tak vypadá. Tygr zase potřebuje uchovat svou tygří vážnost. A ještě musí vyřešit spoustu problémů se vztahy. A jaký je to pořádek, když lenochod střídá partnerky jako ponožky? Panečku, mamut, ten je věrný. Na konec by to chtělo nějakou změnu. Což takhle "globální oteplení"?(oficiální text distributora)', NULL, 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2015, '201301/Madagascar 3 cz.m4v', 'Madagascar 3', 3, 1001, 'cz', '  ', 'en', 'Madagascar 3', 'http://www.csfd.cz/film/252667-madagaskar-3/', 'Madagascar 3 cz.jpg', 3, 'Rafinovaným tučňákům se s pomocí hrubé šimpanzí síly podařilo opravit letadlo, ve kterém naši hrdinové havarovali během přeletu z Madagaskaru do Afriky, a vyrazili si užívat do kasina v Monte Carlu. Lva Alexe, hrošici Glorii, zebru Martyho a žirafáka Melmana ale na palubu nevzali, což čtveřici donutí doplavat za nimi po svých Středozemním mořem. Tučňáky nutně potřebují, protože bez tučňáků nemají šanci vrátit se domů. Zejvení divokých, i když přátelských šelem v epicentru evropské zábavy pochopitelně vyvolá rozruch, který dostane za úkol zkrotit kapitánka Chantal DuBois. Je to elitní agentka organizace na odchyt zvěře, pro kterou je její práce posláním. Představa, že svůj životopis vyšperkuje ulovením žirafy, lva, zebry a hrošice ji dovádí až k nadlidskému úsilí a k rozhodnutí dostat se jim na kobylku za každou cenu. V Africe se schovali celkem snadno, na civilizovaném světadíle jim poskytne jediné maskování kočovný cirkus, který se po evropské šňůře chystá i do New Yorku. Jenže staňte se cirkusáky, když nic zvláštního neumíte… Tenhle zpáteční lístek vyjde Martyho, Melamana, Glorii a Alexe hodně draze.(oficiální text distributora)', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2016, '201301/Pirati -- Pirates Band of Misfits cz.m4v', 'Pirati', 3, 0, 'cz', NULL, 'en', 'Pirates - Band of Misfits', 'http://www.csfd.cz/film/294673-pirati/', 'Pirati -- Pirates Band of Misfits cz.jpg', NULL, 'Film ukazuje události, které se přihodí bandě bodrých smolařských pirátů, když křižují sedm moří v dobrodružství hodném barona Prášila. Tato komedie začíná, když Pirát kapitán se svou posádkou vytáhne do bitvy proti svému soupeři Černému Bellamymu, aby získal kýženou cenu „Pirát roku“. Jejich cesta vede z Karibiku až do viktoriánského Londýna, kde se setkají s všemocným nepřítelem, který je odhodlán vymazat piráty z povrchu zemského jednou provždy. Piráti zjišťují, že jejich pátrání je vítězství dobrosrdečného optimismu nad nudným násilím zdravého rozumu.(oficiální text distributora)', NULL, 2012, '');
