INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2019, '201301/Doba ledova 3 Usvit dinosauru -- Ice Age 3 Dawn of the Dinosaurs cz.m4v', 'Doba ledova 3 Usvit dinosauru', 3, 31, 'cz', NULL, 'en', 'Ice Age Dawn of the Dinosaurs', 'http://www.csfd.cz/film/234767-doba-ledova-3-usvit-dinosauru/', 'Doba ledova 3 Usvit dinosauru -- Ice Age 3 Dawn of the Dinosaurs cz.jpg', 220, 'Hrdinové z prostředí pod bodem mrazu se vrací, aby zažili další neuvěřitelné dobrodružství, tentokrát v příběhu nazvaném Doba ledová 3: Úsvit dinosaurů. Nová “doba ledová” zavede hlavní hrdiny i diváky do ohromného podzemního světa dinosaurů. Je to barevná krajina plná bujné vegetace, která je ostrým protipólem mrazivého prostředí z prvních dvou “dob ledových”. Je to země nebezpečí, ohromných tvorů, rostlin pojídajících mamuty, země plná tajemných míst. Věčný smolař, prehistorický krysoveverčák Scrat má před sebou osudové setkání s krysoveverkou Scratte. Mamuti Manny a Ellie očekávají první přírůstek do rodiny. Lenochod Sid se zase dostane do problémů poté, co si založí vlastní rodinu s nalezenými dinosauřími vajíčky. Šavlozubý tygr Diego řeší dilema, jestli se z něj po boku kamarádů nestává příliš velký měkota. Navíc, když se objeví dinosauři, Manny přestává být „králem džungle“. A také se všichni seznámí s jednookým lasičákem Buckem, který neúnavně loví dinosaury. Doba ledová 3: Úsvit dinosaurů má všechny esence prvních dvou dílů, které si diváci zamilovali, a ještě mnohem víc. Víc humoru, akce, neuvěřitelnou vizuální podívanou, a především kouzlo zvané 3-D.(oficiální text distributora)', NULL, 2009, '');
