INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2020, '201301/Doba ledova 4 Zeme v pohybu -- Ice Age 4 Continental Drift.m4v', 'Doba ledova 4 Zeme v pohybu', 3, 31, 'cz', NULL, 'en', 'Ice Age Continental Drift', 'http://www.csfd.cz/film/281739-doba-ledova-4-zeme-v-pohybu/', 'Doba ledova 4 Zeme v pohybu -- Ice Age 4 Continental Drift.jpg', 230, 'Do kin se po třech letech vrací oblíbená mamutí rodinka Mannyho, Ellie, jejich teenagerské dcerunky Broskvičky, lenochoda Sida, šavlozubého tygra Diega a potřeštěného krysoveverčáka Scratta, aby prožili nové napínavé dobrodružství. Právě Scratt, se svou vášní pro žaludy a jejich ukrýváním totiž způsobí katastrofu nevídaných rozměrů. Podaří se mu zasadit svůj žalud takovým způsobem, že pradávná Pangea začne praskat a lámat se na jednotlivé kontinenty a díky pohybu litosférických desek dojde i k rozdělení naší prehistorické party. Ellie a Broskvička zůstanou na jednom kontinentu a Many, Sid a Diego jsou unášeni proudem na ledové kře hluboko do širého oceánu. Ať to stojí, co to stojí, naši tři odvážní přátelé se musí dostat zpátky domů a při tom na ně čeká nejedno velké dobrodružství. Při cestě zpět jim dají zabrat nejenom mořské bouře, přílivové vlny a mořské příšery, ale musejí zvládnout i první společné soužití na jedné malé kře a tak se můžeme těšit na řadu komických scén plných vzájemného špičkování. Shodou okolností a náhod se na jejich provizorní loď, kterou si vytvořili z ledovce, dostane i Sidova babička, kterou skupinka zachrání v příboji mořských vln. Už, už se zdá, že naše partička kamarádů bude zachráněna, když se z oparu mlhy vynoří loď, jenže její podivínská posádka na palubě ani v nejmenším nehodlá našim kamarádům pomoci. Naopak, všichni jsou zajati a dostávají se do ještě větší šlamastiky.(oficiální text distributora)', NULL, 2012, '');
