INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2032, '201303/Mickey - Traja musketieri cz.m4v', 'Mickey - Traja musketieri', 3, 25, 'cz', NULL, 'en', 'Mickey Mouse Three Musketeers', 'http://www.csfd.cz/film/190186-tri-musketyri/', 'Mickey - Traja musketieri cz.jpg', 303, 'Nejlepší přátelé Mickey, Donald a Goofy jsou pouze bezvýznamnými pomocníky, ale sní o kariéře pravých mušketýrů. Život se jim úplně změní ve chvíli, kdy jednonohý kapitán mušketýrů Pete připravuje strašné spiknutí. Chce odstranit princeznu Minnii, jedinou myš v království, která mu stojí v cestě k trůnu. A aby měl ulehčenou práci, udělá z našich přátel princezniny stráže. Jenže Mickey, Donald a Goofy nejsou zrovna příkladem neohrožených hrdinů, ale spojí své síly a připraví Petovi nemilé překvapení. Jinak to ani nemůže dopadnout, když jeden bojuje za všechny a všichni z jednoho!(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2033, '201303/Neskrotna -- Brave SK.m4v', 'Neskrotna', 3, 0, 'sk', NULL, 'en', 'Brave', 'http://www.csfd.cz/film/241060-rebelka/', 'Neskrotna -- Brave SK.jpg', NULL, 'Už od starých časů se po mnoho generací šíří příběhy o epických bitvách a mystických legendách v drsné a tajemné Skotské vysočině. Ve filmu se k legendě přidá nová pověst, když odvážná Merida stane tváří v tvář tradici, osudu a nejkrutější bestii. Merida je zkušená lučištnice a impulzivní dcera krále Ferguse a královny Elinor. Je odhodlána jít si v životě svou vlastní cestou. Vzepře se starému zvyku zasvěcení pánům země. Silnému lordu MacGuffinovi, mrzutému Macintoshovi a svárlivému Dingwallovi. Meridiny činy nechtěně rozpoutají v království chaos a běsnění. Když se obrátí o pomoc k podivínské staré čarodějnici, dostane prokleté přání. Merida, která následuje nebezpečné síly, objeví význam skutečné odvahy, aby zlomila příšernou kletbu, než bude příliš pozdě.(oficiální text distributora)', NULL, 2012, '');
