INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2035, '201303/Tom a Jerry -- Rychlo a chlpato cz.m4v', 'Tom a Jerry - Rychlo a chlpato', 3, 30020, 'cz', '  ', 'en', 'Tom a Jerry Fast And Furry', 'http://www.csfd.cz/film/235731-tom-a-jerry-rychle-a-chlupate/', 'Tom a Jerry -- Rychlo a chlpato cz.jpg', 901, 'Honička začíná! Vaši animovaní oblíbenci byli nemilosrdně vykázáni ze svého domova, protože jej neustále ničili! Jejich první štací se stává televizní reality show, kam se přihlásí jako soutěžící - protože hlavní cenou je obrovský, překrásný dům. Tom s Jerrym se okamžitě vyhoupnou na špičku žebříčku sledovanosti. A není divu, od superrychlých vozů přes podvodní stíhačky až po finálový souboj letadel s raketovým pohonem se každý další závod stává větší výzvou než byl ten předchozí. Nenechte si ujít rodinnou zábavu v délce celovečerního filmu! Jako diváci nemůžete prohrát.(Magic Box)', NULL, 2005, '');
