INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2052, '201305/SHREK_FOREVER_AFTER cz.m4v', 'Shrek zvonec a konec', 3, 33, 'cz', NULL, 'en', 'Shrek Forever After', 'http://www.csfd.cz/film/229135-shrek-zvonec-a-konec/', 'SHREK_FOREVER_AFTER cz.jpg', 4, 'Když si podáte nebezpečnou dračici, zachráníte krásnou princeznu a tchýnino království, co dalšího ještě můžete dokázat? Pokud se jmenujete Shrek, pravděpodobně trochu zfotrovatíte. Místo toho, aby jako za starých dobrých časů děsil vesničany, teď jim otrávený Shrek rozdává autogramy. Co se to stalo s divokým zlobrem? Protože se mu stýská po době, kdy byl „pravým netvorem“, podepíše Shrek smlouvu se lstivým prodavačem iluzí Rampelníkem. V tu chvíli se ocitne ve zvrácené alternativní verzi království Za sedmero horami, kde jsou zlobři houfně pronásledováni, Rampelník je králem a Fiona ho ve skutečnosti nikdy nepotkala. Tak musí Shrek napravit, co spískal, zachránit přátele, obnovit svět takový, jaký býval, a znovu získat svou Pravou lásku.(oficiální text distributora)', NULL, 2010, '');
