INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2038, '201304/Aladin cz.m4v', 'Aladin cz', 3, 0, 'cz', NULL, 'en', 'Aladin', 'http://www.csfd.cz/film/18248-aladin/', 'Aladin cz.jpg', NULL, 'Aladin je chudý chlapec z tržiště, jehož jediným majetkem i přítelem je jeho opička Abu. Denně sní svůj sen o úniku z bezútěšného života v bídě a o lásce krásné princezny Jasmíny. Osud se nakloní v jeho prospěch, když jej chce zlý sultánův rádce Džafar využít k získání kouzelné lampy z jeskyně, skryté uprostřed pouště. Aladin se stane majitelem kouzelného koberce a lampy, jejíž dobrý džin mu může splnit tři přání. Aladin se s jeho pomocí skutečně přiblíží k princezně, dosud neústupně odmítající nápadníky, a získá její lásku. Stále jej však trápí jeho prostý původ, který se jí zdráhá přiznat. A to jej ještě čeká souboj se zrádným Džafarem a jeho papouškem Jagem, zmocnivšími se na čas lampy, sultanátu i princezny... Nejstarší zprávy o proslulém souboru perských arabských a indických lidových příběhů, známém pod názvem Pohádky tisíce a jedné noci, pocházejí už z desátého století. První rukopisné záznamy spisu, čítajícího nakonec více než 267 příběhů různé délky, vznikaly ve čtrnáctém a patnáctém století. Příběh o Aladinovi, který je jednou z nejznámějších a nejčastěji zpracovávaných arabských pohádek, vznikl pravděpodobně okolo druhé poloviny dvanáctého století v Egyptě, což však nebrání tomu, aby byl v mnoha adaptacích situován do prostředí, připomínajícího oblíbenou romantickou vizi středověkého Bagdadu. Tvůrci nejnovější americké verze, která na přelomu roku dlouhou dobu kralovala americké distribuci, se nechali volně inspirovat všemi předchozími adaptacemi podobných příběhů a navíc plně využili možností moderního animovaného filmu. Vedle magických letů na koberci a komplikovaných hudebních scén v nejlepší disneyovské tradici je film naplněn nepřetržitým přívalem propracovaných obrazových gagů, soustředěn zejména okolo postavy džina. Ten, v původní verzi namluven Robinem Williamsem, se tak stává takřka hlavní postavou filmu a je hlavním důvodem, proč Aladin může potěšit nejen děti, ale i ty dospělé, kteří mají chuť se smát. Film byl oceněn 2 Oscary.(oficiální text distributora)', NULL, 1992, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2039, '201304/Popoluska.m4v', 'Popoluska', 3, 0, 'sk', NULL, 'en', 'Cindirella', 'http://www.csfd.cz/film/33887-popelka/', 'Popoluska.jpg', NULL, 'Snad nejznámější pohádka na světě, příběh chudé dívky, která se přes noc stává princovou vyvolenou, vychází nyní v zrestaurované podobě na Disney DVD a VHS dokonce ve speciální verzi. Nezapomenutelnou, animovanou verzi této pohádky dokončilo v roce 1950 studio Disney. Diváky kromě kouzelné animace zaujala i nádherná hudba, která film doprovází. Popelka byla tehdy nominována na Oscara ve třech kategoriích: za Nejlepší píseň (Bibbidi-Bobbidi-Boo), Nejlepší soundtrack a Nejlepší zvuk. Kromě toho získala po celém světě velkou řadu ocenění (jen pro ilustraci uveďme Golden Bear Award, Berlínský mezinárodní filmový festival, 1951 či Cinemonde Et Les Film Francais, 1950 a mnohé další).(oficiální text distributora)', NULL, 1950, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2040, '201305/Casino Royale.m4v', 'Casino Royale', 15, 15, 'en', 'en', 'en', 'Casino Royale', 'http://www.imdb.com/title/tt0381061/?ref_=sr_1', 'Casino Royale.jpg', 21, 'Directed by Martin Campbell.  With Daniel Craig, Eva Green, Judi Dench, Jeffrey Wright. Armed with a license to kill, Secret Agent James Bond sets out on his first mission as 007 and must defeat a weapons dealer in a high stakes game of poker at Casino Royale, but things are not what they seem.', NULL, 2006, '');
