INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2043, '201305/HOBBIT_AN_UNEXPECTED_JOURNEY.m4v', 'Hobbit: An Unexpected Journey', 4, 34, 'en', 'cz', 'en', 'Hobbit: An Unexpected Journey', 'http://www.imdb.com/title/tt0903624/?ref_=sr_1', 'HOBBIT_AN_UNEXPECTED_JOURNEY.jpg', 1, 'Directed by Peter Jackson.  With Martin Freeman, Ian McKellen, Richard Armitage, Andy Serkis. A reluctant hobbit, Bilbo Baggins, sets out to the Lonely Mountain with a spirited group of dwarves to reclaim their mountain home - and the gold within it - from the dragon Smaug.', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2044, '201305/R_BALBOA cz.m4v', 'Rocky Balboa', 15, 32, 'cz', NULL, 'en', 'Rocky Balboa', 'http://www.csfd.cz/film/221900-rocky-balboa/', 'R_BALBOA cz.jpg', 6, 'Před třiceti lety byl mužem bez budoucnosti, který se živil prací pro druhořadého lichváře. Když mu štěstí přihrálo možnost postavit se do boxerského ringu s úřadujícím šampiónem těžké váhy Apollem Creedem, jeho šance na vítězství byla jedna ku milionu. Jediné, co chtěl, bylo bojovat až do konce. Jeho odvaha a nezdolnost v životě i v ringu inspirovala miliony lidí. Každá sláva však jednou pomine a Rocky Balboa (Sylvester Stallone), který byl kdysi italským hřebcem, nyní tráví večery vyprávěním starých historek stálým návštěvníkům své restaurace Adrians, pojmenované po jeho ženě, z jejíž předčasné smrti je ještě stále zlomený. Jeho syn (Milo Ventimiglia) s ním nechce trávit čas, protože je příliš zaměstnán svým vlastním životem. Čas a rány osudu Rockyho pokořily, otupily jeho pěsti a vzaly mu všechno kromě jeho starých historek, ale uvnitř je pořád stejný. Ve svém srdci je však pořád bojovníkem. Úřadujícím šampiónem těžké váhy je nyní Mason “The Line” Dixon, který vyniká pouze tím, jak jednoduše se mu podařilo titul získat. Vzhledem k tomu, že se mu nikdy nepostavil do cesty rovnocenný protivník a on tak nemusel nikdy nic zásadního dokázat, je Dixon fanoušky boxu považován za člověka, kterého nečeká žádná zářivá budoucnost. Avšak pouze do okamžiku, kdy jej počítačová simulace přirovná k Rockymu Balboovi v jeho nejlepší formě. Kdo by vyhrál, kdyby se tihle dva postavili proti sobě v ringu? Dixon se svými vypilovanými údery a prací nohou nebo Rocky se svou vášní a silnými direkty? Zní to jako vtip. Ale pro Rockyho, který je téměř dvakrát tak starší jako jeho oponent, je možnost zápasu s Dixonem druhou šancí, kterou nemusel nikdy dostat – šancí, jak dokázat sobě i všem lidem, které má rád, že zatímco tělo stárne, plamen v srdci plápolá stále silně. Když byl v roce 1976 uveden do kin původní film Rocky, stal se mezinárodním fenoménem a nesmrtelnou klasikou. Nečekaný hit se v silné konkurenci velkorozpočtových titulů probojoval do oscarového klání a byl nominován na 10 Cen Akademie, přičemž proměnil nominace v kategoriích nejlepší film, nejlepší režie a nejlepší střih, v nichž soutěžil s filmy jako Taxikář či Network. Sám Stallone, který zarytě trval na tom, že v případě realizace filmu musí být obsazen do jeho hlavní role, byl nominován hned dvakrát – za nejlepší herecký výkon a nejlepší scénář.(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2045, '201305/ROCKY 5 cz.m4v', 'Rocky 5', 15, 32, 'cz', NULL, 'en', 'Rocky 5', 'http://www.csfd.cz/film/16104-rocky-v/', 'ROCKY 5 cz.jpg', 5, 'Rocky Balboa zápasí v Moskvě. Zpočátku má jeho soupeř navrch, nakonec ale Rocky sebere všechny své síly a zvítězí. Je ale zcela vyčerpán a stane se mu podivná věc: po zápase nemůže ovládnout silný třes rukou. Adrian na něj naléhá, aby navštívil lékaře. Tam se Rocky dozví, že vzhledem k velkému počtu ran, které utržil za dobu své kariéry, má nebezpečně poškozený mozek. Adrian trvá na tom, aby Rocky s boxerskou kariérou okamžitě skončil. Právě v té době však Rockyho účetní pomocí falešné plné moci jeho jménem zpronevěří milion dolarů a Rocky musí prodat téměř veškerý svůj majetek. S Adrian a synem Rockym se vrací do míst, kde poprvé zvítězil v boxu. Kvůli penězům chce Rocky opět začít boxovat, nakonec vyslyší prosby nadějného mladého boxera Tommyho a stává se jeho manažerem.(oficiální text distributora)', NULL, 1990, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2053, '201305/SHREK_FOREVER_AFTER sk.m4v', 'Shrek zvonec a koniec SK', 3, 33, 'sk', NULL, 'en', 'Shrek Forever After', 'http://www.csfd.cz/film/229135-shrek-zvonec-a-konec/', 'SHREK_FOREVER_AFTER sk.jpg', 14, 'Když si podáte nebezpečnou dračici, zachráníte krásnou princeznu a tchýnino království, co dalšího ještě můžete dokázat? Pokud se jmenujete Shrek, pravděpodobně trochu zfotrovatíte. Místo toho, aby jako za starých dobrých časů děsil vesničany, teď jim otrávený Shrek rozdává autogramy. Co se to stalo s divokým zlobrem? Protože se mu stýská po době, kdy byl „pravým netvorem“, podepíše Shrek smlouvu se lstivým prodavačem iluzí Rampelníkem. V tu chvíli se ocitne ve zvrácené alternativní verzi království Za sedmero horami, kde jsou zlobři houfně pronásledováni, Rampelník je králem a Fiona ho ve skutečnosti nikdy nepotkala. Tak musí Shrek napravit, co spískal, zachránit přátele, obnovit svět takový, jaký býval, a znovu získat svou Pravou lásku.(oficiální text distributora)', NULL, 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2046, '201305/ROCKY cz.m4v', 'Rocky', 15, 32, 'cz', NULL, 'en', 'Rocky', 'http://www.csfd.cz/film/16103-rocky/', 'ROCKY cz.jpg', 1, 'Slavné boxerské drama outsidera, který dostal šanci a zvítězil sám nad sebou. Titulní hrdina, neúspěšný třicetiletý boxer Rocky Balboa, pracuje jako výběrčí dluhů pro místního lichváře, boxuje v pokoutních zápasech a neúspěšně se dvoří Adrian, plaché prodavačce v obchodě s drobným zvířectvem. V době, kdy bývalý trenér vyhodí Rockyho z tělocvičny, protože se mu zdá, že jako boxer promrhal svůj talent a je vyřízená existence, chystá se ve Filadelfii boj o titul mistra světa v těžké váze. Protože se však vyzyvatel zranil a nemůže k zápasu nastoupit, vyhlásí obhájce titulu Apollo Creed, že bude boxovat s kterýmkoli místním boxerem, protože chce dokázat, že Amerika je zemí, v níž má každý šanci uspět. Vybere si Rockyho, protože se mu líbí jeho přezdívka Italský hřebec, slibující dobrou publicitu. Zatímco se Rocky nesměle sbližuje s Adrian, rozjíždí se velkolepá reklamní kampaň. Rockyho zpovídají televizní reportéři, píše se o něm v novinách. Všichni se ovšem vysmívají jeho bezelstnosti a nikdo mu nedává sebemenší šanci. Přijde za ním bývalý trenér Mickey, nabízí mu své služby jako trenér a manažer. Rocky jej nejprve vyhodí, ale pak souhlasí. Začíná tvrdý trénink. Běhá, boxuje a postupně se dostává do fyzické i psychické kondice, také díky tomu, že se k němu nastěhuje Adrian. V noci před zápasem se jí Rocky svěří, že chce s Creedem vybojovat rovnocenný zápas, i když si uvědomuje své minimální šance. 1. ledna 1976 je hala vyprodána. Začíná velkolepá podívaná, kterou je boj o titul mistra světa. Překvapený Apollo Creed, pro něhož je zápas, stejně jako pro diváky, pouze show pro pobavení, jde hned v prvním kole po Rockyho ráně k zemi. Teprve pak začne opravdový zápas. Rocky bojuje jako o život, několikrát jde k zemi, ale pokaždé se zvedne.(oficiální text distributora)', NULL, 1976, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2047, '201305/ROCKY_2 cz.m4v', 'Rocky 2', 15, 32, 'cz', NULL, 'en', 'Rocky 2', 'http://www.csfd.cz/film/8660-rocky-ii/', 'ROCKY_2 cz.jpg', 2, 'Tesne po zápase, ktorým sme sa lúčili v prvom diele, sú Rocky a Apollo odvezení do nemocnice. I keď pre rozhodcov bol Apollo víťaz, on si stále myslí, že Rockyho neporazil. Chystá odvetu... Zatiaľ čo Rocky má odlišné plány. Po prepustení z nemocnice požiadal Adrianu o ruku, kúpil veľký dom, auto, chystá sa usadiť a dať boxu na chvíľu pokoj. Ale postupne začína byť bez peňazí. Snaží si zarobiť akoto len ide, no bez úspechov. Nakoniec Apollovu výzvu prijíma. Môže začať tréning, ktorý tento raz musí byť tvrdší, dlhší a náročnejší, pretože Apollo berie víťaztvo na Rockym ako jedinú možnosť skončenia zápasu.(fishki)', NULL, 1979, '');
