INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2048, '201305/ROCKY_3 cz.m4v', 'Rocky 3', 15, 32, 'cz', NULL, 'en', 'Rocky 3', 'http://www.csfd.cz/film/8661-rocky-iii/', 'ROCKY_3 cz.jpg', 3, 'Rocky, který ve druhém díle zvítězil ve vyčerpávajícím odvetném souboji s šampiónem Apollo Creedem, se stal mistrem světa všech váhových kategorií. Je z něj boháč a mohl by si užívat spokojeného života. Na obzoru se však objevuje nové nebezpečí. Kdykoli se Rocky objeví v ringu, sleduje ho z povzdálí hrůzu nahánějící a krvelačný svalovec Clubber Lang. Ze svých soupeřů dělá kaši a brousí si zuby na Rockyho mistrovský titul. Rockyho starý trenér a přítel Mikey brzy zpozoruje nebezpečí a uvědomí si, že proti Clubberovi, plnému nenávisti a vzteku nemá Rocky šanci. Několik let se mu daří hrozící střetnutí odvracet a oddalovat. Jednoho dne však Clubber Rockyho veřejně urazí a podaří se mu dosáhnout cíle. Zanedlouho stojí oba soupeři proti sobě v ringu. Už ve druhém kole však jde Rocky k zemi. Je ponížený, zesměšněný, celý jeho svět se zhroutil a navíc zemřel Mikey. Na scénu se však vrací Apollo Creed, tentokrát ne jako soupeř, ale jako přítel, pomocník a rádce. Rocky, nabažený a zlenivělý slávou, ztratil formu, jiskru a své pověstné odhodlání. Apollo jej vezme na předměstí, kde trénují mladí boxeři a Adrian ho přesvědčí, že se traumatu nezbaví, pokud opět nevstoupí do ringu, aby Clubberovi ukázal, kdo je skutečným pánem ringu.(oficiální text distributora)', NULL, 1982, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2049, '201305/ROCKY_4 cz.m4v', 'Rocky 4', 15, 32, 'cz', NULL, 'en', 'Rocky 4', 'http://www.csfd.cz/film/8662-rocky-iv/', 'ROCKY_4 cz.jpg', 4, 'Úspěšný boxer Rocky Balboa žije nyní v ústraní se svou ženou Adrian a synkem. Boxuje jen občas, pro udržení kondice, se svým přítelem, černým boxerem Apollem Creedem. Do Spojených států přilétá amatérský mistr světa v boxu, Rus Ivan Drago, s manželkou Ludmilou a trenérem Koloffem, který chce vstoupit do světa profesionálního boxu. Výzvu k zápasu vyslyší Apollo, který předpokládá, že Rus nemůže být v boxu příliš dobrý. Exhibiční zápas Apolla s Dragem začíná ideologickým projevem Koloffa k Dragovi. Ten je zase šokovaný úvodní taneční show, jíž se jako zpěvák účastní i Apollo. V následném zápase dojde k tragédii: Drago ve druhém kole Apolla vyřídí a ten vzápětí umírá v rukou svého přítele. Rocky je zdrcen a vyzve Draga k zápasu sám...(oficiální text distributora)', NULL, 1985, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2050, '201305/SHREK cz.m4v', 'Shrek', 3, 33, 'cz', NULL, 'en', 'Shrek', 'http://www.csfd.cz/film/14999-shrek/', 'SHREK cz.jpg', 1, 'Shrek, zelený obr s tykadly mimozemšťanů, žije sice docela sám, ale šťastně ve svém domku na samotě u lesa až do chvíle, kdy je doslova přepaden nekonečným zástupem známých i méně známých pohádkových postaviček, které byly vyhoštěny z království nevzhledného a zakyslého Lorda Farquaada. Ve snaze zjednat pořádek a hlavně vrátit klid do svého příbytku Shrek Farquaadovi slíbí, že vysvobodí ze zajetí obávaného draka krásnou princeznu Fionu a přivede mu ji, aby se stala jeho ženou. S pomocí mazaného oslíka, ochotného pro Shreka udělat cokoli, jen ne zmlknout aspoň na okamžik, obr svůj slib splní, jenže pak se to nějak podivně zamotá, zaplete a zkomplikuje, takže je všechno nakonec úplně a docela jinak...(oficiální text distributora)', NULL, 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2051, '201305/SHREK_2 cz.m4v', 'Shrek 2', 3, 33, 'cz', NULL, 'en', 'Shrek 2', 'http://www.csfd.cz/film/15000-shrek-2/', 'SHREK_2 cz.jpg', 2, 'A byla svatba a pak spolu Shrek s Fionou žili šťastně až do smrti… Tedy žili by, kdyby se ovšem nevydali za princezninými rodiči! Návštěva království Za sedmero horami se totiž záhy zvrtne v další komické dobrodružství. S věrným Oslíkem za zády čelí Shrek nástrahám Kmotřičky víly, domýšlivému princi Krasoňovi a proslulému lovci zlobrů Kocourovi v botách, kterému však pod fasádou zuřivého bojovníka tepe srdce ze zlata.(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2054, '201305/SHREK_THE_THIRD cz.m4v', 'Shrek Treti', 3, 33, 'cz', NULL, 'en', 'Shrek The Third', 'http://www.csfd.cz/film/205738-shrek-treti/', 'SHREK_THE_THIRD cz.jpg', 3, 'Za normálních okolností by krásní, mladí a zelení manželé Shrekovi po odstranění zákeřné Kmotřičky víly a jejího nemožného syna Krasoně ve druhém pokračování animované komedie Shrek žili nerušeně a hlavně šťastně až do smrti. Vzhledem k tomu, že se stali protagonisty nejúspěšnějšího animovaného filmu všech dob, nemají na pohodu nárok. Dobrodružství, která si pro ně kreativní scenáristé vymysleli tentokrát, jsou ještě vypečenější, než ta, která oba zlobříci prožili doposud. Posuďte sami: Král Harold má nakahánku, a je proto jasné, že musí vládu v království Za sedmero horami někomu předat. Ještě jasnější je, že nástupcem trůnu se má stát manžel jeho nejjasnější dcery Fiony. Shrek se ale vyhlídkám na kralování brání tak intenzívně, že se raději rozhodne přemluvit Fionina bratrance, teenagera Artuše, aby si královskou korunu nasadil on. Nejdřív ho ale bude muset najít a udělat z něj chlapa. Zatímco Shrek, Oslík a Kocour v botách se vydávají na poměrně dobrodružnou expedici, osiřelá Fiona má úplně jiné starosti. Absence mužského elementu v království se rozhodne zneužít již jednou odpálkovaný Krasoň, který se chce za pomoci záporných pohádkových postav zmocnit vlády v zemi Za sedmero horami násilím. Fiona je ale především Zlobryně a až na druhém místě princezna, a proto se Krasoňovi a jeho zlé armádě postaví. Navíc není sama, v první linii s ní kromě královny Lilien kráčí i svůdné komando princezen (Sněhurka, Popelka, Růženka a další), které jsou odhodlány všechny své přednosti využít boji. Ve Shrekovi Třetím bude prostě pořádně veselo, a to jsme tu ještě nezmínili draslíky, roztomilé potomky Oslíka a Dračice, a jedno překvapení, které Fiona přede všemi tají. Shrek se vždycky opíral o revoluční a naprosto dokonalou animaci a hvězdné hlasové obsazení. Obojí mu zůstalo i ve třetím pokračování, navíc se může pochlubit mimořádně zajímavým hereckým přírůstkem v podání popové ikony současnosti Justina Timberlakea, kterého v české verzi nahradí podobně slavný Vojtěch Kotek. Režie se ujal Chris Miller, který psal ke dvěma předchozím dílům scénáře, a Raman Hui, supervizor animace jedničky i dvojky.(oficiální text distributora)', NULL, 2007, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2056, '201306/Bambi SK.m4v', 'Bambi', 3, 0, 'sk', NULL, 'en', 'Bambi', 'http://www.csfd.cz/film/70340-bambi/', 'Bambi SK.jpg', NULL, 'S prvními slunečními paprsky, které probouzí k životu vše kolem, začíná slavný Bambiho příběh. Díky nejmodernějším technologiím Vám jej studio Walt Disney nyní přináší ve vyčištěné a remasterované podobě! V samém srdci lesa žije malý Bambi, který dělá první nesmělé krůčky životem a poznává svět kolem sebe. Společně se svými kamarády, roztomilým zajíčkem Dupíkem a ostýchavým skunkem Kvítkem, pak zažívají velká dobrodružství. Bruslí na "ztuhlé" vodě, dovádí v lese a seznamují se se všemi jeho podivnými obyvateli. Jejich moudrý průvodce Výr, který má královský původ, jim předává své zkušenosti o lásce, ztrátách a životě. Bambi je jedno velké dobrodružství plné humoru, nádherné animace a hudby nominované na Oscara.(oficiální text distributora)', NULL, 1942, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2057, '201306/Cililing a strateny poklad SK.m4v', 'Strateny poklad', 3, 39, 'sk', NULL, 'en', 'Tinker Bell and the Lost Treasure', 'http://www.csfd.cz/film/257902-zvonilka-a-ztraceny-poklad/', 'Cililing a strateny poklad SK.jpg', 2, 'V tajemné Hvězdné roklince, kde žije Zvonilka a ostatní pohádkové víly, se všichni netrpělivě těší na tradiční podzimní oslavu. Vyjde měsíc, který vílám dovolí uzdravit posvátný strom, zdroj veškeré jejich magie. Nešťastnou náhodou ale uvede Zvonilka celý bájný svět víl v ohrožení a proto se na vlastní pěst a tajně vydá daleko za moře na nebezpečnou výpravu, která by mohla vše napravit. S pomocí kamaráda Terínka a neposedné světlušky musí Zvonilka čelit ohromujícímu novému světu, aby objevila ten největší poklad.(oficiální text distributora)', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2058, '201306/Hlada sa Nemo SK.m4v', 'Hlada sa Nemo', 3, 0, 'sk', NULL, 'en', 'Finding Nemo', 'http://www.csfd.cz/film/43924-hleda-se-nemo/', 'Hlada sa Nemo SK.jpg', NULL, 'V pestrobarevných a teplých vodách Velké útesové bariéry žije ve svém bezpečném a odlehlém příbytku ze sasanek Marlin a jeho jediný syn Nemo. Marlin se s obavami ze všech nepředvídatelných nebezpečí, které hlubiny oceánu skýtají, snaží svého syna před nástrahami okolí ochránit. Nemo je však, stejně jako všechny malé rybky, velmi zvídavý a chce tajemný útes prozkoumat. Když je pak nečekaně unesen daleko od domova a skončí v akváriu, kterým si vyzdobil ordinaci laciný zubař, ocitne se Marlin nečekaně v roli hrdiny na cestě k záchraně svého syna. Během hledání se k Marlinovi připojí samaritánka Dory - ryba s nejhorší krátkodobou pamětí a největším srdcem v celém oceánu. Její nehynoucí optimismus neustále žene Marlina kupředu a dodává mu odhodlání riskovat a překonávat jeho obavy. Díky tomu Marlin také postupně začne věřit, že se nakonec přece jen podaří všechny problémy zdárně vyřešit. Po střetu s četnými nástrahami podmořského i suchozemského světa včetně žraloků, želv, medúz, mořských ptáků, městské kanalizace i samotného člověka, končí odloučení otce a syna šťastným shledáním a kdysi bojácný Marlin se stane v očích svého potomka i celého oceánu opravdovým hrdinou. Oscarem ocenění tvůrci animovaných filmů Toy Story - Příběh hraček, Život brouka a Příšerky, s. r. o. nás tentokrát zavedou do úplně nového světa počítačově animované zábavy plného legrace, citů a nezapomenutelných postaviček. Scénáristou a režisérem filmu Hledá se Nemo je Andrew Stanton, který se podílel jako jeden z režisérů na filmovém hitu Život brouka a spolupracoval na scénářích ke všem čtyřem celovečerním filmům ze společné produkce studií Disney a Pixar. Za svou práci byl nominován na Oscara. Film Hledá se Nemo a jeho úžasný podmořský svět je zcela novým uměleckým a technologickým standardem v počítačové animaci. Vedoucím výroby je John Lasseter, který v Pixaru působí jako výkonný vicepresident. Mezi talentované umělce, kteří propůjčili postavám své hlasy, patří Albert Brooks, Ellen DeGeneres, Willem Dafoe, Geoffrey Rush, Allison Janney, Barry Humphries a Alexander Gould.(oficiální text distributora)', NULL, 2003, '');
