INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2078, '201306/Cililing SK.m4v', 'Cililing', 3, 39, 'sk', NULL, 'en', 'Tinker Bell', 'http://www.csfd.cz/film/241055-zvonilka/', 'Cililing SK.jpg', 1, 'Vstupte do tajného světa Hvězdné roklinky a seznamte se se světově proslulou vílou Zvonilkou v jejím vůbec prvním celovečerním animovaném filmu. Vrtalo vám někdy hlavou, kdo svět vždy tak vyzdobí? Kdo přírodě lesk dává v každém ročním období? Vypravte se za dobrodružstvím a pomozte Zvonilce a jejím čtyřem kamarádkám s přípravami jara. Možná, že s trochou víry a špetkou vílího prášku zjistíte, jak důležité je být sám sebou. Ve filmu napěchovaném zábavou a fantazií oslavíte spolu se Zvonilkou její první celovečerní film. Čeká vás kouzelný příběh pro celou rodinu.(oficiální text distributora)', NULL, 2008, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2079, '201306/HERCULES SK.m4v', 'Herkules', 3, 0, 'sk', NULL, 'en', 'Hercules', 'http://www.csfd.cz/film/18250-herkules/', 'HERCULES SK.jpg', NULL, 'Hercula, syna vládce bohů Dia, unesli z kolébky na Olympu. Odporní nohsledi proradného krále podsvětí Háda naštěstí nedotáhli svůj úkol do konce - podařilo se jim sice Hercula pomocí kouzelného lektvaru téměř zbavit božství, ale k smrtící ráně, která měla ukončit chlapcův život dříve než dospěje a zkříží Hádovy plány, se už nedostali. Hercula pak našel bezdětný pastýř a vychoval ho jako vlastní dítě. Uběhlo osmnáct let a Hercules dospěl v statného mládence, který je pro svou neovladatelnou sílu a nešikovnost postrachem širokého okolí. Když se jednoho dne všemi opovrhovaný mladík náhodou dozví, že je synem samotného Dia, neváhá ani minutu a vydává se na cestu za dobrodružstvím. Během ní se stačí zamilovat, překazit nekalé Hádovi plány a stát se skutečným hrdinou ... nejen silou svalů, ale i příkladným charakterem.(oficiální text distributora)', NULL, 1997, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2080, '201306/CHARLIE SK.m4v', 'Charlie', 3, 0, 'sk', NULL, 'en', 'Charlie', 'http://www.csfd.cz/film/15622-charlie-vsichni-pejskove-jdou-do-nebe/', 'CHARLIE SK.jpg', NULL, 'Charlie není pes jako všichni ostatní. Dal by se přirovnat k Nicku Marshallovi z Po čem ženy touží, prostě pouliční vůdce, miláček fenek, nekonečný frajírek a bonviván. No a pak jednoho dne je konec a Charlie umře. A aby nemusel jít do nebe, kde je to pro takovéhle týpky vážně děsivá nuda, přijme Satanovu nabídku a zaprodá mu svou duši. Za ni dostane výměnou druhý život na Zemi a hodinky, které musí stále tikat, jinak propadne navždy peklu. To je ale Charliemu upřímně jedno, protože může zůstat a dál si užívat "života". Jenže...(monolog)', NULL, 1989, '');
