INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2214, '201307/MULAN SK.m4v', 'Mulan', 3, 0, 'sk', '  ', 'en', 'Mulan', 'http://www.csfd.cz/film/14997-legenda-o-mulan/', 'MULAN SK.jpg', NULL, 'Starověká Čína. Ani Velká zeď nedokázala zadržet hordy bojovných Hunů, kteří napadli mírumilovnou Říši středu. Moudrý a mezi lidem oblíbený císař proto vyhlašuje mobilizaci - jeden muž z každé rodiny musí vstoupit do armády, která má v rozhodující bitvě odrazit vojska dobyvatelů. Mezitím se v jedné zapadlé vesničce připravuje poněkud rozjívená dívka Mulan na své první setkání se svatební dohazovačkou. Nejsvatějším úkolem každé mladé ženy jsou totiž vdavky a oddaná služba manželovi. Jedině v této roli přinese Mulan své rodině radost a úctu celé vesnice. Naneštěstí Mulan je na tuctovou nevěstu trochu divoká a tak její setkání s dohazovačkou dopadne zcela katastrofálně. Tuhle holku si nikdo nikdy nevezme! A k dovršení rodinného neštěstí přijíždí do vesničky císařští verbíři. Starý otec Mulan bude muset nastoupit do armády, protože v rodině není žádný jiný muž. Ještě téže noci se Mulan rozhodne. Aby zachránila otce, ostříhá si vlasy, převlékne se za chlapce a narukuje. V nastávajících nebezpečných dobrodružstvích naštěstí nebude sama. S pomocí duchů rodinných předků a rodinného draka strážného zachrání Mulan Čínu před krvelačnými hordami divokých Hunů a pro sebe a svou rodinu získá úctu celé země.', NULL, 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2215, '201307/PETER PAN CZ.m4v', 'Petr Pan', 3, 0, 'cz', '  ', 'en', '', 'http://www.csfd.cz/film/33901-petr-pan/', 'PETER PAN CZ.jpg', NULL, 'Příběh, který ve vašich srdcích zůstane navždy! Při sledování Disneyho originální klasické pohádky Petr Pan se i vy ve svých představách vznesete. Tento film vás totiž přesvědčí, že při troše dobré vůle je možné úplně všechno. Příběh plný nápadů, dobrodružství, nezapomenutelných postav, hudby a létacího prášku dosahuje nevídaných výšin! Wendy a její bráškové znají Petra Pana z příběhů v knížce, když je ale jednou vezme s sebou do kouzelného světa Země Nezemě, čeká je báječné dobrodružství. Letí s větrem o závod za Petrem a jeho kamarádkou vílou Zvonilkou, objevují ostrov a tajnou skrýš Petrovy tlupy Ztracených chlapců a svádějí vzdušné bitvy s divokými piráty vedenými nechvalně proslulým kapitánem Hookem!(oficiální text distributora)', NULL, 1953, '');
