INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2242, '201307/Romeo a Julie SK.m4v', 'Romeo a Julia', 3, 0, '  ', '  ', '  ', '', 'http://www.csfd.cz/film/236414-romeo-a-julie/', 'Romeo a Julie SK.jpg', NULL, 'Příběh o lásce tuleňů ze dvou znepřátelených rodů... Romantický příběh o lásce dvou, tentokrát tuleňů. Přes nesouhlas rodičů se do sebe zamilují. Juliin otec přislíbil ruku své dcery obludnému mroži Princovi. Julie proto předstírá smrt, aby mohla být znovu se svým milovaným Romeem. Plán se ale nezdaří. Zamilovaná dvojice bude potřebovat pomoc od přátel - otce Lawrence a Pusinkující rybky. Světoznámý příběh o lásce a předsudcích, tentokrát pod hladinou oceánu.(oficiální text distributora)', NULL, 2006, '');
