INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2247, '201309/BROTHER_BEAR SK.m4v', 'Medvedi bratia', 3, 0, 'sk', NULL, 'en', 'Brother Bear', 'http://www.csfd.cz/film/117712-medvedi-bratri/', 'BROTHER_BEAR SK.jpg', NULL, 'V novém animovaném dobrodružství z produkce studia Walta Disney se vydáme do nádherné krajiny v severozápadní Americe, kde se odehrává příběh chlapce Kenaie. Jeho život se nečekaně změní v okamžiku, kdy jej Velký duch promění v medvěda - zvíře, které mladý Kenai nenávidí nejvíce. Ve své nové podobě se spřátelí s medvědím mládětem jménem Koda a pomalu začíná přemýšlet, jak se vrátit do svého lidského těla. V patách je mu však jeho bratr, který chce Kenaie pomstít, aniž by věděl, že pronásleduje jeho saméhoV dobrodružném příběhu o statečnosti, cti a sebepoznání, ve kterém se objevuje spousta nových a nezapomenutelných postaviček, se mísí dechberoucí animace s city a humorem.(oficiální text distributora)', NULL, 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2248, '201309/MEDVIDEK_PU SK.m4v', 'Macko Pu', 3, 0, 'sk', NULL, 'en', 'Winnie the Pooh', 'http://www.csfd.cz/film/282847-medvidek-pu/', 'MEDVIDEK_PU SK.jpg', NULL, 'Ve Stokorcovém lese začíná den jako každý jiný. Pú se probouzí velice hladový a nemá doma žádný med. Takže se vydává ho sehnat, ale jeho pouť posléze nabere zcela jiný směr – nejprve kvůli soutěži, ve které se mají všichni pokusit najít Ijáčkovi nový ocas. Pú později nachází vzkaz od Kryštůfka Robina, který zní: „Odešel sem. Moc práce. Hnet tu budem.“ Pak ale Sova celý vzkaz pochopí zcela jinak a tvrdí, že jejich přítele uneslo stvoření, zvané „Hnetubud“. Velice záhy se všichni vydávají na napínavé dobrodružství ve snaze zachránit Kryštůfka Robina před imaginárním nebezpečím. Jak se ukazuje, medvěd, který původně toužil jen po trošce medu, má před sebou náročný den. Společnost Walt Disney Animation Studios se ve filmu Medvídek Pú vrací do Stokorcového lesa. Tento nový snímek, který oživuje nadčasové kouzlo, vtip a žertovnou atmosféru původních krátkých filmů, divákům znovu nabízí možnost bavit se ve společnosti "přihlouplého Medvěda" a jeho přátel Tygra, Králíčka, Prasátka, Sovy, Klokanice, Klokánka... a pochopitelně také Ijáčka, který ztratil ocásek.(oficiální text distributora)', NULL, 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2249, '201309/Pocahontas1 SK.m4v', 'Pocahontas', 3, 0, 'sk', NULL, 'en', NULL, 'http://www.csfd.cz/film/12856-pocahontas/', 'Pocahontas1 SK.jpg', NULL, 'Píše se rok 1607 a začíná průkopnické období nově příchozích obyvatelů Ameriky. Skupina britských dobrodruhů včetně chamtivého guvernéra společnosti Virginia, Johna Ratcliffa a pohledného námořního kapitána Johna Smitha připlouvá do Nového Světa na lodi "Susan Constant" s úmyslem dobývat zlato a další poklady. V té době krásná mladá indiánská princezna Pocahontas, dcera náčelníka Powhatana, přemýšlí o svém poslání v životě. Sní o tom, co se skrývá "hned za tou řekou". Měla by se provdat za Kocouma, drsného válečníka, kterého pro ni vybral její otec, nebo ji připraví osud něco jiného ? Pocahontas si pro odpověď přichází do "Kouzelného lesa", kde udílí svoje rady Babička Vrba, čtyřista let starý mystický duch, sídlící ve starém stromě. Pocahontas získává také podporu od svých dvou lesních kamarádů - rozpustilého mývala Meeko a divokého kolibříka Flita. Britští průkopníci hned po svém příjezdu plní Rateliffovy rozkazy a dobývají zlato, zanechávajíce za sebou rozsáhlé škody a zdevastovanou zem. John Smith se setkává s Pocahontas. Přestože zpočátku mají mezi sebou konflikty, jeden druhého přitahují a on díky jí poznává svět, který nepoznal nikdy předtím. Pocahontas ho poučí, že každý kámen, strom a každá živá bytost má svého živého ducha a vysvětluje mu, jak "malovat se všemi barvami větru". V kontrastu s přátelstvím mezi Smithem a Pocahontas se vztahy mezi Brity a domorodými Američany dostanou do kritického stádia, kdy každým dnem stoupá strach a nenávist. Indiáni zajmou Smitha a odsoudí ho k trestu smrti. Pocahontas statečně nasadí svůj vlastní život, když se vrhne mezi kata a Smitha a prohlásí, že napřed musí zabít ji. Smith ji později vrátí její oběť, zachrání ji od britské střely, ale je raněn. V mohutném a dojemném finále se on a Pocahontas musí rozloučit, vědí však, že jejich duchové budou navždycky spjati na cestě, která nikdy nekončí.(oficiální text distributora)', NULL, 1995, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2250, '201309/RATATOUILLE SK.m4v', 'Ratatouille', 3, 0, 'sk', NULL, 'en', NULL, 'http://www.csfd.cz/film/150988-ratatouille/', 'RATATOUILLE SK.jpg', NULL, 'Oscarem oceněný režisér Brad Bird a vypravěčští mistři z Pixar Animation Studios uvádějí film Ratatouille, nejoriginálnější komedii letošního léta o tak bizarním přátelství, jaké si jen lze představit. Hlavním hrdinou filmu je krysák Remy, který odvážně sní o tom, že se stane vyhlášeným kuchařem v pařížské pětihvězdičkové restauraci. Společně se zkrachovalým vynašečem kuchyňských odpadků Linguinim se vydají za svým cílem stát se nejvyhlášenějším kuchařem v Paříži. Remy měl celý život dokonalý čich a na krysu také velice neobvyklý sen: vařit v luxusní restauraci. Nenechá se zviklat očividnými potížemi, se kterými se krysa, toužící po kariéře v oboru, který hlodavce přímo nenávidí, nevyhnutelně setká, nemluvě o výtkách ostatních členů rodiny, kteří se mu snaží domluvit, aby se spokojil s tradičním životem na skládce, a své touhy uspokojuje skromným flambováním a smažením. Ale když ho okolnosti doslova vrhnou do restaurace, kterou proslavil jeho kuchařský vzor Auguste Gusteau (jehož heslo "vařit může každý" Remyho po celý život inspirovalo), brzy zjistí, že pokud jste krysa a někdo vás uvidí, může vám v kuchyni jít doslova o život. Když už se zdá, že je se splněním Remyho snů konec, potká toho, koho potkat potřebuje, přítele, který mu věří: stydlivého a podřadného vynašeče odpadků, kterého z restaurace čeká vyhazov. Teď, kdy už oba nemají co ztratit, se z Remyho a Linguiniho stanou bizarní společníci, kteří, hnáni Linguiniho nešikovností a Remyho neobvyklými nápady, převrátí Paříž vzhůru nohama na neuvěřitelné cestě plné humorných zápletek, působivých zážitků a těch nejnepravděpodobnějších triumfů, o kterých by jeden bez druhého ani nesnili.(oficiální text distributora)', NULL, 2007, '');
