INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2251, '201309/TARZAN1 SK.m4v', 'Tarzan', 3, 0, 'sk', NULL, 'en', NULL, 'http://www.csfd.cz/film/5979-tarzan/', 'TARZAN1 SK.jpg', NULL, 'Tarzan vyrůstal od útlého dětství v divoké africké džungli. Z osiřelého lidského mláděte, které před lety nezištně adoptovala gorila Kala, vyrostl silný a spravedlivý král džungle. Jeho dobrodružný život v korunách stromů obrátí naruby expedice profesora Portera, který přijel do africké džungle studovat život goril. Tarzan si poprvé uvědomí, že je člověk a jeho prozření je umocněno opětovanou láskou k profesorově dceři Jane. Na romantiku ovšem zatím moc času nezbývá. Zlotřilý průvodce expedice Clayton hodlá uskutečnit svůj ďábelský plán chce gorily zbavit svobody a v klecích je výhodně prodat Tento celovečerní animovaný snímek studia Walta Disneye je už osmačtyřicátým filmovým zpracováním Tarzanova dobrodružství. Díky zkušenostem vynikajících animátorů a nejnovější technice je ovšem prvním tarzanovským filmem, který mohl do nejmenších detailů respektovat ducha legendární předlohy, jejímž autorem je Edgar Rice Burroughs. Samotná podstata animovaného filmu dovolila tvůrcům realizovat příběh s velkolepým rozmachem a efekty, které by byly v žánru hraného filmu zhola nemožné. Ostatně Burroughs už v roce 1936 psal svému synovi, že nejlepší filmová adaptace jeho románů by byla animovaná. Ale to by ji musel natočit někdo jako Disney...(oficiální text distributora)', NULL, 1999, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2252, '201309/UZASNAKOVI SK.m4v', 'Rodinka uzasnych', 3, 0, 'sk', NULL, 'en', 'The Incredibles', 'http://www.csfd.cz/film/127213-uzasnakovi/', 'UZASNAKOVI SK.jpg', NULL, 'Bob Parr, známy ako Mr. Úžasný, býval jedným z najväčších svetových superhrdinov a zachraňovanie sveta bolo jeho denným chlebom. Svoj superoblek musel zavesiť na klinec pred pätnástimi rokmi. Teraz pracuje ako odhadca škôd pre poisťovňu a bojuje s nudou a rastúcim bruchom. So svojou manželkou (bývalou Elastiženou) a tromi deťmi sa pokúšajú žiť normálny život. Jeho chvíľa prichádza, keď ho tajomný kontakt vyšle na vzdialený ostrov s tajným poslaním. Tam zistí, že génius zla pripravuje voči svetu diabolskú pomstu.(oficiální text distributora)', NULL, 2004, '');
