INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2356, '201311/FAST AND FURIOUS 6.m4v', 'Rychle a zbesile 6', 15, 37, 'cz', NULL, 'en', 'Fast and Furious 6', 'http://www.csfd.cz/film/297625-rychle-a-zbesile-6/', 'FAST AND FURIOUS 6.jpg', 6, 'Na konci pátého dílu se Dom (Vin Diesel) a Brian (Paul Walker) radovali z dobře odvedené práce, když jejich zásluhou skončil jeden mafián z Ria o sto miliónů dolarů chudší a oni o stejnou sumu bohatší. Vše však mělo háček, nad oběma visela hrozba vězení, a tak se museli rozprchnout po světě, daleko od svých blízkých. Zvláštní agent Hobbs (Dwayne Johnson), jenž jim šel po krku, se mezitím zaměřil na novou partičku bezohledných námezdných řidičů, kteří svých schopností za volantem využívají k páchání zločinu. Skupina vedená bezohledným a chladnokrevným géniem zločinu Owenem Shawem (Luke Evans) mu ale neustále uniká, a tak se ji rozhodne zastavit jejich vlastními zbraněmi. S pomocí rychlých řidičů, kteří jako by se za volantem narodili. A těmi nejlepšími jsou přece Dom, Brian a jejich tým. Odměna za takovou práci? Úplné očištění a možnost vrátit se domů. Pro Doma je tu ještě jedna speciální motivace – jeho holka Letty (Michelle Rodriguez), kterou všichni považovali za mrtvou, žije a navíc pracuje pro nepřítele.(oficiální text distributora)', NULL, 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2357, '201311/STAR_TREK__INTO_DARKNESS.m4v', 'Into Darkness', 4, 38, 'en', 'cz', NULL, NULL, 'http://www.imdb.com/title/tt1408101/?ref_=nv_sr_1', 'STAR_TREK__INTO_DARKNESS.jpg', 12, 'Directed by J.J. Abrams.  With Chris Pine, Zachary Quinto, Zoe Saldana, Benedict Cumberbatch. After the crew of the Enterprise find an unstoppable force of terror from within their own organization, Captain Kirk leads a manhunt to a war-zone world to capture a one-man weapon of mass destruction.', NULL, 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2358, '201311/STAR_TREK_XI_EL.m4v', 'Star Trek (Restart)', 4, 38, 'en', 'cz', NULL, NULL, 'http://www.imdb.com/title/tt0796366/?ref_=fn_al_tt_1', 'STAR_TREK_XI_EL.jpg', 11, 'Directed by J.J. Abrams.  With Chris Pine, Zachary Quinto, Simon Pegg, Leonard Nimoy. The brash James T. Kirk tries to live up to his father''s legacy with Mr. Spock keeping him in check as a vengeful, time-traveling Romulan creates black holes to destroy the Federation one planet at a time.', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2359, '201311/THE_CROODS.m4v', 'Krudovci', 3, 0, 'sk', NULL, 'en', 'The Croods', 'http://www.csfd.cz/film/269237-croodsovi/', 'THE_CROODS.jpg', NULL, 'Dobrodružná animovaná komedie vypráví příběh první pravěké rodiny na světě, která se jmenuje Croodsovi. Taťka Grug miluje a chrání svou rodinu. Řídí se svéráznou filozofií pro přežití „Strach je dobrý, změna špatná“, a drží se jí až do chvíle, kdy dojde k zničení jejich rodinné jeskyně, kde doposud spokojeně bydleli. Grug je nakonec nucen uznat, že lepší než zde jen přežívat, je začít znovu opravdu žít někde jinde. A tak Croodsovi podniknou cestu do neznáma, ohromující výlet do krajiny plné nejrůznějších, neuvěřitelných a a někdy i pěkně nebezpečných stvoření. Jejich pohled na svět se díky tomu navždy změní. Croodsovi čeká velká zkouška, ale také největší dobrodružství jejich života.   A kdo všechno do rodiny Croodsových vlastně patří? Těší se na vás autoritativní taťka Grug se svou zásadovou, podobně drsně laděnou ženou Uggou a s třemi svéráznými dětmi: nejmladší čtyřletou uličnicí Sandy, rebelující puberťačkou Iškou a devítiletým Thunkem, který by si přál být skvělým lovcem jako jeho otec, ale zatím mu k naplnění snu toho dost chybí… Součástí této jedinečné prehistorické rodinky je i babi, Grugova pravěká a trochu divoká tchyně. A navíc se ke Croodsům ještě připojí Boy, okouzlující mladík, který má na svém kontě některé skvělé objevy - například oheň nebo boty. A tak se Boy stává tím, kdo Croodsovým představí nové možnosti a nový svět. V něm ovšem čekají i další podivuhodní tvorové. Někteří jsou roztomilými mazlíčky a jiní zase nejdou pro ránu nebo pěkný kousanec daleko. Ostatně už jejich jména něco naznačují: Krokoštěně, Myšlon, Bouchač, Pásek, Pštrovce nebo Medvědí sova …(oficiální text distributora)', NULL, 2013, '');
