INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2363, '201312/MONSTERS_UNIVERSITY.m4v', 'Priserky: Univerzita', 3, 0, 'sk', NULL, 'en', 'Monsters University', 'http://www.csfd.cz/film/264581-univerzita-pro-priserky/', 'MONSTERS_UNIVERSITY.jpg', 101, 'Už od té doby, kdy byl Mike Wazowski jenom malé monstrum, snil o tom, že se stane úspěšnou příšerkou. A všichni přeci vědí, že ty nejlepší příšerky pocházejí z Univerzity pro příšerky. Ale hned během prvního semestru na univerzitě se začnou Mikeovy plány hroutit, když mu cestu zkříží příšerkovské eso James P. Sullivan, řečený Sulley, který je na strašení přirozený talent. Této dvojici se život na univerzitě i na koleji malinko vymkne kontrole a jejich soutěživost je dovede až k vyhazovu z elitního univerzitního strašícího programu. A aby to bylo ještě horší, Mike i Sulley si uvědomí, že budou muset spolupracovat, pokud budou chtít své průšvihy napravit.(oficiální text distributora)', NULL, 2013, '');
