INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2370, '201312/Tinkerbell_4_TAJOMSTVO_KRIDEL.m4v', 'Tajomstvo kridel', 3, 39, 'sk', NULL, 'en', 'Tinker Bell: Secret of the Wings', 'http://www.csfd.cz/film/316610-zvonilka-tajemstvi-kridel/', 'Tinkerbell_4_TAJOMSTVO_KRIDEL.jpg', 4, 'Zvonilka: Tajemství křídel je fantastickým příběhem, ve kterém se Zvonilka vydává napříč zapovězenou hranicí ze svého domova do tajemných Zimních lesů, kde se setkává s celou řadou nových zimních víl a odhaluje kouzelné tajemství – Zvonilka má totiž sestru, stejně veselou a hravou Modrovločku. Ta se s ní na oplátku vydává na návštěvu do Zvonilčina domova. Když se o jejich pouti dozví královna Clarion a král Milori, vládci Zimních lesů, varují obě před tím, že už nikdy nesmějí zapovězenou hranici překročit, a vysvětlují jim oběť, kterou sami museli za podobný přestupek podstoupit. Zvonilka a Modrovločka ale nemohou ovlivnit přírodu. Když stroj na výrobu sněhu, který Modrovločka při svém putování po Zvonilčině domově používá, začne ohrožovat strom, jenž je zdrojem kouzelného prášku víl, jsou obě sestry nuceny sjednotit letní a zimní víly a vše napravit dříve, než bude pozdě.(oficiální text distributora)', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2371, '201312/TINKERBELL_VELKA_ZACHRANNA_VYPRAVA.m4v', 'Velka zachranna vyprava', 3, 39, 'sk', NULL, 'en', 'Tinker Bell and the Great Fairy Rescue', 'http://www.csfd.cz/film/284091-zvonilka-a-velka-zachranna-vyprava/', 'TINKERBELL_VELKA_ZACHRANNA_VYPRAVA.jpg', 3, 'Co byste dělali, kdybyste potkali vílu? Staňte se svědky prvního setkání Zvonilky s člověkem, který není tím, kým myslíte. Dlouho před tím, než se Zvonilka setkala s Wendy a ztracenými chlapci, se seznámila s Lizzy, dívenkou, která pevně věřila na sílu vílího prášku a kouzelnou říši víl. Když víla jednoho léta navštíví rozkvetlé anglické louky, protnou se poprvé dva velmi odlišné světy a vznikne zvláštní pouto mezi Zvonilkou a zvídavým děvčátkem, které potřebuje kamarády. Zatímco se ostatní víly vypraví na odvážnou záchrannou akci, pustí se Zvonilka do velmi riskantního dobrodružství a ohrozí vlastní bezpečí i budoucnost všech víl. Prožijte naplno úžasný Disneyho nový příběh o síle důvěry a přátelství. Film Zvonilka a velká záchranná výprava je plný napětí a snění a učaruje celou vaši rodinu.(oficiální text distributora)', NULL, 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2372, '201312/TRANSFORMERS_1.m4v', 'Transformers', 4, 40, 'cz', NULL, 'en', 'Transformers', 'http://www.csfd.cz/film/220919-transformers/', 'TRANSFORMERS_1.jpg', 1, 'Transformers patří k nejúspěšnějším hračkám v historii. Roboti, kteří se dokážou přetransformovat v libovolný dopravní prostředek, jsou populární natolik, že se stali hrdiny akčního animovaného seriálu. Mohli by ale být také předlohou pro hraný celovečerní akční film? Pochybnosti hravě rozmetají dva argumenty – úspěšné snímky se v současnosti natáčejí podle mnohem podivnějších námětů (třeba podle atrakcí v zábavních parcích, viz Piráti z Karibiku) a Transformers se navíc mohou opřít o dva velikány světového filmu, produkujícího Stevena Spielberga a režírujícího Michaela Baye (Mizerové 1 + 2, Skála, Armageddon). Ti dva mají dostatečný kredit na to, aby se z projektu, který by jinak doprovázely pochyby, stal jeden ze nejočekávanějších filmů léta. Prakticky odnepaměti ve vesmíru kolem nás zuří válka dvou vyspělých mimozemských civilizaci, „hodných“ Autobots a „zlých“ Decepticons. Dousd se nám vyhýbala, ale když tito vyspělí mechanoidi objeví na Zemi zdroj energie, jehož ovládnutí může jedné či druhé straně přinést konečné vítězství, promění se naše planeta v peklo na zemi (nebo spíš ve vesmíru). Jejich invazi prožíváme společně s teenagerem Samem (Shia LaBeouf), kterého doposud trápila pouze škola, absence pořádné káry a slečna jménem Mikaela (Megan Fox), jež dává přednost jeho spolužákům. Ve chvíli, kdy se v televizi objeví první zprávy o ničivých robotech, začne mít o jeden problém navíc. Je to právě on, kdo nevědomky ukrývá klíč k onomu životně důležitému energetickému zdroji, proto má nárok na první místo na seznamu ohrožených druhů. Hned za ním figuruje zbytek naší civilizace… Spojením Stevena Spielberga s Michaelem Bayem vznikl z dětské hračky silný epický příběh, který úspěšně kombinuje dramatičnost Války světů s diváckou vstřícností Dne nezávislosti. Přestože příběhu dominují velkolepé akční sekvence, o které se starali mistři oboru, specialisté z trikového studia George Lucase Industrial Light and Magic, je v něm dostatek prostoru i pro humor a pro originální zápletku. „Nejdůležitější bude přesvědčit lidi o tom, že film, který se inspiroval dětskou hračkou, může být pro dospělé. Stačí ale pár vteřin libovolné akční sekvence a vaše předsudky budou ty tam,“ tvrdí producent filmu Lorenzo di Bonaventura.(oficiální text distributora)', NULL, 2007, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9965, '201405/S105.m4v', 'The Wolf and the Lion', 4, 1000, 'cz', 'cz', 'en', '', '', 'S105.jpg', 105, 'The Wolf and the Lion: Tyrion''s predicament becomes common knowledge in King''s Landing, making life difficult for Eddard Stark. Ned''s position is already precarious due to a disagreement with King Robert Baratheon over plans to assassinate Daenerys before she can rally the Dothraki to invade Westeros. In the Eyrie, Catelyn is shocked by her sister Lysa''s paranoia and distrust.', '2014-05-29 08:56:05.869', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9966, '201405/S106.m4v', 'A Golden Crown', 4, 1000, 'cz', 'cz', 'en', '', '', 'S106.jpg', 106, 'A Golden Crown: In Vaes Dothrak, Viserys'' frustrations at the constant delays before he can receive his crown boil over. In King''s Landing, while King Robert goes on a hunt, Eddard issues a decree that could splinter the Seven Kingdoms and makes a startling discovery about the heirs to the throne. In the Eyrie, Tyrion Lannister wagers everything on the greed of the sellsword Bronn.', '2014-05-29 08:56:05.94', NULL, NULL);
