INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2380, '201312-2/JA_ZLODUCH.m4v', 'Ja, zloduch', 3, 33640, 'sk', '  ', 'en', 'Despicable Me', 'http://www.csfd.cz/film/263442-ja-padouch/', 'JA_ZLODUCH.jpg', 1, 'Ve šťastném předměstském sousedství, obklopeném bílým plaňkovým plotem a kvetoucími keři růží, stojí černý dům s mrtvým trávníkem. Aniž by to sousedé věděli, hluboko pod tímto domem je obrovský tajný úkryt. A zde nacházíme Grua (STEVE CARELL) obklopeného armádou svých uličnických a zlomyslných přisluhovačů, mimoňů, který plánuje tu největší loupež v historii celé zeměkoule. Gru se chystá ukrást Měsíc (ano, Měsíc!). Gru si libuje ve všem špatném. Vyzbrojen svým arzenálem Zmenšovacích paprsků, Mrazících paprsků – mrazáků a obojživelných bojových vozidel poráží vše, co mu stojí v cestě. A to všechno až do okamžiku, kdy se střetne s neochvějným přesvědčením třech malých osiřelých děvčátek, které na něj pohlédnou a vidí v něm něco, co nikdo jiný dosud neviděl: možného tátu.(oficiální text distributora)', NULL, 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2373, '201312/TRANSFORMERS_2_1.m4v', 'Pomsta Porazenych', 4, 40, 'cz', NULL, 'en', 'Transformers: Revenge of the Fallen', 'http://www.csfd.cz/film/232460-transformers-pomsta-porazenych/', 'TRANSFORMERS_2_1.jpg', 2, 'Puberťák Sam Witwicky (Shia LaBeouf) se už vzpamatoval z prvního setkání s hodnými Autoboty a zlými Deceptikony. Navíc dospěl a začal se připravovat na opuštění rodného hnízda, vstříc bouřlivému vysokoškolskému životu. Náladu mu kazí snad jen fakt, že vztah s rozkošnou Mikaelou (Megan Fox) se promění na chození na dálku, protože ona na univerzitní studia nemá peníze. Odloučení naštěstí netrvá dlouho. Když Sam objeví úlomek matérie, o kterou spolu Autoboti a Deceptikoni tolik bojovali v jedničce, a získá z něj pro obě strany drahocenné informace, začnou po něm jít mechaničtí gauneři z celé galaxie. A že jich tu je. Podle trikových specialistů ze studia ILM se ve filmu objeví kolem šedesáti různých robotů. Sama proto brzy začne pálit univerzitní půda pod nohama a v doprovodu Mikaley, věrného Autobota přezdívaného Bumblebee, který se dokáže přetransformovat v nadupaný Chevrolet, a bohužel i za asistence rodičů, před nimi prchá cestou necestou.(oficiální text distributora)', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2374, '201312/TRANSFORMERS_3.m4v', 'Temna strana mesiaca', 4, 40, 'cz', NULL, 'en', 'Transformers: Dark of the Moon', 'http://www.csfd.cz/film/261832-transformers-3/', 'TRANSFORMERS_3.jpg', 3, 'V originále nese třetí díl Transformers podtitul „Temná strana Měsíce“. Právě tady před lety posádka první americké měsíční expedice Apollo 11 objevila mimozemské vesmírné plavidlo a moudře si to nechala pro sebe, respektive pro tajné služby. Podle Autobotů, kteří už dvakrát dokázali ochránit Pozemšťany před ničivou invazí jejich zlých konkurentů Deceptikonů, to ale byla zásadní chyba. Chyba, která by mohla zničit celou naši civilizaci, pokud nám Autoboti znovu nepomůžou. Kromě starých známých plecháčů v čele s Optimem Primem a Bumblebeem se na scénu vrací jejich největší lidský spojenec Sam (Shia LaBeouf), životem a bitvami s roboty otřískaný mladý muž, který chce ze všeho nejvíc konečně začít žít normální život. Bohužel už ne po boku šťabajzny Mikaely (Megan Fox), která byla z důvodů, jež znají jen Megan/režisér Bay/představitelé Paramountu, vyškrtnuta ze scénáře. Sama a jeho hormony naštěstí scenárista Ehren Kruger dlouho zahálet nenechá, když mu do cesty přihraje velepohlednou Carly (modelka Rosie Huntington – Whiteley). Ta se naneštěstí stane příčinou Samova dalšího angažmá v nekonečném souboji mezi Autoboty a Deceptikony. Ti se totiž rozhodli zlikvidovat celé Chicago, v němž se Carly právě vyskytuje, a tak se Sam chtě nechtě musí do tohoto města vydat, aby se ji pokusil zachránit. A jen tak bokem i celou lidskou rasu.(oficiální text distributora)', NULL, 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2375, '201312-2/A_I_ARTIFICIAL_INTELLIGENCE.m4v', 'Artificial Intelligence: A.I.', 4, 0, 'en', NULL, 'cz', NULL, 'http://www.imdb.com/title/tt0212720/?ref_=nv_sr_1', 'A_I_ARTIFICIAL_INTELLIGENCE.jpg', NULL, 'Directed by Steven Spielberg.  With Haley Joel Osment, Jude Law, Frances O''Connor, Sam Robards. A highly advanced robotic boy longs to become "real" so that he can regain the love of his human mother.', NULL, 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2376, '201312-2/ALIEN_DISC_1.m4v', 'Alien', 4, 4, 'en', NULL, 'cz', 'Alien', 'http://www.imdb.com/title/tt0078748/?ref_=nv_sr_1', 'ALIEN_DISC_1.jpg', 1, 'Directed by Ridley Scott.  With Sigourney Weaver, Tom Skerritt, John Hurt, Veronica Cartwright. The commercial vessel Nostromo receives a distress call from an unexplored planet. After searching for survivors, the crew heads home only to realize that a deadly bioform has joined them.', NULL, 1979, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2377, '201312-2/ANABELA.m4v', 'Anabelino zelanie', 3, 0, 'sk', NULL, 'en', 'Anabelle''s Wish', 'http://www.csfd.cz/film/11059-anabela-vanocni-prani/', 'ANABELA.jpg', NULL, 'Billy je malý chlapec, který při požáru farmy ztratil řeč. Žije se svým dědečkem a zvířátky na samotě a jedinou jeho lidskou kamarádkou je roztomilá holčička Emily ze sousedství. Na Štědrý den se na farmě narodí telátko Anabela, se kterou Billy zažije spoustu dobrodružství... Ale protože nemluví, má odjet k tetě do města aby se mohl nechat léčit. Billy však farmu a všechny známé tváře nechce opustit. Naštěstí přichází čas vánoc, - čas splněných přání a překvapení, kdy zvířátka na jediný den v roce získávají dar řeči.(oficiální text distributora)', NULL, 1997, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2378, '201312-2/APOCALYPSE_NOW.m4v', 'Apocalypse Now', 15, 0, 'en', NULL, 'cz', NULL, 'http://www.imdb.com/title/tt0078788/?ref_=nv_sr_1', 'APOCALYPSE_NOW.jpg', NULL, 'Directed by Francis Ford Coppola.  With Martin Sheen, Marlon Brando, Robert Duvall, Frederic Forrest. During the U.S.-Viet Nam War, Captain Willard is sent on a dangerous mission into Cambodia to assassinate a renegade colonel who has set himself up as a god among a local tribe.', NULL, 1979, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2379, '201312-2/BRIDGET_JONES.m4v', 'Bridget Jones: S rozumem v koncich', 6, 0, 'cz', NULL, 'en', 'Bridget Jones: The Edge of Reason', 'http://www.csfd.cz/film/134445-bridget-jonesova-s-rozumem-v-koncich/', 'BRIDGET_JONES.jpg', NULL, 'Renée Zellwegerová, Hugh Grant a Colin Firth se opět setkávají v romantické komedii BRIDGET JONESOVÁ: S ROZUMEM V KONCÍCH. V tomto pokračování DENÍKU BRIDGET JONESOVÉ se setkáváme s titulní hrdinkou tam, kde jsme se s ní rozloučili: šťastnou a spokojenou v náručí vytouženého milence Marka Darcyho (Colin Firth). Mark je zatím uznalý, ohleduplný a tolerantní (takřka) ke všem žárlivým záchvatům. Bridget totiž nejde na mysl, proč by se všechny ženy v Londýně - včetně Markovy nové asistentky - "dlouhonohé hrozby" - nesnažily Darcyho odlákat od baculaté, umíněné a často nevhodně se chovající Bridget, která neustále obtěžuje svým tvrzením: "Já přece nikdy neříkám nic jiného, než pravdu." Jakmile se na scéně objeví "dlouhonohé nebezpečí", Bridgetiny růžové obláčky, na kterých se až dosud vznášela, náhle zešednou. Stále častější záchvaty sebepodceňování začnou negativně ovlivňovat její lásku s Darcym. Když už se zdá, že rozbouřená hladina jejich vzájemného vztahu se jen tak neuklidní, objeví se na scéně Bridgetin bývalý šéf, zkušený dobyvatel ženských srdcí Daniel Cleaver (Hugh Grant). Bridget se rychle vymaní z trapné situace a upadne do romantického omylu - avšak stále se jí daří prodírat se spletí trapasů a problémů, jaké provázejí každou pracující ženu, zejména když se stala symbolem jako "samostatná hrdinka, která si dovede poradit se životem".(oficiální text distributora)', NULL, 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9967, '201405/S107.m4v', 'You Win or You Die', 4, 1000, 'cz', 'cz', 'en', '', '', 'S107.jpg', 107, 'You Win or You Die: Eddard Stark''s investigations in King''s Landing reach a dramatic climax as he confronts Queen Cersei with his findings. Tywin Lannister prepares his forces for battle and gives Jaime an important assignment. Ser Jorah Mormont''s actions inspire Khal Drogo to make an important vow. Jon Snow finally takes his place amongst the Night''s Watch.', '2014-05-29 08:56:06.008', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2381, '201312-2/Jak_vycvicit_draka.m4v', 'Jak vycvicit draka', 3, 0, 'cz', NULL, 'en', 'How to Train Your Dragon', 'http://www.csfd.cz/film/234768-jak-vycvicit-draka/', 'Jak_vycvicit_draka.jpg', NULL, 'Viděli jste už trojrozměrného draka? A Vikinga? Pokud ne, budete mít příležitost v animované komedii Jak vycvičit draka od studia DreamWorks. Rodičové chlupaté Kung Fu Pandy a zeleného zlobráka Shreka se tentokrát rozhodli pro podívanou, která je ve všech ohledech poněkud drsnější. Jejich nejnovější dobrodružství se odehrává na drsném Severu, který obývají ještě drsnější Vikingové a spolu s nimi i draci. Ti jsou jednoznačně nejdrsnější. Jediný, kdo není drsný ani trochu, je hlavní hrdina Škyťák. Vikinský teenager nějak nemůže zapadnout mezi ostatní obyvatele rodného ostrova Blpu, protože na rozdíl od nich spíš než svaly (kterých má pomálu), používá hlavu. Asi by se to dalo přejít, kdyby Škyťák nebyl synem náčelníka vesnice, Kliďase Velikána. Taťka má syna rád, ale bohužel si uvědomuje jeho „nedostatečnost“. Zvlášť když se blíží Škyťákovo iniciační uvedení mezi dospělé Vikingy, které lze úspěšně absolvovat jediným způsobem – zabitím draka v souboji. Draci jsou totiž na Blpu jediným, zato permanentním problémem. Desítky druhů létajících ještěrů prakticky neustále útočí na vesnici, kradou domácí zvířata, spalují úrodu i hospodářská stavení, a tak je morální povinností každého Vikinga bojovat proti drakům, jak se dá. I Škyťák by samozřejmě rád nějakého toho Nodra smrťáka, Ohavného zipáka nebo Hrůzáka hrozivého skolil, jenže matička příroda ho na to opravdu vybavila nevalně. Nepomáhají ani užitečné lekce v Drakoškole místního samorosta, kováře Tlamouna. Škyťák je naštěstí hlava otevřená a tam, kde schází hrubá síla, pomůže hlava, díky níž zkonstruuje samostříl, kterým zaútočí na nejobávanějšího z draků, prakticky nepolapitelnou Noční fúrii. Jediný výstřel (velmi úspěšný) změní budoucnost soužití lidského a dračího plemene na ostrově Blpu. Když se totiž Škyťák vydá zraněného draka dorazit a stát se tak definitivně mužem, cosi se v něm při pohledu do jasných dračích očí zlomí. A ten zlom bude začátkem jednoho krásného přátelství. Teď už jen zbývá přesvědčit ostatní Vikingy, že skutečný nepřítel číhá jinde.(oficiální text distributora)', NULL, 2010, '');
