INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2382, '201312-2/LEON.m4v', 'Leon', 15, 0, 'cz', NULL, 'fr', 'Léon', 'http://www.csfd.cz/film/773-leon/', 'LEON.jpg', NULL, 'Strhující thriller o dvanáctileté dívce, která osudově zasáhne do života stárnoucího profesionálního zabijáka. Neobyčejný milostný příběh profesionálního vraha a mladé dívky. Leon je nájemný vrah, člověk, kterého nedokáže nic zastavit před splněním úlohy. Do jeho života vstoupí 12-letá Mathilda. Rodina nevinné Mathildy byla vyvražděná zkorumpovaným policajtem a ona se rozhodla pro krutou pomstu.(oficiální text distributora)', NULL, 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2383, '201312-2/MILLION_DOLLAR_HOTEL.m4v', 'Million Dollar Hotel', 15, 0, 'en', 'cz', 'en', NULL, 'http://www.imdb.com/title/tt0120753/?ref_=nv_sr_3', 'MILLION_DOLLAR_HOTEL.jpg', NULL, 'Directed by Wim Wenders.  With Jeremy Davies, Milla Jovovich, Mel Gibson, Jimmy Smits. Tragi-comic, romantic whodunnit set in a run down hotel which plays host to mentally ill people too poor to afford medical insurance.', NULL, 2000, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2384, '201312-2/PLANES.m4v', 'Lietadla', 3, 0, 'sk', NULL, 'en', 'Planes', 'http://www.csfd.cz/film/307968-letadla/', 'PLANES.jpg', NULL, 'Z oblačných výšin světa filmu Auta přilétají Letadla, animovaný komediálně-dobrodružný snímek nabitý akcí, ve kterém se nám představuje Prášek, práškovací letoun s velkým srdcem a rychlými křídly, jenž sní o tom, že se zúčastní nejúžasnějšího leteckého závodu kolem světa. V cestě mu stojí jen několik poněkud větších problémů - Prášek není tak docela stavěný pro závody, a kromě toho má tak trochu strach z výšek. Navzdory fóbiím se Práškovi za podpory svého učitele z námořního letectva, Skippera, podaří s odřenou vrtulí kvalifikovat do hlavního závodu. Jeho sportovní nadšení a rychlost se ale příliš nezamlouvají obhajujícímu šampiónovi Ripslingerovi, který se neštítí ničeho, jen aby Práška zastavil. Když dojde v průběhu vyvrcholení závěrečného závodu k neštěstí, je Práškova odvaha vystavena skutečné zkoušce. Za podpory přátel starých i nových Prášek vystoupá do výšin, o kterých se mu doposud ani nezdálo, a současně užaslým přihlížejícím dokáže, že svět v oblacích skutečně nezná žádných hranic.(oficiální text distributora)', NULL, 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2385, '201312-2/ST6_UNDISCOVERED_COUNTRY.m4v', 'Undiscovered Country', 4, 38, 'en', 'cz', NULL, NULL, 'http://www.imdb.com/title/tt0102975/?ref_=fn_al_tt_1', 'ST6_UNDISCOVERED_COUNTRY.jpg', 6, 'Directed by Nicholas Meyer.  With William Shatner, Leonard Nimoy, DeForest Kelley, James Doohan. On the eve of retirement, Kirk and McCoy are charged with assassinating the Klingon High Chancellor and imprisoned. The Enterprise crew must help them escape to thwart a conspiracy aimed at sabotaging the last best hope for peace.', NULL, 1991, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2386, '201312-2/ST7_GENERATION.m4v', 'Generation', 4, 38, 'en', 'cz', NULL, NULL, 'http://www.imdb.com/title/tt0111280/?ref_=nv_sr_1', 'ST7_GENERATION.jpg', 7, 'Directed by David Carson.  With Patrick Stewart, William Shatner, Malcolm McDowell, Jonathan Frakes. Captain Picard, with the help of supposedly dead Captain Kirk, must stop a madman willing to murder on a planetary scale in order to enter an energy ribbon.', NULL, 1994, '');
