INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2389, '201312-2/WHAT_WOMEN_WANT.m4v', 'Po cem zeny touzi', 6, 0, 'cz', NULL, 'en', 'What Women Want', 'http://www.csfd.cz/film/12862-po-cem-zeny-touzi/', 'WHAT_WOMEN_WANT.jpg', NULL, 'Nick Marshall (Mel Gibson) je chamtivý šovinistický hlupák, zaměstnaný ve velmi dobře prosperující reklamní agentuře v Chicagu, kterou vede Dan Vanamaker (Alan Alda). Ženy jsou pro něj převážně jako tmel v jeho rukou, což podporuje jeho přesvědčení, že je schopen se vmanévrovat do ženské atmosféry. Je přesvědčen, že jedině on má nárok na uvolněné místo kreativního ředitele v agentuře. Ale "jeho" místo, k Nickově velkému překvapení a zklamání, dostává Darcy Maguire (Helen Hunt), ambiciózní kariéristická žena, která, zdá se, je předurčena být v neustálém konfliktu s egoistickým Nickem. Po nehodě v koupelně (Nick zasažen elektrickým proudem spadne do vany), ráno objeví, že má schopnost poslouchat, co si ženy myslí. Jeho služebná se nepošklebovala jako obvykle, dokonce ani jeho vrátná mu slovně nelichotí, jaké má krásné pozadí. To je, co si tyto ženy myslí, co je uloženo jen v jejich hlavách. Zpočátku je Nick poměrně zděšen, ale brzy je rozverně vzrušen představou, že zná všechno, co je ukryto v ženském mozku. A to se velmi hodí v přítomnosti kolegyně Darcy. Náhle dva rivalové jsou spojenci.(oficiální text distributora)', NULL, 2000, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2390, '201401/Dead Man.m4v', 'Dead Man', 15, 0, 'en', 'cz', NULL, 'Dead Man', 'http://www.imdb.com/title/tt0112817/?ref_=nv_sr_2', 'Dead Man.jpg', NULL, 'Directed by Jim Jarmusch.  With Johnny Depp, Gary Farmer, Crispin Glover, Lance Henriksen. On the run after murdering a man, accountant William Blake encounters a strange North American man named Nobody who prepares him for his journey into the spiritual world.', NULL, 1995, '');
