INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2680, '201402/DARK_SHADOWS_2012.m4v', 'Temne Stiny', 6, 0, 'cz', NULL, 'en', 'Dark Shadows', 'http://www.csfd.cz/film/243202-temne-stiny/', 'DARK_SHADOWS_2012.jpg', NULL, 'V roce 1752 vypluli Joshua a Naomi Collinsovi se svým synem Barnabasem lodí z anglického Liverpoolu, aby začali nový život v Americe. Ale ani oceán jim nepomůže v tom, aby unikli záhadnému prokletí, které jejich rodinu pronásleduje. Uplyne dvacet let a Barnabasovi leží svět u nohou - tedy aspoň město Collinsport ve státě Maine. Majitel panství Collinwood - Barnabas je bohatý pán, má moc a je to notorický playboy…dokud neudělá zásadní chybu - zlomí totiž srdce Angelique Bouchard . Angelique je čarodějnice každým coulem. Přichystá mu horší osud, než je samotná smrt: promění ho v upíra a pak ho upálí zaživa. O dvě stě let později se Barnabas nechtěně dostane ze své hrobky a přichází do naprosto jiného světa roku 1972. Vrací se na panství Collinwood a zjistí, že z kdysi velkolepého panství jsou dnes jen ruiny. Zbytku rozpadlé rodiny Collinsů se daří o něco lépe, každý skrývá své temné tajemství. Hlava rodiny Elizabeth Collins Stoddard přivolá blízkou psychiatričku Dr. Julii Hoffman, aby jim pomohla vyřešit rodinné trápení. V usedlosti žije také neúspěšný bratr Elizabeth Roger Collins její pubertální dcera, rebelka Carolyn a Rogerův předčasně vyspělý 10ti letý syn David Collins. Rodinná záhada se přenese i na správce Willieho Loomise, kterého hraje Jackie Earle Haley a na Davidovu novou chůvu Victorii Winters.(oficiální text distributora)', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2681, '201402/Den Sakala.m4v', 'Den Sakala', 15, 0, 'cz', NULL, 'en', 'The Day of the Jackal', 'http://www.csfd.cz/film/10147-sakal/', 'Den Sakala.jpg', NULL, 'Kultový politický thriller nakrútený podľa románu Fredericka Forsytha vychádza zo skutočných udalostí okolo pokusu o atentát na francúzskeho prezidenta generála Charlesa de Gaullea. Začiatkom 60-tych rokov 20. storočia považovali bývalí členovia francúzskych légií osamostatnenie Alžírska a jeho odsúhlasenie francúzskou vládou za zradu svojich padlých spolubojovníkov. Vytvorili skupinu sústredenú na likvidáciu de Gaulla, avšak bez úspechu. Prezident sa tak stal najstráženejším človekom na svete. Po dolapení a zabití viacerých dôležitých členov skupiny si preto najali profesionálneho zabijaka s krycím menom Šakal, ktorý mal za úplne samostatnú operáciu zinkasovať pol milióna dolárov. Príbeh sa sústredí na prípravy atentátu a na snahu tajnej polície vystopovať Šakala. Film je klasikou svojho žánru a roku 1997 sa dočkal i remakeu.(oficiální text distributora)', NULL, 1973, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2682, '201402/JAK_NA_VEC.m4v', 'Jak na vec', 6, 0, 'cz', NULL, 'en', 'About a Boy', 'http://www.csfd.cz/film/18137-jak-na-vec/', 'JAK_NA_VEC.jpg', NULL, 'Osmatřicetiletý Will je typický londýnský playboy. Je bohatý, svobodný, bezdětný a bezstarostný. Ale jen do chvíle, než mu jedna z bezpočtu přítelkyň, které až dosud střídal ve svižném tempu, dá "kopačky". A víte proč? Kvůli svému dítěti. Právě tato nová a zcela nepochopitelná zkušenost přivede Willa do nedotčeného "loviště", a tím je Sdružení osamělých rodičů, kde jsou téměř výhradně svobodné či rozvedené ženy. Právě díky Susie, té nejatraktivnější, se Will jedné neděle seznámí s Marcusem. Je to divný kluk a ještě podivnější je jeho matka Fiona. Právě ten víkend, kdy poslala syna se sousedkou, se pokusí o sebevraždu, a tak se Will proti své vůli ocitne v roli záchranáře a vzápětí i pěstouna. Ten "spratek" se na něj překvapivě upnul, i když mu bylo od začátku jasné, že Fiona u Willa nemá nejmenší šanci. Dospělému chlapci zprvu není po chuti, že Marcus se stal jeho každodenním hostem. Ruší to jeho nicnedělání. Jenže pak se karta obrátí a Will toho malého nutně potřebuje, aby postupně začal chápat, že Marcus už navždy vstoupil do jeho života.(oficiální text distributora)', NULL, 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2683, '201402/PUBLIC_ENEMIES.m4v', 'Verejni nepratele', 15, 0, 'cz', NULL, 'en', 'Public Enemies', 'http://www.csfd.cz/film/236238-verejni-nepratele/', 'PUBLIC_ENEMIES.jpg', NULL, 'Přestože se tenhle příběh odehrál před bezmála osmdesáti lety, je až nebezpečně aktuální. Vypráví o muži, který měl obrovské sympatie veřejnosti, navzdory tomu, že páchal ty největší zločiny. Podle státních institucí vykrádal banky. Podle lidí trestal ty, kdo zavinili hospodářskou krizi. John Dillinger má v Americe podobnou pověst jako Václav Babinský u nás. Jeho život a dílo byly tak neuvěřitelné, že se staly živnou půdou pro spousty mýtů, které se na ně nabalily tak pevně, že pravdu od fikce už těžko někdo rozezná. Režisér Michael Mann (Nelítostný souboj, Miami Vice) se naštěstí místo hledání pravdy v kupce polopravd a legend soustředil na natočení drsného a živočišného kriminálního thrilleru svým specifickým rukopisem, který charakterizuje pečlivé budování postav a postupná gradace příběhu, který obvykle končívá v působivě natočeném dešti kulek. Michael Mann má ve zvyku pracovat s těmi nejlepšími, a proto si do role Johna Dillingera vybral asi nejpopulárnějšího herce současnosti Johnnyho Deppa, jeho životní lásku obsadil držitelkou Oscara Marion Cotillard (Edith Piaf) a postavu Dillingerovy nemesis, agenta FBI Melvina Purvise hraje tvář z největších kasovních trháků poslední doby Christian Bale (Temný rytíř, Terminátor 4). Veřejní nepřátelé jsou atraktivní i množstvím rovin, mezi kterými se jejich příběh pohybuje. Úspěšné tažení Dillingerovy bandy americkými bankami a postupně se utahující policejní smyčka na hrdlech jejích členů sice dominuje, ale vedle toho sledujete i Dillingerův intimní příběh, který je skoro stejně zajímavý jako jeho zločinecké eskapády, nebo válku o veřejné mínění, již lupič úspěšně svádí s etablující se FBI, která chce na jeho dopadení postavit smysl své existence. V přepadených bankách se choval jako jeho oblíbení filmoví hrdinové. K cizím penězům se navíc obvykle nedostával hrubým násilím, ale díky originálním nápadům realizovaným s neobvyklou drzostí. Z policejních pastí unikal zázrakem, a když už sklaply, nepředstavovaly pro něj problém ani mříže vězeňské cely.(oficiální text distributora)', NULL, 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9969, '201405/S109.m4v', 'Baelor', 4, 1000, 'cz', 'cz', 'en', '', '', 'S109.jpg', 109, 'Baelor: With Sansaʼs life in danger, Ned makes a fateful decision. Catelyn brokers an unsavory deal with the slippery Walder Frey. Tyrion acquires a mistress and is forced by his father to fight on the front lines. Robb wins his first major victory and captures a prized prisoner. Jon is rewarded for his valor and discovers a dark secret about Maester Aemon. As Drogoʼs wound festers, Daenerys defies his bloodrider Qotho and puts her trust in the enslaved witch Mirri Maz Duur.', '2014-05-29 08:56:06.147', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2684, '201402/Temny usvit.m4v', 'Temny usvit', 15, 0, 'cz', NULL, 'en', 'Rescue Dawn', 'http://www.csfd.cz/film/222600-temny-usvit/', 'Temny usvit.jpg', NULL, 'V dějinách velkých útěků těžko naleznete příběh, který by se podobal osudu Dietera Denglera, jediného Američana, jenž dokázal uniknout ze zajateckého tábora uprostřed neprostupné laoské džungle. Příběh tohoto amerického vojáka se stal předlohou pro film Temný úsvit, který natočil režisér Werner Herzog. Dieter (Christian Bale) již od dětství snil o tom, že se stane pilotem. Přání létat v oblacích se mu splnilo, když se dostal k vojenskému letectvu. Ale na své úplně první přísně tajné misi nad územím Laosu je jeho letoun sestřelen a on se ocitá v pasti džungle daleko od své jednotky. Brzy je zajat nechvalně proslulými laoskými vojáky a odveden do zajateckého tábora, kde nalézá další dva americké vojáky, kteří jsou tam násilím drženi už dva roky. Dengler se však s pozicí zajatce nehodlá smířit a ihned plánuje útěk. Vůbec nemá tušení, kde se nalézá, ale jedno ví jistě: Boj o svůj život nevzdá lehce.(oficiální text distributora)', NULL, 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2685, '201403/DENIK_BRIDGET_JONESOVE.m4v', 'Denik Bridget Jonesove', 6, 0, 'cz', NULL, 'en', 'Bridget Jones Diary', 'http://www.csfd.cz/film/18089-denik-bridget-jonesove/', 'DENIK_BRIDGET_JONESOVE.jpg', NULL, 'Bridget Jonesová je trochu kulatější "třicítka", která má ve svém životě zdánlivě jednoduchá a splnitelná přání - shodit nějaké to kilo a najít opravdový vztah. Jenže právě v roce, do něhož vstoupila s bezpočtem předsevzetí a rozhodnutím vést si deník, se to na jeho stránkách hemží pohromami. Především se zamilovala do svého pohledného šéfa, aby časem zjistila, že je jen "bokovka", protože "stálice" je nejen vychrtlá, ale navíc bohatá. Dala proto výpověď v nakladatelství, kde ji to celkem bavilo, a vrhla se střemhlav na kariéru televizní moderátorky, což zdaleka, jak zjišťuje, není profese jen o úsměvů a úspěchu. Její matka, ochotná ještě včera udělat vše pro blaho a sňatek své dcery, z ničeho nic dělá vše pro jakéhosi obstarožního seladona a tatínek tomu všemu s tragickým výrazem tiše přihlíží. K čemu dobře míněné rady přátel, když se Bridget cítí jako kulový blesk. Ještě že je, čas od času, po ruce lahvička oblíbeného Chardonnay a stále na dosah krabička cigaret. Jenže pak se v jejím životě stane něco, s čím skutečně vůbec nepočítala. Kdo by to také do toho nadutého Marka řekl... S postavou Bridget Jonesové se seznámili nejprve čtenáři britského deníku Independent (1995). Týden co týden si mohli přečíst vtipnou, suchým ostrovním humorem nahlíženou kapitolu z deníku mladé, svobodné ženy s nevídaným talentem na maléry a trapasy. O rok později vznikla knížka a její mimořádný úspěch rozhodl o cestě na plátno.(oficiální text distributora)', NULL, 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2686, '201403/DESPICABLE_ME_2.m4v', 'Ja, zloduch 2', 3, 33640, 'sk', '  ', 'en', 'Despicable Me 2', 'http://www.csfd.cz/film/300387-ja-padouch-2/', 'DESPICABLE_ME_2.jpg', 2, 'Může být napravený padouch vůbec ještě zábavný? Když vypráví pohádky na dobrou noc? Když se bojí o nevinnost své nejstarší dcery? Když už nechce vůbec nic ukrást? Gru naštěstí neskončí jako obyčejný civil, protože v jeho nitru se stále ukrývají schopnosti, které by leckdo rád využil. Jako například šéf mocné Antipadoušské ligy, Silas Velezadek (To příjmení nekomentujte, nemá to rád). Silas sice padouchy všeho druhu hluboce opovrhuje, ale kdo se může do myšlení zlosyna vcítit líp než zlosyn bývalý? A tak vyšle svou nejlepší agentku Lucy Wilde, aby Grua přiměla ke spolupráci. Té se to s pomocí paralyzéru převlečeného za rtěnku podaří a Gru začne na stará kolena páchat dobro. A brzy bude mít plné ruce práce, protože na radaru Antipadoušské ligy se objevil obzvlášť protřelý padouch Eduardo, nevysvětlitelně začali mizet Mimoňové, Margo se zapalují lýtka, Edith je i nadále životu nebezpečná a Agnes se všemožně snaží sehnat taťkovi nějakou mamku. A právě to může být pro Grua ve finále tou největší hrozbou.(oficiální text distributora)', NULL, 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2687, '201403/DIE_HARD.m4v', 'Smrtonosna pasca', 15, 51, 'cz', NULL, 'en', 'Die Hard', 'http://www.csfd.cz/film/6642-smrtonosna-past/', 'DIE_HARD.jpg', 10, 'Newyorský policista John McClane (Bruce Willis) přijel do Los Angles navštívit svou ženu, která je zrovna na vánočním večírku v nedostavěné budově firmy Nakatomi. Do budovy však vtrhne německý terorista Hans Gruber (Alan Rickman) se svou jednotkou a zajme všechny hosty (včetně McClaneovy ženy). Jen McClane unikne požárním schodištěm. Teroristé nechtějí propuštění několika svých členů, jak říkají, ale jejich hacker se pokouší dostat do sejfu firmy Nakatomi, ve kterém se nachází 640 milionů dolarů. McClane musí zasáhnout. Film byl nominován na několik technických Oscarů a odstartoval hvězdnou kariéru Bruce Willise.(oficiální text distributora)', NULL, 1988, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2688, '201403/KILLER_ELITE.m4v', 'Elitni zabijaci', 15, 0, 'cz', NULL, 'en', 'Killer Elite', 'http://www.csfd.cz/film/273881-elitni-zabijaci/', 'KILLER_ELITE.jpg', NULL, 'Pohádkově bohatému ománskému šejkovi zbývá posledních pár měsíců života. Než mu vyměřený čas vyprší, chce dostat na talíři hlavy mužů, kteří zabili jeho tři syny. Nabízí svá ropná pole, což je velmi lákavá nabídka, ovšem odpovídá nelehkému zadání. Obětmi šejkovy odplaty se mají stát příslušníci britských speciálních jednotek (SAS), elitní, všestranně vybavení vojáci, kteří jeho hochy připravili o život během války o Omán. Tuhle pracovní nabídku, ovšem bez konkrétních detailů, dostane Hunter (Robert De Niro), legenda mezi nájemnými zabijáky. Když po příletu do Ománu zjistí, že má zlikvidovat příslušníky SAS, nechce úkol vzít. S tím se tak trochu počítalo, protože Hunter má být jen návnadou ve složité hře, jejímž cílem je najmout na tenhle mimořádný úkol Dannyho (Jason Statham). Hunterův žák a přítel je ve svém oboru absolutní jednička, bohužel ho před časem pověsil na hřebík. Pokud práci vezme a zabije tři esa SAS, zůstane Hunter naživu. V opačném případě má smůlu. A protože Dannymu kdysi zachránil život, je jasné, co se bude dít. Kromě toho, že trénovaní vojáci nebudou snadnými terči, číhá nebezpečí i v mocné tajné organizaci, která je chrání, a oni pro ni za to vykonávají špinavou práci. V čele „bodyguardů“ příslušníků SAS stojí jejich bývalý člen Spike (Clive Owen), který chce kolegy ochránit za každou cenu. Danny a Spike jsou jako dvě nukleární hlavice letící proti sobě. Takovou akční explozi, k jaké dojde po jejich střetu, jste už dlouho v kině nezažili.(oficiální text distributora)', NULL, 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2689, '201403/MAMA.m4v', 'Mama', 2, 0, 'cz', NULL, 'en', 'MAMA', 'http://www.csfd.cz/film/307086-mama/', 'MAMA.jpg', NULL, 'Pět let Lucas (Nikolaj Coster-Waldau) vytrvale hledal malé neteře Lilly a Victorii, které po násilné matčině smrti zmizely i s otcem neznámo kam. Jeho pátrání, hraničící s posedlostí, narušuje i vztah s přítelkyní Annabel (Jessica Chastain), ale Lucas se nakonec dočká satisfakce. Obě dívky se najatým pátračům podaří najít v opuštěné chatě stojící uprostřed hlubokých lesů. Dívky jsou zubožené, sociálně velmi zaostalé, ale naživu, což si nikdo neumí vysvětlit. Protože jejich návrat do civilizace proběhne nečekaně rychle, může Lucas zažádat o jejich svěření do své péče a spolu s Annabel se jim snaží nahradit harmonickou rodinu. Postupně si ale oba začínají klást otázku, zda si s dívkami nepřivedli domů i někoho dalšího. Někoho, kdo Lilly a Victorii pomáhal celou tu dobu přežít a teď se jich nechce vzdát. Zvlášť Annabel nese narůstající dusno provázené znepokojivými jevy hůře, protože je zjevné, že si mateřskou lásku obou dívek bude muset vybojovat proti soupeři, který používá dost děsivé zbraně...(oficiální text distributora)', NULL, 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2690, '201403/OKRESNI_PREBOR.m4v', 'Okresni prebor - posledni zapas Pepika Hnatka', 9, 0, 'cz', NULL, NULL, '', 'http://www.csfd.cz/film/274728-okresni-prebor-posledni-zapas-pepika-hnatka/', 'OKRESNI_PREBOR.jpg', NULL, 'Film vychází z populárního televizního seriálu Okresní přebor. Jak sám název naznačuje, ve filmu se objeví legendami opředená postava trenéra s licencí - Josefa Hnátka. Protože v samotném seriálu se tento muž vyskytoval pouze jako popel rozprášený na houslickém hřišti, rozhodli se tvůrci, že ho divákům představí i ve své živoucí podobě. Příběh celovečerního filmu je tak situován do doby jeho života a rozehrává dilema muže, pro něhož je fotbal celý jeho život. Dokonce víc než život. Titulní roli houslické persóny ztvární divadelní režisér, umělecký šéf pražského Dejvického divadla a příležitostný herec Miroslav Krobot.(oficiální text distributora)', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2691, '201403/RAGING_BULL.m4v', 'Zurici byk', 15, 0, 'cz', NULL, 'en', 'Raging Bull', 'http://www.csfd.cz/film/8258-zurici-byk/', 'RAGING_BULL.jpg', NULL, 'Portrét boxera Jakea LaMottu, šampióna v strednej váhe, ktorý bol povestný svojou výdržou a odolnosťou v ringu. Film sleduje LaMottove osudy počas dvoch dekád od začiatkov jeho boxerskej kariéry v štyridsiatych rokoch. LaMotta sa preslávil agresívnym štýlom boja a nezlomnou zaťatosťou, vďaka ktorej neraz z posledných síl odolával ranám svojich súperov. Jeho prchký a výbušný temperament mu vyslúžil prezývku Zúriaci býk a doviedol ho nielen k boxerskym výhrám, ale aj k životným prehrám. LaMottu spaľovala žiarlivosť, kvôli ktorej stratil milovanú ženu Vickie i jediného priateľa a spojenca, svojho brata Joeyho. Po ukončení kariéry viedol bar, ale kvôli známosti s neplnoletou dievčinou sa dostal do väzenia a po prepustení skončil ako bavič v nočnom podniku. Režisér Martin Scorsese nakrútil film podľa LaMottovej vlastnej autobiografie. Z príbehu o vzostupe a páde boxera vyťažil materiál pre ilustráciu svojich obsedantných tém komplexu viny, sebadeštrukcie a problematického vzťahu muža k žen e. Zúriaceho býka LaMottu zobrazil ako muža neovládateľných vášní, ktorý si zúrivosť vybíja násilím na tých, ktorých najviac miluje, a vykúpenie za svoje viny následne hľadá v ranách utŕžených v ringu. Robert De Niro kvôli úlohe LaMottu podstúpil intenzívny tréning a neskôr pribral 20 kg. Svoju postavu obdaril nielen animálnou zúrivosťou, ale podarilo sa mu stvárniť aj beznádejnosť boja zúriaceho býka, ktorý musí ísť vždy hlavou proti múru. Za svoj herecký výkon De Niro získal Oscara. Film je povestný bravúrne stvárnenými scénami v ringu, zobrazujúcimi násilnú stránku tvrdého športu. Oscarom bola ocenená aj Scorseseho stála spolupracovníčka, strihačka Thelma Schoonmakerová. Film bol nominovaný na Ceny americkej filmovej akadémie vo viacerých kategóriách a hoci Scorsese cenu za réžiu nezískal, jeho Zúriaci býk je považovaný za najlepší film osemdesiatych rokov a roku 1990 bol zaradený do Národného filmového registra.(oficiální text distributora)', NULL, 1980, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2692, '201403/WRECK_IT_RALPH.m4v', 'Raubir Ralf', 3, 0, 'cz', NULL, 'en', 'Wreck it Ralph', 'http://www.csfd.cz/film/300893-raubir-ralf/', 'WRECK_IT_RALPH.jpg', NULL, 'Wreck-It Ralph touží po tom, aby byl milován tak, jako perfektní Good Guy jménem Fix-It Felix z jeho hry. Problém je v tom, že nikdo nemiluje Bad Guye. Ale oni milují hrdiny… takže když přichází moderní hra ve stylu kdo dřív vystřelí, kde se objeví neoblomná Sergeant Calhoun, Ralph to vidí jako příležitost k hrdinství a štěstí. Vplíží se do hry s jednoduchým plánem – vyhrát medaili – ale brzy zničí všechno a nechtěně vypustí smrtelného nepřítele, který ohrožuje každou hru v arkádě. Jediná Ralphova naděje? Vanellope von Schweetz, mladá problémistka z hry o závodění, která je tou jedinou, kdo může Ralpha naučit, co to znamená být Good Guy. Ale zjistí, že je natolik dobrý, aby se stal hrdinou před tím, než se objeví pro celou arkádu nápis „Game Over“?(oficiální text distributora)', NULL, 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (34080, '201503/S406 - The Laws of Gods and Men.m4v', 'The Laws of Gods and Men', 4, 1000, 'en', 'cz', 'en', 'The Laws of Gods and Men', '', 'S406 - The Laws of Gods and Men.jpg', 406, 'The Laws of Gods and Men: Stannis and Davos set sail with a new strategy. Dany meets with supplicants. Tyrion faces down his father in the throne room.', '2015-03-05 18:12:54.331', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2693, '201404/FROZEN.m4v', 'Ladove kralovstvo', 3, 0, 'sk', NULL, 'en', 'Frozen', 'http://www.csfd.cz/film/328862-ledove-kralovstvi/', 'FROZEN.jpg', 1, 'Nebojácná a věčně optimistická Anna se vydává na velkolepou výpravu v doprovodu drsného horala Kristoffa a jeho věrného soba Svena, aby nalezla svou sestru Elsu, jejíž ledová kouzla uvěznila království Arendelle do věčné zimy. Na své pouti se Anna a Kristoff setkávají s podmínkami, které si nezadají s nejvyššími velehorami světa, mýtickými trolly a legračním sněhulákem Olafem, a navzdory drsným živlům se zoufale pokoušejí dosáhnout svého cíle dříve, než bude zcela pozdě.(Falcon)', NULL, 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9970, '201405/S110.m4v', 'Fire and Blood', 4, 1000, 'cz', 'cz', 'en', '', '', 'S110.jpg', 110, 'Fire and Blood: As tragic news spreads across the Seven Kingdoms, Bran and Rickon share a prophetic dream, Catelyn interrogates Jaime about her sonʼs fall, and Robbʼs destiny is forever changed. After a surprising decision by his father, Tyrion heads south. Arya assumes a new identity in an attempt to escape Kingʼs Landing, and Sansa is terrorized by Joffrey. At the Wall, Jon is forced to choose between the Nightʼs Watch and the family he left behind. Across the sea, Dany pays a terrible price for her love, but finds new hope.', '2014-05-29 08:56:06.24', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (2694, '201404/HOBBIT_2_DESOLATION_OF_SMAUG.m4v', 'Smakova draci poust', 4, 34, 'cz', NULL, 'en', 'Desolation of Smaug', 'http://www.csfd.cz/film/238304-hobit-smakova-draci-poust/', 'HOBBIT_2_DESOLATION_OF_SMAUG.jpg', 2, 'Hobit: Šmakova dračí poušť líčí další dobrodružství hobita Bilbo Pytlíka na jeho společné pouti s čarodějem Gandalfem a třinácti trpaslíky vedenými Thorinem Pavézou. Vydali se na výpravnou cestu, jejímž cílem je získat zpět Osamělou horu a ztracené království trpaslíků Erebor. Poté, co přežili začátek své neočekávané cesty, pokračuje tato společnost směrem na Východ a cestou potkává kožoměnce Medděda a v lese Mirkwood plném nástrah se střetává s houfem obřích Pavouků. Když se jim podaří uniknout ze zajetí nebezpečných lesních elfů, pokračují trpaslíci k Jezernímu městu a nakonec k samotné Osamělé hoře, kde musí čelit největšímu nebezpečí v podobě Šmaka -  nejstrašlivější nestvůry, která prověří nejen jejich odvahu, ale i pevnost jejich přátelství a smysluplnost celé pouti.(Warner Bros. CZ)', NULL, 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9951, '201405/47_RONIN.m4v', '47 Roninov', 4, 0, 'cz', 'cz', 'en', '47 Ronin', 'http://www.csfd.cz/film/287719-47-roninu/', '47_RONIN.jpg', NULL, 'Rónin je samuraj, který ztratil svého pána a tím i svou čest, a jeho postavení na společenském žebříčku středověkého Japonska tomu odpovídá. Ještě mnohem níž však stojí lidé smíšené rasy, mezi něž patří i nalezenec Kai (Keanu Reeves), zčásti Brit, zčásti Japonec. Díky svým mimořádným schopnostem je vzat na milost skupinou bývalých samurajů, jejichž pán se stal obětí spiknutí, které připravili zákeřný Lord Kira a čarodějka Mizuki. Róninové chtějí Lorda Kiru porazit za každou cenu, jenže trumfy rozhodně nejsou na jejich straně. Dokud nezískají Kaie. I tak ale bude cesta za spravedlivou odplatou trnitá a jen málokdo z nich po ní dojde až do konce, který navíc může mít v souladu s japonskými tradicemi velmi trpkou příchuť.(CinemArt)', '2014-05-29 08:56:04.792', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9952, '201405/DIE_2_HARDER.m4v', 'Smrtonosna pasca 2', 15, 51, 'cz', 'cz', 'en', 'Die Harder', 'http://www.csfd.cz/film/4158-smrtonosna-past-2/', 'DIE_2_HARDER.jpg', 20, 'S policistou Johnem McClanem se setkáváme i v druhém pokračování úspěšného akčního thrilleru, tentokrát v režii Rennyho Harlina. Přesně rok po dramatické misi v Los Angeles John plánuje klidné vánoční svátky. Na Štědrý den jede na newyorské letiště vyzvednout manželku Holly, jež má přiletět z Los Angeles, ale letiště padne do rukou po zuby ozbrojených a perfektně vycvičených teroristů. A když jde o život jeho rodině i stovkám ostatních pasažérů, kteří se stali rukojmími, musí John znovu sáhnout po zbrani. V hlavní roli opět exceluje Bruce Willis.(oficiální text distributora)', '2014-05-29 08:56:04.94', 1990, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9953, '201405/DIE_3_HARD_WITH_A_VENGEANCE.m4v', 'Smrtonosna pasca 3', 15, 51, 'cz', 'cz', 'en', 'Die Hard with a Vengeance', 'http://www.csfd.cz/film/6643-smrtonosna-past-3/', 'DIE_3_HARD_WITH_A_VENGEANCE.jpg', 30, 'Bruce Willis coby policista John McClane se vrací a tentokrát se utká v souboji s nebezpečným teroristou, který chce ochromit New York a dostat jeho hlavu. Je totiž bratrem zločince Grubera, jehož McClane sprovodil ze světa ve Smrtonosné pasti 1. Do třetice se setkáváme s policistou Johnem McClanem, tentokrát v New Yorku. Žena ho opustila, kvůli pití se ocitl ve výslužbě a kolegové si z něho spíš utahují, než aby si ho vážili. Ale to se záhy změní, protože terorista, který vyhazuje do vzduchu budovy velkoměsta, chce Johnovu hlavu. Nastraží po celé metropoli bomby a John je donucen plnit jeho úkoly, které se blíží sebevraždě. A protože mu jde opravdu o krk, probudí se v něm jeho zasuté schopnosti, zvlášť když zjistí, že jeho nepřítelem je Simon Gruber (Jeremy Irons), bratr Hanse Grubera, jehož McClane sprovodil ze světa v jedničce. Spojence nečekaně získává v Zeovi (Samuel L. Jackson), majiteli obchůdku v Harlemu. Pomsta však je jen součástí Simonova plánu, v němž jde ve skutečnosti hlavně o zlato ... Režisérem třetího pokračování je opět John McTiernan a v hlavní roli se vrací charismatický Bruce Willis.(oficiální text distributora)', '2014-05-29 08:56:05.01', 1995, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9954, '201405/DIE_4_HARD_LIVE_FREE_OR_DIE_HARD.m4v', 'Smrtonosna pasca 4', 15, 51, 'cz', 'cz', 'en', 'Live Free or Die Hard', 'http://www.csfd.cz/film/71041-smrtonosna-past-4-0/', 'DIE_4_HARD_LIVE_FREE_OR_DIE_HARD.jpg', 40, 'Amerika je při oslavách Dne nezávislosti napadena velmi rafinovaným útočníkem. Ten využívá nejmodernější technologie, aby napáchal co největší škody na americké infrastruktuře a uvrhl zemi do chaosu. Jeho promyšlený plán však má jednu trhlinu. Nepočítá s tím, že existuje John McClane (Bruce Willis), který je v proklatě dobré formě. Čtvrté pokračování mimořádně úspěšné série s Brucem Willisem jako nekompromisním protivníkem teroristů všeho druhu a všech národností, slibuje klasickou akční podívanou. V záplavě všech nejmodernějších trikových filmů tak nabízí nejen hrdinu ze staré školy, ale i klasické automobilové honičky, detonace, pořádného záporného hrdinu a střet postmoderního "počítačového" světa se zdravým selským rozumem.(oficiální text distributora)', '2014-05-29 08:56:05.096', 2007, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9955, '201405/DIE_5_A_GOOD_DAY_TO_DIE_HARD.m4v', 'Smrtonosna pasca 5', 15, 51, 'cz', 'cz', 'en', 'A Good Day To Die Hard', 'http://www.imdb.com/title/tt1606378/?ref_=fn_al_tt_1', 'DIE_5_A_GOOD_DAY_TO_DIE_HARD.jpg', 50, 'Directed by John Moore.  With Bruce Willis, Jai Courtney, Sebastian Koch, Mary Elizabeth Winstead. John McClane travels to Russia to help out his seemingly wayward son, Jack, only to discover that Jack is a CIA operative working undercover, causing the father and son to team up against underworld forces.', '2014-05-29 08:56:05.165', 2013, '');
