INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9956, '201405/ENDERS_GAME.m4v', 'Enderova Hra', 4, 0, 'cz', 'cz', 'en', 'Ender''s Game', 'http://www.csfd.cz/film/306861-enderova-hra/', 'ENDERS_GAME.jpg', NULL, 'Země přežila devastující mimozemský útok, je zničená a je jasné, že příští invaze bude totální a její výsledek definitivní. Lidstvo se proto rozhodne vycvičit novou generaci geniálních dětí na válečníky a vojevůdce, kteří mají misky vah v příští nevyhnutelné bitvě naklonit na naši stranu. Ti nejlepší a nejnadanější jsou vybráni a posláni do Válečné školy na orbitální stanici v kosmu, kde musí podstoupit mnohaletý výcvik a nekonečnou soutěž o to, kdo se nakonec stane hlavním velitelem mezinárodních sil. V atmosféře násilí a neustálého srovnávání cvičí na vojenských simulátorech stejně jako ve vzájemných válečných hrách. Nejefektnějším je takzvaný "Battle room", kde se ve stavu beztíže tříbí strategické i fyzické vlastnosti a kde díky kombinaci inteligence a empatie vyniká právě Ender. Jako hlavní favorit se pak Ender přesouvá do velitelské školy a vede ostatní do boje v simulované válce. Ta opravdová válka se ale blíží rychleji, než se zdálo, a teprve  –náctiletý Ender se stává jediným velitelem mezinárodní flotily v boji o záchranu lidstva. A jeho inteligence ho nutí pochybovat... je tohle ta pravá cesta?(Bontonfilm CZ)', '2014-05-29 08:56:05.235', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9957, '201405/HON_NA_PONORKU.m4v', 'Honba na ponorku', 15, 0, 'cz', 'cz', 'en', 'The Hunt for Red October', 'http://www.csfd.cz/film/6644-hon-na-ponorku/', 'HON_NA_PONORKU.jpg', NULL, 'Film režiséra Johna McTiernana, natočený podle bestselleru Toma Clancyho, je příběhem mužů, v jejichž rukou leží odpovědnost za soudný den. Zbrusu nová, supermoderní jaderná sovětská ponorka míří k pobřeží USA. Jejím velitelem je kapitán Marko Ramius (Sean Connery). Americká vláda se obává, že ponorka "Rudý říjen" zaútočí. Analytik CIA (Alec Baldwin) je jiného názoru. Myslí si, že kapitán Ramius chce emigrovat. Má však jen několik hodin, aby ho našel a svůj názor dokázal. Sovětské námořní a vzdušné síly po ponorce totiž pátrají také. Hon na ponorku začíná.(oficiální text distributora)', '2014-05-29 08:56:05.305', 1990, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9958, '201405/JURASSIC_PARK.m4v', 'Jursky park', 15, 0, 'cz', 'cz', 'en', 'Jurrasic Park', 'http://www.csfd.cz/film/8644-jursky-park/', 'JURASSIC_PARK.jpg', NULL, 'Po fantastickém nálezu miliony let starých D.N.A. se pokusí skupina ambiciózních vědců vytvořit přírodní park s žijícími prehistorickými zvířaty na vzdáleném liduprázdném ostrově. Celý projekt podporuje milionář John Hammond (Richard Attenborough), který těsně před otevřením Jurského parku pro veřejnost pozve na jeho návštěvu svá dvě vnoučata, známého paleontologa s jeho přítelkyní a další odborníky. Průběh jejich klidné návštěvy se rychle změní v noční můru poté, co jeden ze zaměstnanců v naději na tučný zisk od konkurence ukradne embrya dinosaurů. Aby snadno unikl z parku, vypne bezpečnostní systémy a tím i elektřinou napájený plot ohrazující celý Jurský park. Dinosauři se dostávají do volné přírody a souboj lidí s druhohorními obludami začíná.(oficiální text distributora)', '2014-05-29 08:56:05.375', 1993, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9959, '201405/Lone_Ranger.m4v', 'Osamely jezdec', 15, 0, 'cz', 'cz', 'en', 'Lone Ranger', 'http://www.csfd.cz/film/248295-osamely-jezdec/', 'Lone_Ranger.jpg', NULL, 'Indiánský bojovník Tonto (Johnny Depp) se dělí o dosud nevyřčené příběhy, které způsobily proměnu Johna Reida (Armie Hammer), muže zákona, v legendu spravedlnosti – společně s diváky se vydává na divokou jízdu plnou velkolepých překvapení a humorných neshod, které musejí oba rozpačití hrdinové překonat, aby dokázali společně bok po boku bojovat s lidskou chamtivostí a korupcí.(oficiální text distributora)', '2014-05-29 08:56:05.445', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9960, '201405/OBLIVION.m4v', 'Nevedomi', 4, 0, 'cz', 'cz', 'en', 'Oblivion', 'http://www.csfd.cz/film/282846-nevedomi/', 'OBLIVION.jpg', NULL, 'Jacka (Tom Cruise), kterého na Zemi baví především hledání artefaktů zanikající lidské civilizace, vyvedou z každodenní rutiny dvě události. Tou první je nouzové přistání vesmírné lodi, jejíž jediná členka posádky (Olga Kurylenko) tvrdí, že ji s Jackem pojí společná minulost, aniž pro to existuje racionální vysvětlení. Následně je Jack zajat ilegální armádou operující pod zemským povrchem, jejíž charismatický vůdce Beech (Morgan Freeman) toho o něm ví víc než on sám. Pod vlivem těchto událostí se Jack rozhodne podívat na svět kolem sebe jinýma očima a z toho, co uvidí, mu není dvakrát dobře…(oficiální text distributora)', '2014-05-29 08:56:05.514', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9961, '201405/S101.m4v', 'Winter Is Coming', 4, 1000, 'cz', 'cz', 'en', '', '', 'S101.jpg', 101, 'Winter Is Coming: King Robert Baratheon majestically arrives in Winterfell, the home of his old and trusted friend, Eddard Stark, Warden of the North, with an important offer. On the eastern continent, the dispossessed Princess Daenerys Targaryen marries Khal Drogo, a warlord of the Dothraki with tens of thousands of warriors at his command. Her brother, Viserys, callously plans to win Drogo''s allegiance with the marriage, so that he may return home to Westeros and reclaim the Iron Throne, which was seized by force from his father by Robert. In the frozen lands, beyond the Wall, the wildlings are on the move to the alarm of the Night''s Watch. But something else is stirring even further north.', '2014-05-29 08:56:05.583', NULL, NULL);
