INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (9991, '201405/UP.m4v', 'Hore', 3, 0, 'cz', 'cz', 'en', 'Up', 'http://www.csfd.cz/film/236791-vzhuru-do-oblak/', 'UP.jpg', NULL, 'Vzhůru do oblak je příběh o 78letém prodejci balónků Carlovi Fredricksenovi, který si konečně splní celoživotní sen o dobrodružstvích, když na svůj domek přiváže tisíce balónků a odletí s ním do divočiny Jižní Ameriky. Až příliš pozdě ale zjistí, že se k němu potají připojil i původce jeho nejhorších nočních můr: nadšený osmiletý průzkumník divočiny jménem Russell. Jejich společná pouť do ztraceného světa, v němž se setkávají s všemožnými zvláštními, exotickými a překvapivými postavami, je plná humoru, emocí a obrovsky nápaditých příhod. Současně se jedná o výroční desátý film studia Pixar.(oficiální text distributora)', '2014-05-29 08:56:07.754', 2009, '');
