INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20000, '201406/ANGEL_HEART.m4v', 'Angel Heart', 2, 0, 'cz', NULL, 'en', 'Angel Heart', 'http://www.csfd.cz/film/7109-angel-heart/', 'ANGEL_HEART.jpg', NULL, 'Amerika roku 1955, jazz a místa v autobusech vyhrazená jen pro bílé. Soukromé očko Harry Angel (Mickey Rourke) dostane neobvyklý kšeft - pro podivínského právníka jménem Louis Cyphre vypátrat jistého Johnyho Favoritea, kterého 12 let nikdo neviděl. Angel se vydává za stopou a místo odpovědí nachází jen další otázky. Poté, co kolem něj začnou umírat svědkové, Angel pochopí, že tohle není jen tak lecjaká práce a že mu začíná jít o krk...(ČSFD.cz)', '2014-06-23 20:56:32.238', 1987, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20001, '201406/Dar.m4v', 'Dar', 15, 0, 'cz', NULL, 'en', 'The Greatest', 'http://www.csfd.cz/film/254210-dar/', 'Dar.jpg', NULL, 'Poklidný předměstský život dobře situované rodiny Brewerových nečekaně rozvrátí šokující smrt staršího syna Bennetta. Otec, matka i mladší bratr se s náhlou ztrátou blízké osoby snaží vyrovnat každý po svém, dobrovolnou izolovanost jejich paralelních světů však „naruší“ Bennettova nová přítelkyně. Je pevně rozhodnuta dozvědět se více o mladíkovi, se kterým ji pojila krátká, ale intenzivní romance. Debutující Shana Festeová natočila nečekaně vyzrálou studii o vyrovnávání se s bolestí, aniž by sklouzla k povrchnímu sentimentu. Její intenzivní psychologický vhled pod zdánlivě idylický povrch středostavovské rodiny byl inspirován dramatem Roberta Redforda Obyčejní lidé. Skvělým Susan Sarandonové a Pierci Brosnanovi v rolích rodičů se svým hereckým výkonem přinejmenším vyrovná jedna z nejtalentovanějších hereček současnosti, Britka Carey Mulliganová. [44. MFFKV 2009](oficiální text distributora)', '2014-06-23 20:56:32.429', 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20002, '201406/ELYSIUM.m4v', 'Elysium', 4, 0, 'cz', 'cz', 'en', 'Elysium', 'http://www.csfd.cz/film/288738-elysium/', 'ELYSIUM.jpg', NULL, 'V roce 2154 existují dva druhy lidí: velmi bohatí, kteří žijí na nedotčené umělé kosmické stanici s názvem Elysium, a zbytek živoří na přelidněné zničené Zemi. Tajemnice Delacourt (Jodie Foster) je ochotna udělat cokoli, aby pro obyvatele Elysia zůstal zachován jejich luxusní životní styl. A naopak lidé na Zemi jsou ochotni udělat cokoli, aby se jim povedlo získat své místo na Elysiu. Max (Matt Damon) se zapojí do nebezpečné operace, která by mohla přinést rovnost dvou polarizovaných světů.(oficiální text distributora)', '2014-06-23 20:56:32.498', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20003, '201406/EPIC.m4v', 'Kralovstvo lesnych strazcov', 3, 0, 'sk', 'en', NULL, 'Epic', 'http://www.csfd.cz/film/300894-kralovstvi-lesnich-strazcu/', 'EPIC.jpg', NULL, 'Bystrá, statečná a tvrdohlavá dívka, taková je sedmnáctiletá Mary, která prožije dobrodružství, o jakém se jí nezdálo ani v nejdivočejším snu! Po návratu do rodného domu se snaží sblížit se svým otcem Bombou, který se jí odcizil. Svérázný a roztržitý profesor Bomba je posedlý zkoumáním pozoruhodné civilizace drobných lesních mužíčků žijících v lesích. Mary dochází trpělivost. Poté, co se kouzlem ocitne v tomto podivném lesním společenství, je ovšem nucena svůj pohled přehodnotit. Pokud se chce dostat zpátky domů, musí partičce zábavných zelených postaviček pomoci, uvěřit v jejich tajuplný svět a zachránit ho. Boj o záchranu lesa se však promění v boj o náš lidský svět. Díky odvaze, kterou Mary prokáže, získá nejen nazpět svého otce Bombu, ale i nové přátele. Královnu lesa - krásnou, bystrou a silnou Taru, která vždy panuje s úctou a vtipem. Obří housenku Nim Galů, známou svou vášní pro večírky, které rovněž nechybí moudrost a praktičnost. Komediální duo hlemýždě a slimáka Hňupa a Hruba, přičemž Hrub se považuje za největšího lamače dámských srdcí v lese. Ostříleného bojovníka Ronina, šéfa elitní lesní jednotky, muže s ostře řezanou tváří a suchým humorem, ochotného za svou královnu položit i život.(oficiální text distributora)', '2014-06-23 20:56:32.601', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20004, '201406/Ghostbusters.m4v', 'Krotitelia duchov', 4, 0, 'cz', NULL, NULL, 'Ghostbusters', 'http://www.csfd.cz/film/10360-krotitele-duchu/', 'Ghostbusters.jpg', NULL, 'Hejna malých zelených duchů, strašidla a paranormální jevy se začínají objevovat v New Yorku. Kdo může ochránit lidi před těmito nadpřirozenými bytostmi? Pouze tři nezaměstnaní parapsychologové. Dávají se dohromady a vytvářejí tým lovců duchů. Vybaveni ektoplazmovými kanóny, pastmi na duchy a speciálním vozidlem bojují proti přesile slizských oblud.(oficiální text distributora)', '2014-06-23 20:56:32.713', 1984, '');
