INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20006, '201406/Jak dostat tatinka do polepsovny.m4v', 'Jak dostat tatinka do polepsovny', 3, 0, 'cz', NULL, 'cz', 'Jak dostat tatinka do polepsovny', 'http://www.csfd.cz/film/33318-jak-dostat-tatinka-do-polepsovny/', 'Jak dostat tatinka do polepsovny.jpg', NULL, 'Při premiéře pokračování své dnes už proslavené komedie o malém Vaškovi a jeho rodičích Jak vytrhnout velrybě stoličku autorka a režisérka Marie Poledňáková uvedla: "Druhá část, respektive volné pokračování, byla napsána vlastně před uvedením hry Jak vytrhnout velrybě stoličku. Takže podnětem nebyl úspěch díla, ale skutečnost, že v jeho závěrečných scénách vznikla velice zajímavá situace. Vystihli to i herci a v posledním období natáčení si sami vymýšleli scény, domýšleli osudy svých postav; k fabulování se přidal postupně celý štáb. A ústřední myšlenka? Na první pohled by se mohlo zdát, že jde o příběh lehký, úsměvný. Jestliže se však zamyslíme hlouběji nad problémem neúplné rodiny i jejího znovuvybudování, vytěžíme asi víc zajímavých poznatků. Luboš a Anna, Vaškovi rodiče, se ocitají ve složité situaci. Jeden druhého se snaží pochopit, respektovat rozdílnost povah - zpočátku hlavně kvůli dítěti. A není lehká ta cesta, na níž se má znovu upevnit vztah už jednou narušený. Situace, která nastala, je dost kuriózní: dva lidé, muž a žena, se po osmi letech opět setkávají, znovu se poznávají. Tento problém mě tedy zaujal - a také dětský svět, svět s jeho sny, touhami, svět, který se prostě nechce smířit s něčím, co se neshoduje s jeho představami o dobru a spravedlnosti." Skvělá letní komedie, v níž, jak říká autorka, opravdu nejde jen o humor, byla natočena před více než dvaceti lety. Přesto nás zaručeně bude bavit i nadále.(oficiální text distributora)', '2014-06-23 20:56:32.919', 1978, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (21028, '201407/HAPPY_FEET.m4v', 'Happy Feet', 3, 0, 'cz', 'cz', 'en', '', 'http://www.csfd.cz/film/135439-happy-feet/', 'HAPPY_FEET.jpg', NULL, 'Pokud nedovedete zpívat, jste mezi tučňáky z arktických plání nikým. Tučňák Brumla je bohužel tím nejhorším zpěvákem na celém širém světě. Co mu však chybí tuhle, nechybí mu tamhle. Je totiž skvělý tanečník, ba co víc, je blázen do stepu. Brumla je prostě natolik jiný, že jej přísný vládce národa tučňáků, stařešina Noah, nadobro vyloučí z komunity. Náš hrdina se poprvé v životě dostane mimo domov a v dálavách se setkává s věru podivnými brachy, kteří sice tučňáky bezesporu jsou, ale vůbec tak nevypadají - Adelie Amigos pod vedením zkušeného Ramona. Brumlovy “správňácké” pohyby se jim zalíbí natolik, že mu nabídnou spolupráci. V zemi Adeliů se Brumla společně s guru Lovelacem a partou Amigos vydává přes širé ledové pustiny vstříc mnoha dobrodružstvím, aby při nich zjistil, že svým zvláštním darem může změnit svět o kousek k lepšímu.', '2014-07-24 03:10:12.378', 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20007, '201406/Jak se kroti krokodyli.m4v', 'Jak se kroti krokodyli', 3, 0, 'cz', NULL, 'cz', 'Jak se kroti krokodyli', 'http://www.csfd.cz/film/194894-jak-se-kroti-krokodyli/', 'Jak se kroti krokodyli.jpg', NULL, 'Tatínek Luboš, který pracuje v Alpách jako záchranář, přijíždí domů do Prahy mnohem dřív, než původně slíbil. Jeho osmiletá dcera Amálka je nadšena, a tak odmítne jet na školu v přírodě, aby si užila tatínka. Luboš se vypraví do školy, aby Amálku omluvil, ale dopadne to tak, že nakonec jede do Vysokých Tater s celou třídou jako zdravotní doprovod. Své manželce Anně (řeší právě odchod do důchodu a ztrátu milované práce u baletu) slíbí, že je to jen na pár dní, ať tedy odjede na chalupu, kam za ní určitě přijede ... Řetězec náhod a nedorozumění tomu však zabrání - hodně tomu napomůže Amálka, ale také svérázný děda Pepík a syn Vašek se svou dobrodružnou povahou, do hry vstupuje i atraktivní učitelka Alice, originální rodina Koudelkových a jejich syn Vítek, dědova sousedka Bobulová, vesnická zrzavá kráska Julča, profesorka Kája se svým smyslem pro matematiku i pro východní meditaci...(oficiální text distributora)', '2014-06-23 20:56:33.021', 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20008, '201406/Jak vytrhnout velrybe stolicku (1).m4v', 'Jak vytrhnout velrybe stolicku (1)', 3, 0, 'cz', NULL, 'cz', 'Jak vytrhnout velrybe stolicku', 'http://www.csfd.cz/film/33317-jak-vytrhnout-velrybe-stolicku/', 'Jak vytrhnout velrybe stolicku (1).jpg', NULL, 'Nadmíru čilý a podnikavý školák Vašek žije sám s maminkou, ale ta se po večerech víc než jemu věnuje baletu. "Všichni kluci mají tátu, jenom já nemám ani psa!" stěžuje si Vašek, a tak se mamince snaží nějakého nového manžela (a sobě tatínka) "dohodit"... Na rekreaci při tom narazí na sympatického Luboše... Mimořádně úspěšná rodinná komedie Marie Poledňákové stojí i poněkolikáté za zhlednutí nejen díky zajímavé zápletce a vtipným dialogům, ale především pro skvělé herecké výkony Jany Preissové, Františka Němce a především neodolatelného Tomáše Holého.(oficiální text distributora)', '2014-06-23 20:56:33.091', 1977, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20009, '201406/KRALOVA_REC.m4v', 'Kralova rec', 15, 0, 'cz', 'cz', 'en', 'King speech', 'http://www.csfd.cz/film/269587-kralova-rec/', 'KRALOVA_REC.jpg', 1, 'Po smrti otce, anglického krále Jiřího V. (Michael Gambon) a skandální abdikaci krále Eduarda VIII. (Guy Pearce), je Bertie (Colin Firth), který celý život trpí vadou řeči, nečekaně korunovaný králem Jiřím VI. Země stojí na prahu 2. světové války a zoufale potřebuje silného vůdce. Proto králova žena Alžběta (Helena Bonham Carter), budoucí královna matka, domluví svému muži setkání s excentrickým terapeutem řeči Lionelem Loguem (Geoffrey Rush). Po tuhém začátku se tito dva ponoří do neobvyklého způsobu léčby a časem mezi nimi vznikne nenarušitelné pouto. S pomocí Loguea, rodiny, vlády a Winstona Churchilla (Timothy Spall) král překoná koktání a pronese zásadní řeč v rádiu, která inspiruje anglický lid a spojí jej ve válečné bitvě.(oficiální text distributora)', '2014-06-23 20:56:33.185', 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20010, '201406/Levi kral.m4v', 'Levi kral', 3, 0, 'sk', 'cz', 'en', 'The Lion King', 'http://www.csfd.cz/film/6741-lvi-kral/', 'Levi kral.jpg', 1, 'Zdivočelé stádo hnané na Scarův příkaz potměšilými hyenami by bezbranného Simbu určitě zadupalo do země, kdyby jej nezachránil jeho otec Mufasa. Při zoufalé záchraně však zahyne. Protřelému Scarovi pak nedá moc práce přesvědčit vyděšeného Simbu, že je zodpovědný za otcovu smrt a měl by neodkladně uprchnout! A tak se Scar se svojí suitou konečně dostane k moci - zatímco Simba si najde nové zvířecí přátele Pumbu a Timona, s nimiž v duchu bezstarostné filozofie Hakuna Matata vyroste do majestátní lví podoby. Ale země jeho otce strádá, královská krev se nezapře a kdesi na hvězdné obloze si Mufasa hluboce oddychl, když probuzený Simba razantně vyrazil v jeho královské stopě...(oficiální text distributora)', '2014-06-23 20:56:33.297', 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20018, '201406/THE_LEGO_MOVIE.m4v', 'Lego Pribeh', 3, 0, 'cz', NULL, 'en', 'The Lego Movie', 'http://www.csfd.cz/film/321771-lego-pribeh/', 'THE_LEGO_MOVIE.jpg', NULL, 'Příběh 3D filmu, natočeného pomocí počítačové animace podle původního scénáře, sleduje osudy Emmeta, obyčejné, pravidel dbalé a dokonale průměrné mini-figurky LEGO, která je omylem považována za nejvýjimečnější osobu, která jediná může zachránit svět. Je odveden do společenstva cizinců, kteří se vydali na velkolepou výpravu proti zlému tyranovi. Emmet je na takovou cestu až k popukání zoufale nepřipraven.(Warner Bros. CZ)', '2014-06-23 20:56:33.879', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20011, '201406/MAPA MEHO SVETA.m4v', 'Mapa meho sveta', 15, 0, 'cz', NULL, 'en', 'A map of the world', 'http://www.csfd.cz/film/17686-mapa-meho-sveta/', 'MAPA MEHO SVETA.jpg', 1, 'Stačí okamžik... a život se změní k nepoznání. Školní zdravotní sestra Alice (Sigourney Weaver) žije na farmě v malé obci Wisconsinu. Má dvě krásné holčičky, milujícího manžela a nejlepší kamarádku Terezu (Julianne Moore). Jako již po několikáté Alice slíbí, že vezme Tereziny děti společně se svými k vodě. Jedno nepatrné zaváhání, jeden letmý pohled jinam... a celý život se rázem změní. Lizza, dcera Terezy, leží nehybně na hladině jezera a nejeví známky života. Přátelství mezi Alicí a Terezou je náhle vystaveno té nejtěžší zkoušce, která je plná bolesti, nenávisti, zklamání a provinění.(oficiální text distributora)', '2014-06-23 20:56:33.366', 1999, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20012, '201406/MATERSKE_GALEJE.m4v', 'Materske galeje', 9, 0, 'cz', NULL, 'en', 'Motherhood', 'http://www.csfd.cz/film/242849-materske-galeje/', 'MATERSKE_GALEJE.jpg', 1, 'Film Mateřské galeje přináší příběh Elizy Welch (Uma Thurman) - bývalé spisovatelky, která nyní veškerou energii věnuje výchově svých dvou dětí a internetovému blogu o rodičovství. Eliza má dobrosrdečného, ale duší nepřítomného manžela (Anthony Edwards), který se příliš nestará ani o její trápení, ani o rodinu jako takovou, zatímco její nejlepší kamarádka Sheila (Minnie Driver) jí ve všem rozumí až příliš dobře. Nadcházející den Elize nachystal další těžké zkoušky z rodičovství. Aby v nich obstála, musí si uvědomit, co je ve jejím životě skutečně důležité. Navíc se rozhodla napsat příspěvek do soutěže časopisu pro rodiče na zdánlivě jednoduché téma: “Co pro mne znamená mateřství?”(oficiální text distributora)', '2014-06-23 20:56:33.435', 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (21029, '201407/Milujte sveho zabijaka.m4v', 'Milujte sveho zabijaka', 15, 0, '  ', '  ', '  ', '', 'http://www.csfd.cz/film/1602-milujte-sveho-zabijaka/', 'Milujte sveho zabijaka.jpg', NULL, 'NByla to televize, která ho vynesla na vrchol popularity, ale jeho největší role se odehrávaly mimo zákulisí studií a přenosových vozů. Příběh legendárního amerického showmana, který žil dvěma životy. Přes den pracoval jako televizní scénárista a producent, v noci jako úkladný vrah ve službách CIA.', '2014-07-24 03:10:12.447', 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (21030, '201407/TURBO.m4v', 'Turbo', 3, 0, 'sk', '  ', '  ', 'Turbo', 'http://www.csfd.cz/film/313491-turbo/', 'TURBO.jpg', NULL, 'Turbo je obyčejný malý šnek, který má velký sen. Žít život hlemýždím tempem ho neuspokojuje, touží po mnohem vyšší rychlosti. Neustále trénuje, své pokroky měří metrem. Jeden metr dokáže aktuálně „uběhnout" za sedmnáct minut. Kvůli tomu příliš nezapadá do hlemýždího společenství. Mezi hlemýždi se totiž nenosí velké sny a velká rychlost, důležitějším ukazatelem jsou odpracované hodiny na hlavním zdroji obživy, tedy na „rodném" rajčatovém stromku. Turbův bratr Čet, jehož krédem je „Bezpečnost na prvním, posledním i všech ostatních místech", má svého bratra rád, ale také má obavy, že Turbova posedlost rychlostí ho jednou přivede do neštěstí. Jeho nejhorší obavy se naplní ve chvíli, kdy Turbo opustí rodné rajče a vydá se do světa, aby si splnil své sny o vyšší rychlosti. NFatální střet se speciálně upraveným motorem nadupaného sportovního auta změní jeho molekulární strukturu a díky této podivné nehodě získá Turbo superschopnost: Neuvěřitelnou rychlost. V tu chvíli se přestává pohybovat šnečím tempem a jako neónová střela si to drandí ulicemi Los Angeles někdy i ve třísetkilometrové rychlosti. Ale i když je Turbo nabitý turbo rychlostí, nedokáže uprchnout Titovi, spolumajiteli kalifornského stánku s rychlým občerstvením, jehož koníčkem a velkou vášní je organizace šnečích závodů v nákupním centru Starlight Plaza. Tito záhy pozná Turbův potenciál a zařadí ho do své stáje, mezi „šnečí jezdce" s ulitami, které připomínají závodní mini autíčka. Tito drsní hlemýždí závodníci se s Turbem rychle spřátelí a společně se vydají na cestu plnou dobrodružství, protože rychlíka Turba pouhé šnečí závody rozhodně neuspokojí. Na cestě za snem si uvědomí, že těžko uspěje sám bez pomoci druhých a že když pomůže splnit malé sny svých kamarádů, pomohou mu na oplátku splnit ten jeho, velký – vítězství ve slavném závodu Indy 500, kde budou jeho soupeři opravdová auta.', '2014-07-24 03:10:12.517', 2013, '');
