INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20013, '201406/MUZI_V_NADEJI.m4v', 'Muzi v nadeji', 9, 0, 'cz', NULL, 'cz', '', 'http://www.csfd.cz/film/276933-muzi-v-nadeji/', 'MUZI_V_NADEJI.jpg', 1, 'Může být nevěra základem šťastného manželství? Šarmantní bonviván Rudolf je o tom přesvědčen: „Ženská má mít pocit, že o chlapa musí bojovat, musí se snažit, aby si ho udržela. A hlavně, ženská se nesmí nudit!” Rudolf s úspěchem uplatňuje svou divokou teorii v každodenní praxi a to s neutuchajícím elánem, který je u čerstvého šedesátníka záviděníhodný. O to víc pak nechápe naivitu svého nesnesitelně korektního zetě Ondřeje, který pro samé svědomité plnění povinností nevnímá, jak nebezpečně se jeho žena Alice začíná nudit. Milující tatínek a kamarádský tchán Rudolf proto nabídne Ondřejovi několik prověřených rad, jak vnést do skomírajícího manželství potřebný vzruch. Koneckonců jde o rodinné štěstí jeho jediné dcery... Z nudného slušňáka Ondřeje se pak díky románku s krásnou Šarlotou skutečně stává sebevědomý pán tvorstva, obletovaný milenkou a obdivovaný vlastní manželkou. Situace je to sice krásná, ale dlouhodobě neudržitelná. Už proto, že skutečnými pány tvorstva jsou pochopitelně ženy. Ondřeje čeká smršť komediálních situací, kterým čelí s odzbrojující kombinací typicky mužských vlastností - hravou vynalézavostí a nezničitelnou nadějí, že všechno dobře dopadne.(oficiální text distributora)', '2014-06-23 20:56:33.507', 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20014, '201406/Nocni volani.m4v', 'Nocni volani', 15, 0, 'cz', NULL, 'en', 'Night Listener', 'http://www.csfd.cz/film/223258-nocni-volani/', 'Nocni volani.jpg', 1, 'Gabriel (R. Williams) moderuje oblíbený noční rozhlasový pořad. Jednou jej kontaktuje těžce nemocný chlapec Pete (R. Culkin) . Po mnoha rozhovorech se Gabriel rozhodne zjistit pravou Peteovu identitu. Jeho telefonní číslo ani adresa však nejsou pravé a obklopují ho samá znepokojivá tajemství..(oficiální text distributora)', '2014-06-23 20:56:33.576', 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20015, '201406/Notting Hill.m4v', 'Notting Hill', 6, 0, 'cz', NULL, 'en', 'Notting Hill', 'http://www.csfd.cz/film/17806-notting-hill/', 'Notting Hill.jpg', 1, 'Zatímco ona je slavná hollywoodská hvězda a celý svět jí leží u nohou, on má malou prodejnu knih v malebné londýnské čtvrti. Anna Scottová, herečka, která do Anglie přijela na premiéru svého nového filmu, zajde do krámku nesmělého Williama zcela náhodou. Z oboustranného zájmu však začne klíčit vztah, který nutně musí projít zatěžkávací zkouškou. Ale někdy i dva lidé ze zcela odlišných světů mohou najít společnou cestu životem.(oficiální text distributora)', '2014-06-23 20:56:33.646', 1999, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20016, '201406/Popelka.m4v', 'Popelka', 3, 0, 'cz', NULL, 'en', 'Cinderella', 'http://www.csfd.cz/film/33887-popelka/', 'Popelka.jpg', NULL, 'Snad nejznámější pohádka na světě, příběh chudé dívky, která se přes noc stává princovou vyvolenou, vychází nyní v zrestaurované podobě na Disney DVD a VHS dokonce ve speciální verzi. Nezapomenutelnou, animovanou verzi této pohádky dokončilo v roce 1950 studio Disney. Diváky kromě kouzelné animace zaujala i nádherná hudba, která film doprovází. Popelka byla tehdy nominována na Oscara ve třech kategoriích: za Nejlepší píseň (Bibbidi-Bobbidi-Boo), Nejlepší soundtrack a Nejlepší zvuk. Kromě toho získala po celém světě velkou řadu ocenění (jen pro ilustraci uveďme Golden Bear Award, Berlínský mezinárodní filmový festival, 1951 či Cinemonde Et Les Film Francais, 1950 a mnohé další).(oficiální text distributora)', '2014-06-23 20:56:33.728', 1950, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (20017, '201406/Svetlo ve tme.m4v', 'Svetlo ve tme', 15, 0, 'cz', NULL, 'en', 'Half light', 'http://www.csfd.cz/film/188112-svetlo-ve-tme/', 'Svetlo ve tme.jpg', 1, 'Život Rachel Carlson (Demi Moore), úspěšné autorky thrillerů, naprosto změní tragická událost: její pětiletý syn nešťastnou náhodou utone. Rachel se rozhodne změnit životní styl a odstěhuje se na odlehlou chatu na skotském pobřeží. Zde však zůstává sama napospas své osamělosti a děsivým představám. Přestává rozeznávat, co je realita a co halucinace, a nakonec bude muset bojovat, aby si zachránila holý život.(oficiální text distributora)', '2014-06-23 20:56:33.809', 2006, '');
