INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (21026, '201407/DISTRICT_9.m4v', 'District 9', 4, 0, 'cz', NULL, 'en', 'District 9', 'http://www.csfd.cz/film/257869-district-9/', 'DISTRICT_9.jpg', NULL, 'Před více než dvaceti lety došlo k prvnímu kontaktu mimozemské civilizace s lidstvem. Lidé očekávali děsivou bitvu nebo významný technologický pokrok. Nedošlo ani k jednomu. Mimozemšťané byli uprchlíky z vlastní planety. Stvoření byla ubytována v nouzových domovech v jihoafrickém Okrsku 9 (District 9), zatímco se státy celého světa dohadovaly, co a jak dál. Teď všem dochází trpělivost. Dohled nad mimozemšťany byl svěřen MNU, soukromé organizaci, které je osud mimozemšťanů lhostejný. Měla by ale naději na obrovský výdělek, pokud dokáže zprovoznit mimozemské zbraně, které vyžadují k aktivaci mimozemskou DNA. Napětí mezi mimozemšťany a lidmi dospěje ke svému vrcholu ve chvíli, kdy začne MNU obyvatele Okrsku 9 stěhovat do nového tábora. Jeden z agentů MNU, Wikus van der Merwe (Sharlto Copley) se nakazí mimozemským virem, který začne měnit jeho DNA. Záhy se stává nejhledanějším mužem planety – je klíčem k odhalení tajů mimozemské technologie. Vyděšený muž má jedinou možnost: ukrýt se v Okrsku 9.(oficiální text distributora)', '2014-07-24 03:10:12.237', 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (21027, '201407/FRANKENWEENIE.m4v', 'Frankenweenie', 3, 0, 'en', 'cz', 'en', 'Frankenweenie', 'http://www.csfd.cz/film/236117-frankenweenie-domaci-mazlicek/', 'FRANKENWEENIE.jpg', NULL, 'Frankenweenie: Domácí mazlíček je dojemným příběhem chlapce a jeho psa. Když mladý Victor nečekaně přijde o svého milovaného psa Sparkyho, povolá na pomoc vědu a svého nejlepšího přítele přivede znovu k životu – jen s několika drobnými změnami. Snaží se svůj výtvor ukrývat, ale když se Sparkymu podaří uniknout, zjistí Victorovi spolužáci, učitelé i ostatní obyvatelé města, že znovuzrození může mít příšerné následky.(oficiální text distributora)', '2014-07-24 03:10:12.308', 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (21032, '201407/Zamek v oblacich.m4v', 'Zamek v oblacich', 3, 0, 'cz', NULL, 'jp', 'Howl no ugoku shiro', 'http://www.csfd.cz/film/91936-zamek-v-oblacich/', 'Zamek v oblacich.jpg', NULL, 'Mladá dívka Sophie, co by ošklivé káčátko se zlatým srdcem se na počátku příběhu stává obětí magie zlomyslné čarodejnice, která ji přemění na stařenu. Sophiinou jedinou nadějí se zdá být vyhledat mladého čaroděje Howla, jehož zázračný dům na muřích nohách obchází po vrcholcích hor nad městem. Když se jí to s nemalým vypětím podaří, začne zde pracovat jako uklízečka a domácí. Pomalu objevuje taje a záhady, které Howlův chodící dům skrývá. Jak se ale postupně rozvíjí klubko záhad okolo Howla, Sophie je vtažena do jeho světa a začíná chápat kým je. Do pohledného mladého čaroděje se zamiluje a její skálopevná láska bude postavena před zkoušku, ze které nikdo nemusí vyváznout živ a zdráv.(shinobi)', '2014-07-24 03:10:12.656', 2004, '');
