INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (30030, '201407/BEAUTY_AND_THE_BEAST_DE.m4v', 'Kraska a Zvire', 3, 0, 'sk', NULL, 'en', 'Beauty and The Beast', 'http://www.csfd.cz/film/18252-kraska-a-zvire/', 'BEAUTY_AND_THE_BEAST_DE.jpg', NULL, 'V dobách, kdy ještě pohádka byla skutečností, žil ve Francii krásný mladý princ, který ale byl pyšný a sobecký. Stará čarodějnice se ho rozhodla potrestat a proměnila ho v hrozné zvíře. Zželelo se jí však mladého života a dala princi ještě šanci na vysvobození: pokud se do něj zamiluje nějaká dívka, stane se opět člověkem. Uběhlo několik let. Ve vzdálené vesnici žil podivínský vynálezce Maurice, který měl překrásnou dceru - říkali jí Kráska. Myslel si na ni marnivý krasavec Gaston, ale Krásku zajímaly jen knihy. Jednou se vynálezce Maurice vydal na cesty a zabloudil do domu obludného zvířete. Aby se zachránil, musela za zvířetem přijít jeho dcera Kráska. Jak už to v pohádkách bývá, všechno dobře dopadne. Kráska se do zvířete zamiluje, princi se vrátí jeho původní podoba, napraví se a bude z něj dobrý a spravedlivý král. Kráska a zvíře byla jako vůbec první kreslený film v historii udílení Oscarů nominována na nejlepší film, oceněna byla Oscary za nejlepší píseň a nejlepší hudbu.(oficiální text distributora)', '2014-07-31 12:40:27.026', 1991, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (30040, '201407/CARRIE.m4v', 'Carrie', 2, 0, 'cz', NULL, 'en', 'Carrie', 'http://www.csfd.cz/film/2316-carrie/', 'CARRIE.jpg', NULL, 'Hrdinkou filmu je nesmělá dívka Carrie Whiteová, která je terorizována pološílenou matkou, náboženskou fanatičkou, a pro svůj vzhled a zakomplexovanost také zdrojem posměchu spolužáků i některých učitelů. Jednoho dne v sobě Carrie objeví nadpřirozené telekinetické schopnosti. Zjistí, že její pohled může nabýt ničivé síly, tryskající bez ohledu na její vůli, a to ve chvíli, kdy je nervově nejrozrušenější. Atmosféra hrůzy narůstá souběžně se stupňujícím se ponižováním a nenávistí, až dosáhne bodu, kdy už není možné situaci zachránit a rozmotat spletenec nedorozumění. Zdánlivě nevážné a nedůležité epizody splétají síť, ze které se již nikdo nedokáže vymanit. Pohár přeteče ve chvíli, kdy je ponižovaná, psychicky týraná dívka zcela nečekaně pozvána nejhezčím chlapcem třídy na velký ples. Zaskočená, téměř šokovaná dívka nejprve odmítne, ale pak přijme. Přes protesty a hysterické výstupy matky se Carrie vydá v nových šatech na ples. Prožije krátkou chvíli triumfu, kdy je zvolena královnou plesu a téměř se vymaní role opovrhované škaredky. To je chvíle, kdy už neunese další potupu a kdy krutým žertem dohnána k zoufalé sebezničující vzpouře změní pomocí svých schopností maturitní ples v děsivá jatka.(Allan)', '2014-07-31 12:40:27.304', 1976, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (30050, '201407/CATCH_44.m4v', 'Catch .44', 15, 0, 'cz', NULL, 'en', 'Catch .44', 'http://www.csfd.cz/film/278709-catch-44/', 'CATCH_44.jpg', NULL, 'Pro Tes a její dvě kámošky Karu a Dawn to zprvu vypadalo jednoduše: v odlehlém motorestu se měly zmocnit drogové zásliky pro jejich šéfa Mela (Bruce Willis). Když se ale rozpoutá řetězec nezastavitelných událostí, velmi rychle jim dojde, že tohle může být jejich poslední kšeft. To, co zprvu vypadalo jako snadná práce, se postupně mění na nebezpečnou hru kočky s myší.(oficiální text distributora)', '2014-07-31 12:40:27.388', 2011, '');
