INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (30060, '201407/Dorucovaci_sluzba_slecny_Kiki.m4v', 'Dorucovaci sluzba carodejky Kiki', 3, 0, 'cz', NULL, 'jp', 'Majo no takkyūbin', 'http://www.csfd.cz/film/28675-dorucovaci-sluzba-carodejky-kiki/', 'Dorucovaci_sluzba_slecny_Kiki.jpg', NULL, 'Magický příběh podnikavé třináctileté dívky Kiki, která musí splnit tradiční rituál, aby se stala plnohodnotnou čarodějnicí. V doprovodu své černé kočky Jiji se musí vydat vstříc celoživotnímu dobrodružství, aby prokázala svojI soběstačnost. Přistane v dalekém přímořském městečku a zakládá si vlastní živnost - doručovací službu. Tím započne nádherná zkušenost samostatnosti, zodpovědnosti a nalezení svého místa ve světě.(oficiální text distributora)', '2014-07-31 12:40:27.528', 1989, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (30070, '201407/Little_Mermaid_LIC1.m4v', 'Mala morska vila', 3, 0, 'cz', NULL, 'en', 'Little Mermaid', 'http://www.csfd.cz/film/11734-mala-morska-vila/', 'Little_Mermaid_LIC1.jpg', NULL, 'Dcera krále moří se jmenuje Ariel, je jí šestnáct let a je zamilovaná do prince Erika, kterému zachránila život, když jeho loď ztroskotala v bouři. Protože vládce podmořské říše nemá pro náklonnost své dcery k lidem pochopení, musí se Ariel obrátit se žádostí o pomoc na zlou čarodějnici. Ta ji na tři dny přičaruje nohy pod podmínkou, že jí víla daruje svůj krásný hlas. A ještě je tu jeden háček pokud princ dívku do tří dnů z lásky nepolíbí, stane se z ní opět mořská panna s ploutví a její duše navěky propadne zlé čarodějnici. Bůhví jak by to dopadlo, kdyby Ariel nepomáhali její legrační přátelé krab, racek a rybka Film získal dva Oscary - za hudbu (Alan Menken) i píseň (Alan Menken, Howard Ashman) a na rozdíl od stejnojmenné předlohy Hanse Christiana Andersena končí šťastně.(oficiální text distributora)', '2014-07-31 12:40:28.113', 1989, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (30080, '201407/Princezna Mononoke.m4v', 'Princezna Mononoke', 3, 0, 'cz', NULL, 'jp', 'Mononoke hime', 'http://www.csfd.cz/film/22205-princezna-mononoke/', 'Princezna Mononoke.jpg', NULL, 'Za dávných časů, v divokých hvozdech Japonska začíná mýtický boj mezi rozrůstající se civilizací lidí a zvířecími božstvy, která vládnou lesům. A také příběh lásky lidského dítěte vlčí bohyně - princezny Mononoke - a válečníka věřícího v mír mezi lidmi a přírodou - prince Ashitaky.(oficiální text distributora)', '2014-07-31 12:40:28.301', 1997, '');
