INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (30280, '201407/tim_burton_christmas_movie.m4v', 'Ukradene Vanoce', 4, 0, 'cz', 'cz', 'en', 'The Nightmare Before Christmas', 'http://www.csfd.cz/film/29484-ukradene-vanoce/', 'tim_burton_christmas_movie.jpg', NULL, 'Kultový animovaný film z produkcie Tima Burtona s vynikajúcou hudbou a pesničkami Dannyho Elfmana. Jack Skellington, smutný kostlivec s tekvicovou hlavou a nekorunovaný vládca Halloweenového mestečka, sa nudí. Veď koho by už vzrušoval permanentný Sviatok Všetkých svätých, ktorý v Halloweentowne trvá celý rok? Ani jeho najmilovanejšiu prácu, strašenie detí, ktorú vykonáva svedomito a invenčne, tento rok nik neocenil. Všetkých pobláznili nové hviezdy, populárni upíri, ktorí vytvorili rekord v pití ľudskej krvi za noc. A dokonca vyhrali aj hokejový zápas na cintoríne s lebkou namiesto puku! Jedného dňa sa Jack náhodou ocitne v susednom Vianočnom meste. Celkom ho uchvátia vianočné sviatky, ktoré doteraz nepoznal všade vládne radosť detí, Santa Claus, jeho darčeky a šťastie dospelých. Aj Jack by rád obdaroval obyvateľov Vianočného mestečka a prekvapil ich počas sviatkov čímsi, čo nikdy nevideli a nezažili Halloweenom! Rozhodne sa im preto zinscenovať Vianoce v haloweenovom šate. V tom mu majú pomôcť jeho priatelia zo záhrobia. Obyvatelia Halloweentownu, umrlci, kostlivci, netopiere, strašidlá a škriatkovia sa už radujú, ako potešia svojich blízkych susedov. Ale nie je to také jednoduché, ako si predstavovali! Unesú toho odpudzujúceho tlsťocha Santu Clausa, aby neškodil, vyrobia krásne hračky a Jack ich nadeľuje deťom Vianočného mesta zo záprahu ťahanom kostrami mŕtvych sobov. No výsledok nie je uspokojujúci. Deti vybaľujú očarujúce umrlčie lebky, prítulné žravé netopiere či nežné bábiky kostlivcov - ale prečo kričia hrôzou a zamdlievajú z hnusu? Protilietadlové delá už mieria na oblohu a snažia sa zostreliť ten diabolský záprah, rozsievajúci do nechápajúcich detských srdiečok zmätok a strach...(oficiální text distributora)', '2014-08-18 19:38:49.916', 1993, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (30320, '201409/MR_PEABODY_AND_SHERMAN.m4v', 'Mr Peabody a Sherman', 3, 0, 'sk', NULL, 'en', 'Mr Peabody and Sherman', 'http://www.csfd.cz/film/291174-dobrodruzstvi-pana-peabodyho-a-shermana/', 'MR_PEABODY_AND_SHERMAN.jpg', NULL, 'Pan Peabody je geniální jako Einstein, vtipný jako Oscar Wilde, odvážný jako Indiana Jones, má deduktivní schopnosti Sherlocka Holmese, styl oblékání Jamese Bonda a kulinářské dovednosti Zdeňka Pohlreicha. Je také světově proslulou celebritou, držitelem Nobelovy ceny, olympijským vítězem ve skoku a desetiboji. A navíc je pes. Díky svým schopnostem a navzdory tomu, že je šelma, adoptoval pan Peabody malého kluka Shermana, kterého od plenek vychovává tak, jak nejlépe umí. Přesto si občas musí přiznat, že je to snad jediná věc, která mu tak úplně nejde a že má co dělat, aby se Shermanem vůbec udržel krok. Sherman je totiž velmi zvědavý a upřímný, díky čemuž se často dostává do různě velkých průšvihů. I proto, aby ukojil synkovu touhu po poznání historie, vynalezl pan Peabody úžasný přístroj jménem Časomat. A díky tomu mají přístup kamkoliv do minulosti. Společně tak zažívají neskutečná dobrodružství, která si člověk nebo pes dokáže sotva představit, poznávají slavné postavy i okamžiky dějin. Na cestách časem musí dodržovat pár zásadních pravidel jako „nikdy se nesetkat sám se sebou", nebo „jen minimálně ovlivňovat minulost nebo raději vůbec", což se jim daří do chvíle, než Sherman potajmu vezme na divokou projížďku Časomatem svou spolužačku Penny, na kterou chce udělat dojem. Nechtěně zamíchají několika nejdůležitějšími událostmi světových dějin a nakonec jim pan Peabody musí přispěchat na pomoc a zachránit je, než se navždy změní minulost, přítomnost i budoucnost. Můžete se těšit na velkou legraci a pár zábavných setkání s velikány světových dějin, při kterých občas může tuhnout krev. Komu by neztuhl úsměv, kdyby se ocitl před branami Tróje ve finální fázi dobývání tohoto města, nebo v Paříži v době Velké francouzské revoluce?(CinemArt)', '2014-09-07 03:10:37.137', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33130, '201411/TRISTAN.m4v', 'Tristan a Izolda', 3, 0, 'sk', NULL, 'fr', 'Tristan et Iseut', 'http://www.csfd.cz/film/201907-tristan-a-isolda/', 'TRISTAN.jpg', 1, 'Mnozí věří, že se tento příběh skutečně stal. On se jmenoval Tristan, byl mladý a statečný. Izolda byla křehká a krásná. Žili v dávných dobách před několika staletími a ačkoli jejich láska nebyla okolí příliš po vůli, vydržela navěky. Tristan a Izolda je nesmrtelný příběh lásky a přátelství pro všechny věkové kategorie diváků na celém světě. Zajímavý animovaný film pro celou rodinu.(oficiální text distributora)', '2014-11-15 08:43:15.038', 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33140, '201411/Princezna slunce.m4v', 'Princezna slunce', 3, 0, 'sk', NULL, 'fr', 'La Reine soleil', 'http://www.csfd.cz/film/237995-princezna-slunce/', 'Princezna slunce.jpg', 1, 'Princezna Akhesa je dcerou mocného egyptského faraóna. Ovšem trochu paličatou a neposlušnou dcerou. Když se dozví, že moc jejího otce je v ohrožení, rozhodne se vyhledat svou matku, která žije léta v ústraní, a požádá ji o pomoc. Spolu s ní se na dlouhou cestu cestu vydá i princ Tut, o kterém si sice Akhesa myslí, že je zbabělec a ufňukanec. Během jejich společných dobrodružství ale změní názor...(Kahalan)', '2014-11-15 08:43:15.327', 2007, '');
