INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33150, '201411/HOW_TO_TRAIN_YOUR_DRAGON_2.m4v', 'Ako si vycvicit draka 2', 3, 0, 'sk', NULL, 'en', 'How to Train Your Dragon II', 'http://www.csfd.cz/film/295082-jak-vycvicit-draka-2/', 'HOW_TO_TRAIN_YOUR_DRAGON_2.jpg', 1, 'Ostrov Blp býval místem, kde Vikingové ve dne v noci bojovali s draky. Pak však mladý snílek Škyťák vycvičil draka Bezzubku, spřátelil se s ním a hlavně všem vysvětlil, že lidé a draci mohou žít v harmonii. Příběh poskočil o pět let dopředu a z Blpu se stal v mezidobí dračí ráj, kde se lidé a draci navzájem respektují a mají se rádi. Škyťákova největší kamarádka Astrid je hvězdou oblíbených dračích závodů, ve kterých hravě poráží ostatní vikinské teenagery, zatímco Škyťák dává přednost osamělým spanilým letům s Bezzubkou, během nichž mapují neznámá území a objevují nové světy a nové draky. Jedno z jejich dobrodružství je přivede do těžko přístupné ledové jeskyně, která je domovem stovky neznámých divokých draků. Taky se zde setkají s tajemnou Dračí jezdkyní, z které se vyklube Škyťákova dávno ztracená matka Valka. Na láskyplné objetí matky se synem ale není čas, protože nad soužitím lidí a draků se stahují mračna, která se přihnala díky Dragovi, mocichtivým a samozvaným „dračím bohem", kterému slouží nafoukaný lovec draků Eret. Tihle dva představují nebezpečí, se kterým sám Škyťák s Bezzubkou nic nezmůžou, a tak dají dohromady starou partu, do níž kromě Astrid patří i Tlamoun, Snoplivec, Rybinoha a dvojčata Ťafan a Rafana. Spolu s nimi se ale budou muset mocnému nepříteli postavit i Škyťákův otec, vikinský náčelník Kliďas Velikán a především jeho matka Valka. Naučit táhnout tenhle nesourodý spolek za jeden provaz bude nesmírně obtížné. Bez toho ale nebudou mít obyvatelé Blpu sebemenší šanci.(CinemArt)', '2014-11-15 08:43:15.397', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33190, '201411/SCOOBY_DOO_CAMP_SCARE.m4v', 'Scooby Doo v tabore duchov', 3, 0, 'cz', '  ', 'en', 'Scooby Doo Camp Scare', 'http://www.csfd.cz/film/284303-scooby-doo-a-prizrak-na-letnim-tabore/', 'SCOOBY_DOO_CAMP_SCARE.jpg', 1, 'Vítejte v táboře Malý los – vedoucí jsou tu přátelští, aktivity nekonečné a hrůzostrašné historky působivé. Dokonce tak, že kdykoli někdo začně vyprávět duchařský příběh, strašidelné stvoření ožije! Ale parta Záhady s.r.o. se nedá tak snadno vyděsit, takže zasedněte kolem táboráku a pár zloduchů si pěkně podáme. Scooby-Doo, Shaggy, Freddy, Daphne a Velma pátrají po indiciích, které by jim pomohly objasnit tajemství Dřevorubce, Rybího muže a Bánší z Černého kaňonu. A samozřejmě, Shaggy a Scooby-Doo jsou na horké stopě případu "co bude k večeři”! Připravte se na strašidelnou letní legraci se Záhadami s.r.o. v tomto báječném, celovečerním filmu, který dá úplně nový význam termínu "táborový duch”!(oficiální text distributora)', '2014-11-23 16:53:09.864', 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33210, '201411/SCOOBYDOO_WRESTLEMANIA_MYSTERY.m4v', 'Scooby Doo zahada kolem Wrestlemanie', 3, 0, 'cz', '  ', 'en', 'Scooby Doo WrestleMania Mystery', 'http://www.csfd.cz/film/366273-scooby-doo-zahada-kolem-wrestlemanie/', 'SCOOBYDOO_WRESTLEMANIA_MYSTERY.jpg', 1, 'Přichází zbrusu nový původní film a s ním neúnavně akční Scooby - Doo, Shaggy a další členové jejich gangu! Záhada začíná, když Shaggy a Scooby - Doo vyhrají lístky na sérii zápasů do WWE City a přesvědčí ostatní, aby se k nim přidali. Jenže tohle město skrývá strašné tajemství: drtivou silou je svírá Medvědí přízrak! Zachránit vytoužený titul pomohou našim kamarádům i superhvězdy jako John Cena, Triple H, Sin Cara, Brodus Clay, AJ Lee, The Miz a Kane. Sledujte Scooby - Dooa a jeho partu, jak se snaží přijít celé věci na kloub dřív, než bude pozdě. Skvělé dobrodružství pro celou rodinu - to musíte mít ještě dnes!(Magic Box)', '2014-11-23 19:51:35.646', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33220, '201411/MALEFICENT.m4v', 'Vladkyna zla', 3, 0, 'sk', NULL, 'en', 'Maleficent', 'http://www.csfd.cz/film/210164-zloba-kralovna-cerne-magie/', 'MALEFICENT.jpg', 1, 'Snímek Zloba – Královna černé magie vypráví dosud nezpracovaný příběh legendární zlé královny z klasického snímku společnosti Disney Šípková Růženka z roku 1959, a seznamuje diváky s její zradou, která nakonec způsobila, že se její kdysi čisté srdce proměnilo v kámen. Královna Zloba, hnaná touhou po pomstě a urputnou snahou udržet si trůn, uvrhne na novorozenou dcerku lidského krále, Auroru, krutou kletbu. Jak princezna roste, ocitá se přímo v samém středu narůstajícího konfliktu mezi lesním královstvím, které si zamilovala, a lidským královstvím, ze kterého pochází. Zloba si uvědomuje, že princezna může být klíčem k míru mezi oběma říšemi a je tak nucena k nemilosrdným činům, které oba světy navždy změní.(Falcon)', '2014-11-23 19:51:35.837', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33230, '201411/TRANSFORMERS_AGE_OF_EXTINCTION.m4v', 'Transformers: Zanik', 4, 40, 'cz', '  ', 'en', 'Transformers: Age of Extiction', 'http://www.csfd.cz/film/312718-transformers-zanik/', 'TRANSFORMERS_AGE_OF_EXTINCTION.jpg', 4, 'Automechanik Cade Yager (Walhberg) se snaží uživit sebe a svou dospívající dceru Tessu (Nicola Peltz) opravováním hodně nepojízdných vraků. Zoufale se mu nedaří, ale jen do doby, než se z jednoho roztřískaného náklaďáku vyklube Transformer a zrovna ten nejdůležitější – Optimus Prime. Cade z toho jen těžko může mít radost, protože Amerika se ještě úplně nevzpamatovala ze souboje „hodných" Autobotů se „zlými" Deceptikony, který na konci třetího dílu zlikvidoval půlku Chicaga. Čtyři roky poté jsou všichni zástupci téhle mimozemské rasy na indexu a elitní vědecké mozky se snaží vytvořit pozemské Transformery, kteří by účinněji čelili podobným hrozbám. Z minulosti ale víme, že cesta do pekel bývá dlážděná dobrými úmysly, a jinak tomu nebude ani v tomto případě. To však Cadea příliš netrápí. Zatím. Jako nový lidský parťák toho nejslavnějšího z Transformerů se ocitl nejen v hledáčku americké vlády, ale pronásledují ho i Optimovi krajané. Do toho všeho se jeho dcera zamilovala, s čímž jako otec rozhodně nemůže souhlasit. Čtvrtí Transformers kromě Wahlberga v hlavní roli přinesou ještě jednu zásadní novinku – Dinoboty, což jsou mechaničtí tvorové, kteří vypadají jako prehistoričtí ještěři. Vítejte v transformovaném Jurském parku.(Bontonfilm CZ)', '2014-11-23 19:51:36.036', 2014, '');
