INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33490, '201412/STAR_TREK_V_EU.m4v', 'Star Trek Final Frontier', 4, 38, 'en', 'en', 'en', 'Star Trek Final Frontier', 'http://www.csfd.cz/film/20585-star-trek-v-nejzazsi-hranice/', 'STAR_TREK_V_EU.jpg', 5, 'Na zapadlé planetě Nimbus III, kde se střetávají hranice Federace, Klingonské a Romulanské říše, na planetě takzvaného galaktického míru, kde najdete vše kromě míru, se vynořuje Vulkánec Sybok a začíná stavět svou galaktickou armádu Světla, aby naplnil dávná proroctví. Na rozdíl od jiných Vulkánců využívá své telepatické schopnosti ke kontrole ostatních, kteří ho pak slepě následují. Když přímým útokem získá kontrolu nad "hlavním městem" pusté planety, zajme vyslance všech tří mocností. Kapitán Kirk je spolu s novou a dosud neseřízenou Enterprise vyslán, aby rukojmí osvobodil. Avšak Sybok pro ni připravil past, ze které není úniku...(oficiální text distributora)', '2014-12-05 21:53:23.38', 1989, '');
