INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33500, '201412/STAR_TREK_INSURRECTION.m4v', 'Star Trek Insurrection', 4, 38, 'en', 'en', 'en', 'Star Trek Insurrection', 'http://www.csfd.cz/film/33056-star-trek-ix-vzpoura/', 'STAR_TREK_INSURRECTION.jpg', 9, 'Od dob založení Spojené Federace Planet byla základní směrnice zcela jasná - žádná expedice hvězdné flotily se nesmí vměšovat do přirozeného vývoje jiných civilizací, dokud nebudou připraveny na první kontakt - čili dokud neobjeví technologii hypermotoru. Nyní ale Picard dostává rozkazy, které jsou v přímém rozporu s tímto pravidlem. Pokud uposlechne nadřízené, 600 mírumilovných obyvatel planety Ba´ku bude násilně vysídleno ze svých domovů pro údajné dobro miliónů ostatních lidí, kteří pak budou moci využívat účinku Pramene mládí planety Ba´ku. Pokud neuposlechne, riskuje svou loď, svou kariéru i svůj život. Pro Picarda je však volba jasná, postavit se proti celé hvězdné flotile... a vést vzpouru za záchranu Ráje.(oficiální text distributora)', '2014-12-05 21:53:23.592', 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33510, '201412/STAR_TREK_VIII_EU.m4v', 'First Contact', 4, 38, 'en', 'en', 'en', 'First Contact', 'http://www.csfd.cz/film/19251-star-trek-viii-prvni-kontakt/', 'STAR_TREK_VIII_EU.jpg', 8, 'Bitva o záchranu budoucnosti začala. Americký film na motivy slavného seriálu. V pořadí osmý film (a druhý s posádkou Nové generace), nazvaný Star Trek: První kontakt (1996), patří bezesporu v sérii k těm nejlepším. V něm posádka hvězdné lodi Enterprise, vedená kapitánem Picardem (Patrick Stewart), podnikne bojovou misi do minulosti, přesněji řečeno do 21. století, aby zachránila budoucnost naší planety. Expanzivní civilizace Borg se totiž pokouší zabránit prvnímu kontaktu, ke kterému došlo 4. dubna 2063, aby mohla kolonizovat Zemi, nechráněnou spoluprací s dalšími vesmírnými druhy. Vedle neohrožené posádky Enterprise hrají důležitou roli v příběhu excentrický vynálezce nadsvětelného pohonu Zefram Cochrane (James Cromwell) a jeho černošská spolupracovnice Lily (Alfre Woodardová). Režie se poprvé ujal představitel prvního důstojníka Rikera Jonathan Frakes, který měl už určité zkušenosti s režií několika seriálových epizod. Přesvědčivě vypadající vizuální efekty vznikly ve známé Lucasově trikové továrně Industrial Light and Magic. Postavu borgské královny vytvořila jihoafrická herečka Alice Krigeová, která si ji znovu zopakovala v závěrečné dvojepizodě seriálu Star Trek: Voyager, nazvané Dohra (2001), a v krátkém plastickém snímku Star Trek: Borg Encounter (2003), určeném k promítání v zábavních parcích.(oficiální text distributora)', '2014-12-05 21:53:23.664', 1996, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33520, '201412/STARTREK_MOTION_PICTURE.m4v', 'The Motion Picture', 4, 38, 'en', 'en', 'en', 'The Motion Picture', 'http://www.csfd.cz/film/9834-star-trek-film/', 'STARTREK_MOTION_PICTURE.jpg', 1, 'Slavná ENTERPRISE se pyšně vznáší ve vesmíru. Neznámý vetřelec zničí tři mohutné Klingonské lodě a kapitán James T. Kirk se vrací do nové Enterprise převzít velení. Všichni se snaží zastavit mimozemského vetřelce na jeho neodvratné cestě k planetě Zemi.(oficiální text distributora)', '2014-12-05 21:53:23.734', 1979, '');
