INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33600, '201412/Transcendence.m4v', 'Transcendencia', 4, 0, 'cz', 'cz', 'en', 'Transcendence', 'http://www.csfd.cz/film/329244-transcendence/', 'Transcendence.jpg', NULL, 'Pohledný a úspěšný vědec Will Caster (Johnny Depp) má velký celoživotní sen: vytvořit stroj obdařený umělou inteligencí i schopností vnímat emoce, který by dokázal soustředit kolektivní vědění a vědomí lidstva. Jeho kontroverzní experimenty z říše snů mu přináší slávu i nepřátele. Extremisté z řad odpůrců umělé inteligence se ho pokusí zastavit a spáchají na něj atentát ve chvíli, kdy je jeho dílo téměř u konce. Sami tím však „božský experiment" uspíší a Will osobně se v projektu transcendence stane hlavním hrdinou. Pro jeho ženu Evelyn (Rebecca Hall) a dalšího vědce Maxe (Paul Bettany) zde totiž neleží otázka, zda je to možné, ale zda je to správné. A co se vlastně stane. Willovo vědomí připojí k super počítači a obřímu vesmíru umělé mysli, kterou sám vytvořil. Důsledky záhy pocítí celý svět. Willova původní touha obsáhnout vědění celého světa se však rychle mění v jiný cíl: získat nad celým světem kontrolu.(Bontonfilm CZ)', '2014-12-26 18:06:18.671', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33780, '201501/Obchod na korze.m4v', 'Obchod na korze', 15, 0, 'sk', 'en', 'sk', 'Obchod na korze', 'http://www.csfd.cz/film/4999-obchod-na-korze/', 'Obchod na korze.jpg', 1, 'Dej filmu sa odohráva počas druhej svetovej vojny v malom meste Slovenského štátu. Stolár Tóno Brtko, poctivý a slušný človek prijme pod nátlakom svojej ženy a vysoko postaveného švagra arizačný dekrét na obchod staručkej pani Lautmannovej. Aj keď je obchod nevýnosný, Brtko v ňom zotrváva a predstiera, že arizuje. Stará pani sa o Tóna stará ako o syna a on jej pomáha ako môže. Príde však deň, keď všetci Židia z mestečka obdržia predvolanie k transportu. Všetci okrem pani Lautmannovej. Tóno sa ju spočiatku snaží ukryť, no neskôr ju prehovára, aby sa sama prihlásila. Tlak udalostí dovedie Tóna Brtka k hraničnej životnej situácii...(oficiální text distributora)', '2015-01-17 21:17:29.517', 1965, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33770, '201501/FOR_A_FEW_DOLLARS_MORE.m4v', 'Za par dolaru navic', 15, 0, 'cz', 'cz', 'en', 'For a few Dollars More', 'http://www.csfd.cz/film/5915-pro-par-dolaru-navic/', 'FOR_A_FEW_DOLLARS_MORE.jpg', 1, 'Lovec lidí Clint Eastwood se vydává po stopách obávaného zločince El India, který uprchnl z vězení. Jelikož největší banka na Západě je v El Pasu, je více než jasné, že jeho kroky budou směřovat právě tam... Lovec lidí Mortimer pátrá po hledaném vrahovi Callawayovi. Dopadne ho, vyinkasuje odměnu a vydává se hledat dalšího zločince. Tomu je však na stopě už jiný lovec, Monco, kterému se podaří jej dopadnout i inkasovat vypsanou odměnu. Brzy nato dojde k útěku obávaného lupiče El India z vězení. Monco i Mortimer přijíždějí do El Pasa, kde je největší banka. Oba předpokládají, že se ji El Indio po svém útěku určitě pokusí vykrást. Monco a Mortimer na sebe narazí a je z toho rvačka. Potom se však dohodnou na partnerství pokud El India dopadnou, o odměnu se rozdělí. El Indio mezitím loupež skutečně plánuje. Monco se má podle plánu pokusit proniknout do bandy jako jejich kumpán...(oficiální text distributora)', '2015-01-17 21:17:29.496', 1965, '');
