INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33700, '201501/EDGE_OF_TOMORROW.m4v', 'Na hrane zitrka', 4, 0, 'cz', 'cz', 'en', 'Edge of Tomorrow', 'http://www.csfd.cz/film/307556-na-hrane-zitrka/', 'EDGE_OF_TOMORROW.jpg', 1, 'Příběh se odehrává v blízké budoucnosti. Mimikové, mimozemská rasa připomínající včelí kolonii, vytrvale útočí na Zemi, obracejí velká města v hromadu trosek a zanechávají za sebou miliony lidských obětí. Podplukovník Bill Cage (Tom Cruise), který nikdy nebyl v žádné bitvě, je bez cirátů degradován a s nulovým výcvikem a mizernou výzbrojí poslán splnit úkol, který není ničím jiným než sebevražednou misí. Během několika minut je zabit, předtím se mu však podaří zneškodnit Alphu. Ač se to zdá nemožné, procitne opět na začátku toho samého pekelného dne a je nucen znovu bojovat a zemřít... a tak stále dokola. Přímý fyzický kontakt s mimozemšťanem způsobil, že se dostal do časové smyčky a je tak odsouzen prožívat nelítostnou bitvu znovu a znovu. S každým dalším pokusem se však zvyšuje jeho odolnost a pohotovost a své rostoucí schopnosti dokáže v boji s Mimiky lépe využívat. Po jeho boku bojuje členka speciálních jednotek Rita Vrataski (Emily Blunt), která zlikvidovala víc Mimiků než kterýkoliv jiný bojovník na Zemi. Každá další bitva je pro Cagea a Ritu příležitostí najít způsob, jak mimozemské vetřelce porazit a Zemi zachránit.(Warner Bros. CZ)', '2015-01-17 21:17:29.087', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33710, '201501/Muj_soused_Totoro.m4v', 'Muj soused Totoro', 3, 0, 'cz', 'cz', 'jp', 'Tonari no Totoro', 'http://www.csfd.cz/film/42137-muj-soused-totoro/', 'Muj_soused_Totoro.jpg', 1, 'Film, po kterém se postava Totora stala ikonou studia Ghibli. Satsuke a její malá sestřička Mei se společně s tatínkem stěhují do nového domu na venkově. Jejich maminka je v nemocnici, tatínek má hodně práce, a tak oběma, zejména však malé Mei, zůstává spousta času na seznamování se s novým domovem. Brzy poznají, že dům a jeho okolí nejsou jen tak ledajaké, ale že jsou obývány nejrůznějšími kouzelnými bytostmi. Díky zvědavé Mei narazí na dobráckého lesního ducha Totora, se kterým za krátko zažijí nejedno úžasné dobrodružství, ve světě, který mohou spatřit jen dětské oči.(berg.12)', '2015-01-17 21:17:29.365', 1988, '');
