INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33760, '201501/CONAN_THE_BARBARIAN.m4v', 'Barbar Conan', 15, 0, 'cz', 'cz', 'en', 'Conan The Barbarian', 'http://www.csfd.cz/film/6685-barbar-conan/', 'CONAN_THE_BARBARIAN.jpg', 1, 'Conanův příběh se odehrává v mýtické době, plné nadpřirozených sil a nebezpečí. Když je Conan ještě malý chlapec, je vesnice, ve které žije, přepadena válečníky vedenými Thulsou Soudcem a všichni mimo dětí jsou povražděni. Děti jsou odvedeny a připoutány k obrovskému žentouru. Po patnácti letech utrpení zůstal naživu jen Conan a jeho srdce je plné nenávisti. Jednoho dne je prodán na gladiátorské zápasy a díky své stoupající hvězdě je vzděláván a do umění boje zasvěcován nejlepšími zápasníky a válečníky své doby. Po nějaké době je z otroctví propuštěn a na své pouti pustinou nalezne starobylý meč barbarského krále. Ze spárů čarodějnice osvobodí zloděje a lučištníka Subotaie a společně se vydávají na cestu za slávou a pomstou, za Thulsou Soudcem, jenž kolem sebe během let vybudoval Hadí Kult a sám sebe ustanovil spasitelem světa. Conan se Subotaiem se vloupají do jedné z Hadích věží, kde se setkají s krásnou válečnicí Valerií, mezi níž a Conanem vzplanou city jimi oběma dosud nepoznané. Cesta za vítězstvím bude ještě dlouhá a cena za něj velmi vysoká...(oficiální text distributora)', '2015-01-17 21:17:29.477', 1982, '');
