INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33850, '201501/ZBRAN_1.m4v', 'Smrtonosna zbran 1', 15, 33930, 'cz', 'cz', 'en', 'Lethal Weapon', 'http://www.csfd.cz/film/2438-smrtonosna-zbran/', 'ZBRAN_1.jpg', 10, 'Černošský seržant Roger Murtaugh (Danny Glover), který má pár týdnů do důchodu, dostane za parťáka bílého seržanta Martina Rigse, jenž chtěl po smrti manželky spáchat sebevraždu. Z počátku si nepadnou do oka, společně však budou muset vyřešit případ nebezpečného gangu pašeráků drog. Úspěch filmu zaručil další tři pokračování.(oficiální text distributora)', '2015-02-03 06:10:20.159', 1987, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33860, '201501/ZBRAN_2.m4v', 'Smrtonosna zbran 2', 15, 33930, 'cz', 'cz', 'en', 'Lethal Weapon 2', 'http://www.csfd.cz/film/2439-smrtonosna-zbran-2/', 'ZBRAN_2.jpg', 20, 'Los Angeles. Policisté Martin Riggs a Roger Murtaugh jsou na stopě pašeráků drog. Sledování účetního Lea Getze, který zpronevěřil mafiánské peníze, je přivádí k jihoafrickému diplomatovi Ruddovi. Oba detektivové mají dostatek důkazů, ale Rudd a jeho zabijáci pod rouškou diplomatické imunity nerušeně pracují dál. Riggs, přezdívaný "Smrtonosná zbraň", se uchyluje k nevybíravým metodám. Využije proti Ruddovi i jeho přitažlivou sekretářku. Mezi gangstery a oběma policisty vypukne nevyhlášená válka. Murtaugh se štěstím unikne nastražené bombě a Riggsův karavan se po setkání s Ruddovými muži změní v rozstřílenou trosku. Nezničitelní detektivové však pomalu získávají převahu a Rudd se chystá k ústupu. Schyluje se k rozhodujícímu střetnutí v docích losangelského přístavu...(oficiální text distributora)', '2015-02-03 06:10:20.69', 1989, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33870, '201501/EXPENDABLES_3.m4v', 'Expendables 3', 15, 33940, 'cz', 'cz', 'en', 'Expendables 3', 'http://www.csfd.cz/film/323531-expendables-postradatelni-3/', 'EXPENDABLES_3.jpg', 30, 'Tým Expendables bude tentokrát čelit přímo jednomu ze svých zakladatelů a bývalých šéfů. Conrad Stonebanks s tváří Mela Gibsona stál přímo u jejich zrodu, pak se z něj ale stal odpadlík a dokonce jeden z Barneyho úkolů na zabití. Přežil a teď má bezcitný a bezskrupulózní obchodník se zbraněmi jediný cíl. Bývalého parťáka a celý ten tým postradatelných vysloužilců sprovodit ze světa. Plány Expendables jsou ale přesně opačné. Každopádně to tentokráte bude hodně osobní. Barney navíc vážně uvažuje o tom, že své staré spolubojovníky pošle do důchodu. V řadách Expendables se proto objevuje čerstvá mladá krev s novým stylem a se znalostí něčeho tak opovržlivého jako jsou moderní technologie. Novou krev zastupuje Kella Lutz (známý ze série Twilight ), Glen Powell, boxer Victor Ortiz a také žena! Olympijská medailistka v judu a zápasnice smíšených bojových umění Ronda Rousey.(Bontonfilm CZ)', '2015-02-03 06:10:20.747', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33880, '201501/ZBRAN_4.m4v', 'Smrtonosna zbran 4', 15, 33930, 'cz', 'cz', 'en', 'Lethal Weapon 4', 'http://www.csfd.cz/film/2441-smrtonosna-zbran-4/', 'ZBRAN_4.jpg', 40, 'Čtvrté pokračování úspěšné série akčních filmů. Tentokrát čelí dvojice sympatických, už stárnoucích detektivů hrozbě ze strany čínské mafie a hongkongského zabijáka. I tak může skončit vyjížďka na ryby...(oficiální text distributora)', '2015-02-03 06:10:20.772', 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33890, '201501/HER_2013.m4v', 'Ona / Her', 4, 0, 'cz', 'cz', 'en', 'Her', 'http://www.csfd.cz/film/312650-ona/', 'HER_2013.jpg', 1, 'Děj snímku Her se odehrává v Los Angeles v nedaleké budoucnosti. Theodore (Joaquin Phoenix) je komplikovaný a citlivý muž, který se živí psaním dojemných a osobních dopisů pro druhé. Se zlomeným srdcem po ukončení dlouhého vztahu se začne zajímat o nový, pokročilý operační systém, o kterém jeho výrobce tvrdí, že představuje zcela unikátní a intuitivní bytost. Po jeho instalaci se seznamuje se „Samanthou", umělou inteligencí s milým ženským hlasem (Scarlett Johansson), která má zajímavé postřehy, je citlivá a překvapivě vtipná. Jak její potřeby a požadavky rostou společně s těmi jeho, mění se jejich přátelství ve skutečnou vzájemnou lásku.(Falcon)', '2015-02-03 06:10:20.79', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33900, '201501/ZBRAN_3.m4v', 'Smrtonosna zbran 3', 15, 33930, 'cz', 'cz', 'en', 'Lethal Weapon 3', 'http://www.csfd.cz/film/2440-smrtonosna-zbran-3/', 'ZBRAN_3.jpg', 30, 'Přestřižení nesprávného drátu časované nálože zdemoluje nejen sedmiposchoďovou budovu, ale také sen detektiva Murtaugha o klidném odchodu do důchodu za několik dnů. Se svým poněkud ztřeštěným partnerem Riggsem skončí u pochůzkářů a vyšetřování jejich případu - krádeži zbraní s policejních skladů - se ujme seržantka Lorna Coleová, policistka s ocelovými nervy a perfektní znalostí orientálních bojových umění. Zprvu odmítá s bývalými detektivy spolupracovat, ale nakonec vezme na milost je i jejich známého Lea Getze, realitního agenta a nadšeného spolupracovníka. Pátrání vede k bývalému policistovi Travisovi. Zdá se, že tento soupeř, obklopený bandou ostrých hochů, bude díky svým znalostem policejních metod nad síly tří detektivů a jejich pomocníka. Tvrdý boj o přežití vrcholí a po objevení speciální munice na probíjení neprůstřelných vest ve výzbroji gangsterů ztrácí Murtaugh jistotu, že se vysněného důchodu dočká.(oficiální text distributora)', '2015-02-03 06:10:20.816', 1992, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (34070, '201503/THE_EQUALIZER.m4v', 'The Equalizer', 15, 0, 'cz', 'cz', 'en', 'The Equalizer', 'http://www.csfd.cz/film/308340-equalizer/', 'THE_EQUALIZER.jpg', 1000, 'Ve snímku The Equalizer představuje Denzel Washington bývalého člena elitní tajné vojenské jednotky McCalla, který předstíral svou smrt, aby mohl začít žít poklidný život v Bostonu. Když kvůli záchraně života mladé dívky Teri (Chloe Grace Moretz) musí svůj dobrovolný odpočinek ukončit, ocitá se tváří v tvář extrémně násilným ruským gangsterům. Během tohoto zúčtování se v McCallovi znovu probudí touha po konání spravedlnosti. Pokud se tedy ocitnete v nesnázích s mizivou šancí na záchranu, a nemáte se na koho obrátit, McCall vám pomůže. Je tím jediným, kdo dokáže vaše problémy vyřešit jednou provždy. Je to Equalizer.(Falcon)', '2015-03-05 18:12:54.309', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (33910, '201501/SNEHULIENKA.m4v', 'Snehulienka a sedem trpaslikov', 3, 0, 'sk', 'sk', 'en', 'Snow White and the Seven Dwarfs', 'http://www.csfd.cz/film/83776-snehurka-a-sedm-trpasliku/', 'SNEHULIENKA.jpg', 1, 'Sněhurka a sedm trpaslíků je prvním celovečerním animovaným příběhem, který byl kdy natočen. Sněhurka, za kterou získalo studio Walt Disney i svého prvního Oscara, započala tradici legendární Disneyovské rodinné zábavy, která se vyznačuje osobitým kouzlem, dobrou stavbou příběhu a bezvýhradně špičkovou animací. Příběh, ve kterém se mísí pohádkové kouzlo, dechberoucí napětí a neobyčejná odvaha, začíná jak jinak u kouzelného zrcadla. Kdo je na světě nejkrásnější? Zlá královna se dozvídá, že její půvab daleko převyšuje nevlastní dcera, Sněhurka. Proto se jí chce zbavit. Na útěku před nenávistí narazí Sněhurka na chaloupku, ve které žije sedm roztomilých trpaslíků Šmudla, Prófa, Štístko, Kejchal, Stydlín, Dřímal a nabručený Rejpal. Vzájemné setkání změní osudy každého z nich.(oficiální text distributora)', '2015-02-03 06:10:20.84', 1937, '');
