INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35100, '201503/Spider_Man_2.m4v', 'Spiderman 2', 4, 1002, 'cz', 'cz', 'en', 'Spiderman 2', 'http://www.csfd.cz/film/116933-spider-man-2/', 'Spider_Man_2.jpg', 2, 'Od chvíle, kdy se Peter Parker (TOBEY MAGUIRE) rozhodl opustit svou dlouholetou lásku Mary Jane Watson (KIRSTEN DUNST), aby se mohl v kostýmu Spider-Mana i nadále zodpovědně věnovat svému povolání superhrdiny, uplynuly dva roky. Jeho touha po milované M.J. se ale postupem času stala ještě silnější a proto si nyní pohrává s myšlenkou, že by se tajného života superhrdiny na důkaz své lásky vzdal. M.J. se však mezitím rozhodla jít dál vydala se na dráhu filmové herečky a v jejím životě figuruje nový muž. Peter má problémy i se svým nejlepším přítelem Harrym Osbornem (JAMES FRANCO). Jejich kamarádský vztah začíná být stále více zastíněn Harryho záští vůči Spider-Manovi, jehož viní ze smrti svého otce. Když už se zdá, že se situace nemůže změnit k horšímu, je Peter postaven tváří v tvář mocnému Dr. Octopusovi (ALFRED MOLINA) ďábelské stvůře s dvěma páry smrtících chapadel, která je mu více než důstojným protivníkem. Aby dokázal jeho řádění zastavit, musí se naučit žít se svým osudem a využít všech svých schopností, které jsou mu zároveň darem i prokletím.(oficiální text distributora)', '2015-03-28 03:10:03.264', 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35110, '201503/Spider_Man_1.m4v', 'Spiderman', 4, 1002, 'cz', 'cz', 'en', 'Spiderman', 'http://www.csfd.cz/film/7563-spider-man/', 'Spider_Man_1.jpg', 1, 'Peter Parker (Tobey Maguire) je dobrý a inteligentný, ale nenápadný chalan, ktorý žije so svojou tetou a strýkom a má rád fotenie a svoju spolužiačku Mary Jane (ktorá chodí s iným spolužiakom s veľkými svalmi a malým mozgom). Pri jednej školskej exkurzii ho uhryzne geneticky upravený pavúk a Peter začne naberať nadľudské schopnosti. Vie loziť po stenách, skákať na stometrové vzdialenosti, je mimoriadne ohybný a zo zápästí vystreľuje pavučinu. Stáva sa z neho Spider-Man. Lenže s príchodom jeho síl prichádza na svet aj jeho úhlavný nepriateľ - robotický lietajúci zvrhlík Green Goblin (Willem Dafoe), ktorý má v pláne podmaniť si celý svet. Aby to mal jednoduchšie, snaží sa unikátneho Spider-Mana zlákať na svoju stranu. Lenže ten, využívajúci svoje schopnosti pre konanie dobra, sa mu postaví na odpor. Súboj titánov sa začína a na popredí Peterovej vysnenej, ale komplikovane sa rozvíjajúcej romance s Mary Jane (Kirsten Dunst) sa schyľuje k veľkým digitálnym ohňostrojom 139 miliónového komixového hyperblockbusteru.(oficiální text distributora)', '2015-03-28 03:10:03.438', 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35130, '201504/PENGUINS_OF_MADAGASCAR.m4v', 'Tucnaci z Madagascaru', 3, 1001, 'cz', 'cz', 'en', 'Penguins of Madagascar', 'http://www.imdb.com/title/tt1911658/?ref_=fn_al_tt_1', 'PENGUINS_OF_MADAGASCAR.jpg', 100, 'Directed by Eric Darnell, Simon J. Smith.  With Tom McGrath, Chris Miller, Christopher Knights, Conrad Vernon. Skipper, Kowalski, Rico and Private join forces with undercover organization The North Wind to stop the villainous Dr. Octavius Brine from destroying the world as we know it.', '2015-04-10 03:10:05.158', 2014, '');
