INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35220, '201505/LAPUTA_NEBESKY_ZAMEK.m4v', 'Laputa Nebesky Zamek', 3, 0, 'cz', '  ', 'jp', 'Tenkū no shiro Laputa', 'http://www.csfd.cz/film/28495-laputa-nebesky-zamek/', 'LAPUTA_NEBESKY_ZAMEK.jpg', 0, 'Do náruče Pazua, kluka z hornického městečka, se jednoho dne snese z nebe dívka Sheeta, která má kolem krku tajemný modrý kámen. Brzy zjišťují, že se jedná o mocný kámen z levitačního krystalu, po kterém usilovně pátrají nejen ziskuchtiví vzdušní piráti, ale i tajemní vládní agenti. Krystal totiž neznamená jen velké bohatství, ale může být i klíčem k Laputě – dávnému a legendárnímu městu, které pluje po obloze a ukrývá v sobě nejen obrovské bohatství, ale také obrovskou moc', '2015-05-16 03:10:02.731', 1986, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35230, '201505/22_JUMP_STREET.m4v', '22 Jump Street', 9, 0, 'cz', '  ', 'jp', '22 Jump Street', 'http://www.csfd.cz/film/341386-22-jump-street/', '22_JUMP_STREET.jpg', 0, 'Poté, co si prošli nástrahami střední školy (dvakrát po sobě), čekají na policejní důstojníky Schmidta (Jonah Hill) a Jenka (Channing Tatum) velké změny - jsou pověřeni prací v utajení na místní vysoké škole. Ale zatímco Jenko potká spřízněnou duši ve sportovním týmu a Schmidt pronikne do řad studentů umění, začínají o svém vzájemném vztahu zvolna pochybovat. Jejich úkolem už není pouze vyřešit případ, ale jsou nuceni zjistit, zda mezi nimi může existovat společný vztah na úrovni. Pokud se těmto dvěma přerostlým puberťákům podaří vypracovat se z univerzitních nováčků ve skutečné muže, pak by vysoká škola mohla být tím nejlepším, co je kdy potkalo.', '2015-05-16 03:10:02.75', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35240, '201505/TOP_GUN.m4v', 'Top Gun', 15, 0, 'cz', '  ', 'en', 'Top Gun', 'http://www.csfd.cz/film/8288-top-gun/', 'TOP_GUN.jpg', 0, 'Mladí vojenští piloti Pete "Maverick" Mitchell a jeho kolega Goose jsou vysláni na pětitýdenní kurz do kalifornského Miramaru do školy pro elitní vojenské piloty, které se říká Top Gun. Maverick je na to hrdý a hodlá se stát nejlepším bojovým pilotem ve vojenském námořnictvu. Avšak jeho hazardérství není obdivováno, naopak mu působí mezi kolegy a nadřízenými potíže, protože Maverick porušuje pravidla a oficiální předpisy. Jeho instinkt je pro něho důležitější. Když však jeho kamarád Goose přijde při jednom zkušebním letu o život, začíná se Maverick měnit...', '2015-05-16 03:10:02.8', 1986, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35250, '201505/HOT_SHOTS_PART_DEUX.m4v', 'Hot Shots 2', 15, 0, 'cz', '  ', 'en', '', 'http://www.csfd.cz/film/5-zhave-vystrely-2/', 'HOT_SHOTS_PART_DEUX.jpg', 0, 'Machr Harley opět v akci, a to v pokračování mimořádně úspěšné parodie. Topper Harley žije v pánském klášteře, jeho nezpůsobilý ex-velitel je v Bílém domě a na Středním východě je třeba vysvobodit skupinu amerických rukojmí. Topper je jediný muž, který to může dokázat, ale příliš se váže na svou ex-přítelkyni, než aby šel podruhé do akce - dokud ho krásná agentka CIA nedostane znovu do sedla. Harley a jeho Žhavé výstřely podnikají zábavnou misi na život a na smrt, misi ve stopách akce a napětí, nastražených léček a parodií na veškeré filmové klasiky, kde unikne zesměšnění jen málokterá známá filmová tvář.', '2015-05-16 03:10:02.82', 1993, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35270, '201505/PONYO.m4v', 'Ponyo z utesu nad morem', 3, 0, 'cz', '  ', 'jp', 'Gake no ue no Ponyo', 'http://www.csfd.cz/film/225873-ponyo-z-utesu-nad-morem/', 'PONYO.jpg', 0, 'Vítejte ve světě, kde nic není nemožné. Neobyčejná animovaná pohádka se odehrává v malém městečku u moře, kde syn námořníka, pětiletý Sosuke, žije poklidný život v domě na útesu ses vou matkou Lisou. Jednoho osudného dne objeví Sosuke na pláži krásnou zlatou rybku jménem Ponyo uvězněnou v láhvi. Jak se však ukáže, Ponyo není obyčejná zlatá rybka, ale dcera mocného podmořského čaroděje. Ponyo využije kouzel jejího otce a promění se v mladou dívku, aby mohla být se Sosukem. Mořské hladiny se začnou vinou kouzel nebezpečně zvyšovat a Ponyin otec vyšle mocné mořské vlny, aby našly jeho dceru. Ponyo a Sosuke musí podniknout velké dobrodružství, aby zachránili svět a splnili Ponyin sen stát se skutečným člověkem.', '2015-05-16 06:44:27.033', 2008, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35280, '201505/Porco_Rosso.m4v', 'Porco Rosso', 3, 0, 'cz', '  ', 'jp', 'Kurenai no buta', 'http://www.csfd.cz/film/42133-porco-rosso/', 'Porco_Rosso.jpg', 0, 'Japonský film Kurenai no Buta se odehrává ve třicátých letech dvacátého století v prosluněné Itálii. Pojednává o odvážném pilotovi Porco Rossovi, kterému se stala zvláštní věc... Při jedné letecké bitvě, kdy už zraněný a vyčerpaný Porco nemohl pokračovat, se najednou ocitl nad mraky. Motor měl vypnutý, ale přesto stál na místě. A pak se to stalo. Zpoza mraků začaly vylétávat všichni, kteří se té bitvy zúčastnili, všichni Porcovi kamarádi, a začali vzlétat k nebi. Všichni, kromě Porca. Ten jediný zůstal, a od té doby má prasečí obličej. Kletba však není jediná věc se kterou se musí vypořádat. Nad Jadranským mořem se objevil nový pilot, Američan Donald Curtis, který chce být za každou cenu nejlepší a chce se s Porcem utkat v leteckém souboji. A nechce se bít jen o čest, ale i o lásku místní majitelky hotelu, a Porcovu lásku z dětství Giny. Podaří se Porcovi zlomit kletbu? Podaří se mu získat zpět svou dávnou lásku?', '2015-05-16 06:44:27.209', 1992, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35470, '201506/BOB06-Bastogne.m4v', 'Bastogne', 15, 52, 'cz', '  ', 'en', 'Bastogne', 'http://www.imdb.com/title/tt1247462/?ref_=fn_al_tt_3', 'BOB06-Bastogne.jpg', 6, 'Directed by David Leland.  With Kirk Acevedo, Doug Allen, William Armstrong, Philip Barantini. It''s Christmas 1944, and the 101st is surrounded by Germans in the forest outside of Bastogne with a lack of supplies in bitter cold and snow.', '2015-06-24 03:10:01.575', 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35480, '201506/THE_PASSION_OF_THE_CHRIST.m4v', 'Umucenie krista', 15, 0, 'en', 'en', 'en', 'Passion of the Christ', 'http://www.csfd.cz/film/70199-umuceni-krista/', 'THE_PASSION_OF_THE_CHRIST.jpg', 0, 'The Passion of the Christ je film o posledních dvanácti hodinách života Ježíše z Nazaretu. Film začíná v zahradě Getsemanské, kam se Ježíš odešel modlit po Poslední večeři. Ježíš odolává Satanovým svodům. Následkem zrady Jidáše Iškariotského je Ježíš zatčen a odveden zpátky do města Jeruzaléma, kde ho vůdcové farizeů obviní z rouhání a vynesou nad ním rozsudek smrti. Ježíš je předveden před Piláta, římského místodržícího v Judsku, který si vyslechne obvinění vznesená farizeji proti Ježíšovi. Pilát pochopí, že se jedná o politický spor, a předá záležitost králi Herodovi. Herodes vrátí Ježíše Pilátovi, a ten předloží lidu volbu, zda má propustit Ježíše nebo zločince Barabáše. Lid rozhodne, že propuštěn má být Barabáš a Ježíš má být odsouzen. Ježíš je předán římským vojákům a zbičován. Ježíš, ztýrán k nepoznání, je opět předveden před Piláta, a ten ho ukáže lidu, jako kdyby chtěl říct: "Nestačilo to už ?" Nestačí to. Pilát si nad celým dilematem myje ruce a nařídí svým lidem, aby konali tak, jak si to přeje lid. Ježíš na sebe bere svůj kříž a musí ho nést celým Jeruzalémem až na vrchol Golgoty. Na Golgotě je Ježíš přibit na kříž a prožívá poslední pokušení - strach z toho, že ho jeho Otec opustil. Svůj strach překoná, pohlédne na Marii, svou svatou matku, a řekně něco, co dokáže plně pochopit jen ona: "Dokonáno jest." Pak umírá se slovy: "Otče, do Tvých rukou odevzdávám svého ducha." V okamžiku jeho smrti se země otřásla. Scénář k filmu The Passion of the Christ vytvořil producent a režisér Mel Gibson ve spolupráci s Benediktem Fitzgeraldem (Chladnokrevně, Zelda). V podstatě se jedná o upravené pašije zkompilované ze všech čtyř evangelií - Matoušova, Markova, Lukášova a Janova. Gibson od počátku věděl, že nejdůležitější bude najít toho pravého herce pro roli Ježíše Krista. Jeho volba nakonec padla na Jima Caviezela, který naposledy hrál ve filmu Hrabě Monte Cristo. Caviezela, který je aktivní katolík, tato nabídka okamžitě oslovila. Jako pozoruhodná shoda okolností se mu jevilo i to, že právě oslavil 33. narozeniny a dostal se tedy do "Kristových let". "Ještě nikdo takhle Ježíše neukázal a podle mě Mel ukazuje, jak to doopravdy bylo. Nepoužívá násilí pro násilí, nikdy to nebylo bez důvodu. Ten realismus bude asi mnohé šokovat, ale právě proto je ten film tak nesmírně silný." Pro scény, kdy Ježíš absolvuje bití a bičování nechvalně proslulým římským vynálezem, "devítiocasou kočkou" (důtky s kovovými konci, které trhaly kůži), musel Caviezel podstoupit dlouhé přípravy v maskérně. Po makeupu, kterým měl pokrytu většinu těla, mu naskákaly puchýře, díky kterým pak nemohl v noci spát. Scény ukřižování se natáčely 14 dní, během kterých musel nést, či spíše vláčet cca 70 kg těžký kříž (skutečné kříže vážily až dvojnásobek) až na Golgotu, to vše zahalen pouze do plátna, přestože se natáčelo během zimy. Někdy byl tak podchlazen, že nemohl ani promluvit a štáb mu musel na obličej přikládat ohřívací balíčky, aby vůbec rozhýbal rty. To ještě nebylo všechno - při natáčení během bouřky do Caviezela a pomocného režiséra Jana Micheliniho udeřil blesk - sjel do Micheliniho deštníku a zasáhl i Caviezela, ale jako zázrakem nebyl ani jeden z nich vážně zraněn. Caviezel nicméně na natáčení vzpomíná v dobrém: "Kdybych tím vším neprošel, nebylo by to utrpení tak autentické, takže to tak muselo být," říká.(oficiální text distributora)', '2015-06-24 03:10:01.921', 2004, '');
