INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35330, '201505/Nausika z Vetrneho udoli_02.mp4', 'Nausika z Vetrneho udoli', 3, 0, 'jp', 'cz', 'jp', 'Kaze no tani no Naushika', 'http://www.csfd.cz/film/42132-nausika-z-vetrneho-udoli/', 'Nausika z Vetrneho udoli_02.jpg', 0, 'Legendární snímek Naušika z Větrného údolí z roku 1984 je prvním autorským celovečerním dílem nejmilovanějšího současného japonského filmaře Hayao Miyazakiho. Podobně jako ve svých pozdějších slavných filmech nabízí i tentokrát silnou hlavní hrdinku, promyšlený významový podtext a především úchvatný epický příběh. Od obrovské globální katastrofy uplynulo mnoho let, ale lidé stále žijí v menších konkurenčních společenstvích a toxická zkáza pohlcuje svět. Mocná příroda, reprezentovaná především obřím inteligentním hmyzem, vede s lidmi nelítostný boj a vše se zdá být ztracené. Objeví se však odvážná Naušika, jež s přírodními silami dokáže komunikovat a mohla by dojednat vytoužený mír...(Future Gate)', '2015-05-25 07:07:02.519', 1984, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35340, '201505/Spider-Man 3_01.mp4', 'Spider-Man 3', 4, 1002, 'cz', '  ', 'en', 'Spider-Man 3', 'http://www.csfd.cz/film/215210-spider-man-3/', 'Spider-Man 3_01.jpg', 3, 'Pavoučí muž se vrací potřetí, opět s Tobeym Maguirem v hlavní roli. Tentokrát však nebojuje jen s padouchy, ale také se svým temným a agresivním „druhým já“: Dokáže se mu ubránit a získat zpět Mary Jane, kterou tolik miluje? Režie se ujal Sam Raimi. Ve třetím pokračování dobrodružství slavného komiksového hrdiny se „pavoučí muž“, pod jehož maskou se ukrývá plachý student Peter Parker, zamilovaný do své spolužačky Mary Jane (Kirsten Dunstová), pouští do boje nejen s nebezpečnými padouchy, ale i se svým temným a agresivním „druhým já“. Jeho láska sice získala angažmá na Broadwayi v muzikálu Vzpomínky na Manhattan, ale kritika její výkon strhala. Peter ovšem nemá čas ji utěšovat. Musí do akce a díky různým nedorozuměním se mu jeho dívka začne vzdalovat. Někdejší přítel Harry, který Petera neprávem viní ze smrti svého otce, ho napadne, ale skončí v nemocnici s těžkým zraněním. Peter se také dozvídá pravdu o vrahovi svého strýce a začíná ho stále víc ovládat temná síla, měnící jeho červenomodrý kostým v černý. Dokáže se jí ubránit a získat zpět tu, kterou tolik miluje? V režii Sama Raimiho v titulní roli opět Tobey Maguire.(oficiální text distributora)', '2015-05-25 07:07:02.651', 2007, '');
