INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35540, '201506/RUKOJEMNIK.m4v', 'Rukojemnik', 15, 0, 'sk', '  ', 'sk', 'Rukojemnik', 'http://www.csfd.cz/film/77042-rukojmi/', 'RUKOJEMNIK.jpg', 0, 'Policista Jeff Talley (Bruce Willis) dlouhá léta pracoval jako vyjednavač losange-leského týmu SWAT. Náročná, stresující profese, v níž zdaleka ne všechny případy skončí šťastně, traumatizovaného policistu nakonec donutí přijmout místo policejního šéfa v idylickém městečku Bristo Camino. Sen o poklidné policejní práci daleko od velkoměsta se nečekaně změní v noční můru. Tři brutální zločinci se po nevydařeném vykradení obchodu v panice ukryjí v domě, kde jako rukojmí drží chlapce, dívku a jejich otce. Případ ještě zkomplikuje fakt, že otec unesené rodiny je účetním mafie. Mafie má nespočet spolehlivých způsobů, jak se postarat o ochranu svých tajemství. Policista pochopí, že nesmí couvnout. Musí využít všechny zkušenosti ze své bývalé praxe a znovu bojovat o ohrožené životy Bruce Willis se vrací ve špičkové formě k žánru akční krimi, se kterým spojil své největší úspěchy (série Smrtonosná past/Die Hard, Poslední skaut/The Last Boy Scout, Na dostřel/Striking Distance, Poslední zůstává/Last Man Standing). Film Rukojmí byl natočen podle stejno-jmenného bestselleru Roberta Craise, který je v literárním světě považován za skutečného mistra napětí.(oficiální text distributora)', '2015-06-24 03:10:02.093', 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35550, '201506/Zabijaci lesbickych upirek.m4v', 'Zabijaci lesbickych upirek', 9, 0, 'cz', 'en', 'en', 'Lesbian Vampire Killers', 'http://www.csfd.cz/film/235316-zabijaci-lesbickych-upirek/', 'Zabijaci lesbickych upirek.jpg', 0, 'Jimmyho pustila k vodě další holka. Přemluví proto kamaráda Fletche, aby s ním vyrazil na venkov zapomenout. Na radu vesničanů najdou nocleh v opuštěné chatě, kde shodou okolností pobývá i parta místních děvčat. Jimmymu a Fletcherovi ale přestane být do smíchu, když se z děvčat z ničeho nic vyklubou krvelačné lesbické upírky... Nemyslí na nic ... jen na krev.(oficiální text distributora)', '2015-06-24 03:10:02.113', 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35560, '201506/Mulholland drive.m4v', 'Mulholland drive', 15, 0, 'cz', '  ', 'en', 'Mulholland drive', 'http://www.csfd.cz/film/6256-mulholland-drive/', 'Mulholland drive.jpg', 0, 'Uprostřed noci na pusté losangeleské ulici Mulholland Drive se schyluje k vraždě. Do luxusního cadillacu však narazí vůz řízený opilými mladíky dřív, než stačí muž ve večerním obleku zastřelit elegantní tmavovlásku. Muži v autě zemřou, otřesená žena bloudí nočními ulicemi. Plavovláska Betty Elmsová, která právě přijela z rodného kanadského zapadákova Deep River do Hollywoodu, vyděšenou oběť ráno najde v zamčeném bytě poblíž Sunset Boulevardu, který jí na čas přenechala tetička Ruth. Neznámá, která si podle plakátu na film Gilda začne říkat Rita, ztratila paměť - a Betty se rozhodne pomoci jí pátrat po minulosti plné temných stínů. Zároveň se pokouší realizovat svůj sen stát se herečkou. Důležitou roli v navzájem propletených příbězích obou žen hraje režisér Adam Kesher přeobsazující zrovna hlavní ženskou roli ve svém příštím hitu Příběh Sylvie Northové, ale i byteček v Sierra Bonita, kam se uchylují dívky, které v hollywoodském světě snů zabloudily na své cestě ke slávě. Nesmíme ovšem zapomenout ani na herečku jménem Camilla Rhodesová, filmového bosse jménem Roque, který s lidmi zachází jako s bezmocnými loutkami, a na jeho mafiánské pohůnky - bratry Castiglianeovy. Svou roli hraje i zlověstný sen, který v restauraci Winkie''s na Sunset Boulevardu vypráví velice vyděšený muž jménem Dan... a také podivný noční podnik s názvem Klub Ticha, kabelka s pětadvaceti tisíci dolary a modrá krabice neznámého obsahu, od níž dost dlouho schází ten správný klíč. MULHOLLAND DRIVE patřil dlouhou dobu k nerealizovaným projektům Davida Lynche. V roce 1999 režisér podle svého scénáře natočil za 8 milionů dolarů dvouhodinový pilotní snímek pro televizní společnost ABC, která se však myšlenky plánovaného televizního seriálu vzdala. S využitím dalších 7 milionů od francouzské společnosti CanalPlus David Lynch materiál znovu sestříhal a upravil tak, aby v podobě celovečerního filmu vyhovoval jeho původním představám. Vznikl tak umělecky nekompromisní tvar, který formálně upomíná na Lynchův celovečerní debut ERASERHEAD a který nabízí fascinující procházku světem režisérových obsedantních nočních můr. V příběhu vystavěném na principech snu si autor pohrává s tím, co si obvykle představujeme pod logikou vyprávění, tradiční chronologií, totožností filmových postav i neměnnou podobou předmětů ve filmovém příběhu. Divák si teprve postupně skládá "skutečný" příběh filmu, který se výrazně liší od "vnějšího" příběhu, který sleduje. Stejně jako v LOST HIGHWAY totiž sledujeme cesty vyšinuté mysli, která po naplnění jistého definitivního činu zkouší výčitkám svědomí uniknout do svých představ. Možná, že touha po úniku z reality se ovšem i podruhé zvrhne jen v další zlý sen o lásce k nesprávné osobě... David Lynch v MULHOLLAND DRIVE poprvé otevřeně vypráví o absurdním světě jménem Hollywood, kde hraje důležitou roli závist, kariérismus a zákulisní manipulace. Postavu režiséra Adama Keshera, do jehož práce začnou brutálně zasahovat autoritativní síly zvnějšku, můžeme chápat jako skrytě autobiografickou. Také Lynch musí za uskutečnění svých projektů bojovat - ovšem právě film MULHOLLAND DRIVE je navzdory svému pesimistickému ladění důkazem toho, že nejen zlé, ale i příjemné sny se někdy plní.(oficiální text distributora)', '2015-06-24 03:10:02.134', 2001, '');
