INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35580, '201506/Surrogates.m4v', 'Klony', 15, 0, 'cz', 'en', 'en', 'Surrogates', 'http://www.csfd.cz/film/235722-nahradnici/', 'Surrogates.jpg', 0, 'Lidé žijí své životy vzdáleně z pohodlí svých vlastních domovů přes robotické náhradníky – sexy, fyzicky perfektní mechanické zástupce sebe sama. Je to ideální svět, kde neexistuje zločin, bolest, strach a následky. Když tuto utopii po letech přeruší první vražda, agent FBI Greer (BRUCE WILLIS) objeví rozsáhlé spiknutí za fenoménem náhradníků a musí zrušit svého vlastního náhradníka a tím riskuje svůj život, aby odkryl záhadu.(oficiální text distributora)', '2015-06-24 03:10:02.176', 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35590, '201506/SPEED.m4v', 'Nebezpecna rychlost', 15, 0, 'cz', '  ', 'en', 'Speed', 'http://www.csfd.cz/film/13427-nebezpecna-rychlost/', 'SPEED.jpg', 0, 'Nezpomaluj nebo zemřeš! Nejúspěšnější akční film roku 1994! Pevně se držte, běh napínavých scén, odvážných kaskadérských výkonů, neočekávané romance právě začíná, ve filmu, který budete chtít vidět znovu a znovu. Jack Traven, policejní specialista, který je poslán, aby zneškodnil bombu umístěnou pomstychtivým vyděračem na autobus. Situace je velmi vážná, Jack, kterému pomáhá cestující, musí udržovat rychlost autobusu nad 50mph (tj. asi 100 km/h), jinak bomba exploduje. A to není v ulicích Los Angeles vůbec jednoduché. A tak se před námi odvíjí nepřetržitá akce se spoustou překvapení, která Vás nenechá sedět klidně ve Vašich sedadlech.(oficiální text distributora)', '2015-06-24 03:10:02.196', 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35610, '201506/BOB07-Bod zlomu.m4v', 'Bod zlomu', 15, 52, 'cz', '  ', 'en', 'The Breaking Point', '', 'BOB07-Bod zlomu.jpg', 7, 'Easy Company remains in the Ardennes Forest preparing for an inevitable attack on German forces in the town of Foy. However, morale is low due to cold weather, constant shelling, poor leadership, and numerous casualties.N', '2015-06-24 03:10:02.236', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35620, '201506/BOB02-Den vsech dnu.m4v', 'Den vsech dnu', 15, 52, 'cz', '  ', 'en', 'Day of Days', 'http://www.imdb.com/title/tt1247465/?ref_=fn_al_tt_2', 'BOB02-Den vsech dnu.jpg', 2, 'Directed by Richard Loncraine.  With Nicholas Aaron, Kirk Acevedo, John Adams, Philip Barantini. Easy Company paratroopers jump behind enemy lines in Normandy on D-Day and struggle to reunite in hostile territory.', '2015-06-24 03:10:02.27', 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35630, '201506/Paddinkton.m4v', 'Paddinkton', 3, 0, 'cz', 'cz', 'en', 'Paddinkton', 'http://www.csfd.cz/film/362834-paddington/', 'Paddinkton.jpg', 0, 'Paddington není jen tak obyčejný medvídek. Pochází z nejtemnějšího Peru, nosí červený klobouček a víc než cokoliv jiného miluje marmeládové sendviče. Když zemětřesení zničí jeho domov, pošle ho teta Lucy do Londýna, aby zde našel novou rodinu. Sám a opuštěný na paddingtonském nádraží pomalu zjišťuje, že život ve velkoměstě není úplně takový, jak si ho představoval. Naštěstí ale potká rodinu Brownových, která se ho ujme i přes kategorický nesouhlas tatínka Browna - typického, spořádaného a trochu i nudného Brita. K jeho zděšení, kdekoliv se medvídek objeví, začnou se dít neuvěřitelné věci. Svými ztřeštěnými nápady často převrátí celý dům vzhůru nohama a způsobí neskonalý chaos. Svým úsměvěm a dobrým srdcem si však nakonec získá srdce celé rodiny a vše se v dobré obrací... avšak jen do okamžiku, kdy si ho všimne zákeřná ředitelka místního muzea, která z něj chce udělat vycpaný exponát...(A-Company CZ)', '2015-06-24 03:10:02.29', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35920, '201509/Pisen more.m4v', 'Pisen more', 3, 0, 'cz', '  ', 'fr', 'Le Chant de la Mer', 'http://www.csfd.cz/film/384100-song-of-the-sea/', 'Pisen more.jpg', 0, 'V bájné krajině starých Keltů žije na osamělém majáku otec s dcerou a synem. Život celé rodiny se obrátí vzhůru nohama ve chvíli, kdy se ukáže, že malá dcerka je poslední z tuleních víl. Jedině ona totiž může písní moře zachránit všechny pohádkové bytosti, které proměnila v kámen čarodějnice Macha. Po vysoce ceněné animované pohádce Brendan a tajemství Kellsu (nominace na Oscara) přichází irský režisér Tomm Moore s dalším kouzelným příběhem v celovečerním filmu. Nádherně animovaný svět keltských mýtů a nadějeplné vyprávění o síle rodičovské a sourozenecké lásky vynesly mladému režisérovi nejen srovnání s největšími mistry animovaného filmu, ale také už druhou nominaci na Oscara.(Aerofilms)', '2015-09-16 18:33:28.67', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35680, '201507/Sileny Max Dom Hromu.m4v', 'Sileny Max Dom Hromu', 15, 35071, 'cz', '  ', '  ', 'Mad Max Beyond Thunderdome', 'http://www.csfd.cz/film/6724-sileny-max-a-dom-hromu/', 'Sileny Max Dom Hromu.jpg', 3, 'Dva muži vstoupí. Odchází jen jeden. Taková jsou pravidla arény v Dómu hromů ve městě Bartertown. Zákonodárkyně Aunty Entity bude muset brzy přidat ještě jedno pravidlo: „Neštvete šíleného Maxe!“ Šílený Max a Dóm hromů s Melem Gibsonem (Smrtonosná zbraň, Maverick) v hlavní roli hrdiny Maxe, který bojuje proti barbarům post-katastrofické budoucnosti. Tentokrát se stává zachráncem kmene ztracených dětí. Hudební hvězda Tina Turner září v roli dominantní Aunty Entity, která chce v touze po moci využít Maxe k posílení své vlády nad Bartertownem. Režiséři George Miller a George Ogilvie stvořili další apokalypsu na kolech. Ve filmu uvidíte jednu z nejlepších bojových scén mezi Maxem a gladiátorem Blasterem, kdy oba používají palice, řetězové pily a cokoliv jiného, co není k podlaze Dómu hromů přibito. Po zhlédnutí Šíleného Maxe a Dómu hromů budete souhlasit s písní z filmového soundtracku „We Dont Need Another Hero.”(oficiální text distributora)', '2015-07-11 03:10:02.454', 1985, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35690, '201507/Sileny Max.m4v', 'Sileny Max', 15, 35071, 'cz', '  ', 'en', 'Mad Max', 'http://www.csfd.cz/film/6722-sileny-max/', 'Sileny Max.jpg', 1, 'Ve zpustošené zemi blízké budoucnosti vládne silnicím divoký motorkářský gang. Řádí v ulicích, terorizuje nevinné civilisty a strážcům zákona se jen směje do očí. Jednoho policistu ale podcení. Když motorkáři brutálně napadnou nejlepšího přítele a rodinu Maxe Rockatanskyho (Mel Gibson), změní se Max v běsnícího šílence, který žije jen pro jediné - pro pomstu.(Magic Box)', '2015-07-11 03:10:02.709', 1979, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35700, '201507/TB_AND_LEGEND_OF_NEVERBEAST.m4v', 'Cililing a Zver-Nezver', 3, 39, 'sk', '  ', 'en', 'Legend of the Neverbeast', 'http://www.csfd.cz/film/368770-zvonilka-a-tvor-netvor/', 'TB_AND_LEGEND_OF_NEVERBEAST.jpg', 6, 'Zvonilka a tvor Netvor je dalším ze strhujících dobrodružství Zvonilky a jejích vílích kamarádek. Veselá a talentovaná víla Chovatelka Fauna věří, že nelze soudit růži podle trní a že každý živý tvor je v jádru dobrý. A tak se spřátelí se záhadným tvorem, který je podle prastarých legend známý jako tvor Netvor. Zatímco Zvonilka a její vílí kamarádky nevědí, co si mají o záhadném zvířeti myslet, strážci Hvězdné roklinky, vílí Skauti, se rozhodnou magického tvora ulovit dřív, než přivolá na jejich společný domov zkázu. Fauna musí poslechnout své srdce a zariskovat, aby s pomocí svých vílích kamarádek záhadného tvora Netvora zachránila.(Falcon)', '2015-07-11 03:10:02.749', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35710, '201507/Sileny Max Bojovnik Silnic.m4v', 'Sileny Max Bojovnik Silnic', 15, 35071, 'cz', '  ', 'en', 'Mad Max Road Warrior', 'http://www.csfd.cz/film/6723-sileny-max-2-bojovnik-silnic/', 'Sileny Max Bojovnik Silnic.jpg', 2, 'Jen málo snímků v historii akčních filmů se může rovnat Šílenému Maxovi 2. Nabitý dobrodružstvím, rychlostí i krveprolitím vás vystřelí do světa, kde se post-katastrofická budoucnost setkává s mytologií minulosti. Nebo jednodušeji, jeden z nejúžasnějších kaskadérských filmů vůbec. Před sérií Smrtonosných zbraní a Maverickem (1994) se Mel Gibson proslavil jako Max, osamělý hrdina brázdící australské silnice v nikdy nekončícím pátrání po palivu. Proti němu a hrstce obránců skladu s palivem stojí bizarní válečníci pod vedením Humunguse, který nebere žádné zajatce. Když začne bitva, není cesty zpět, „divoká zábava a fantastický zážitek z filmu." (Sheila Benson, Los Angeles Times). Se Šíleným Maxem na vaší straně nebude o akci nouze.(Magic Box)', '2015-07-11 03:10:02.761', 1981, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000007, '201510/PORTRET_DAMY.m4v', 'Portret damy', 6, 0, 'cz', '  ', 'en', 'The Portrait of a Lady', 'http://www.csfd.cz/film/12916-portret-damy/', 'PORTRET_DAMY.jpg', 0, 'Tvrdohlavá mladá Američanka Nicole Kidmanová cestuje do Evropy, kde chce najít smysl života. Nejprve odmítá několik nápadníků, ale nakonec se nechá svést sobeckým a povrchním sběratelem umění a žen Johnem Malkovichem… Píše se rok 1872 a svobodomyslná Američanka Isabel Archerová přijíždí ke své tetě a strýcovi Touchettovým do Gardencourtu na návštěvu. Chce si ujasnit své názory na život. Odmítne lorda Warburtona, který ji požádá o ruku. Rozjede se do Londýna, kam ji doprovází její bratranec Ralph Touchett, který má tuberkulózu. V metropoli se sejde se svou americkou kamarádkou Henriettou Stackpoleovou, která právě připlula do Anglie. Henrietta jí vypráví, že s ní připlul i Caspar Goodwood, který Isabel sleduje již z Bostonu. Isabel chce, aby jí dal Goodwood dva roky času. Ralph dostane zprávu, že jeho otec umírá, a s Isabel odjedou zpátky do Gardencourtu. Ralph, který je také do Isabel zamilovaný, řekne otci, že by jí rád pomohl, aby si mohla dělat, co chce. Po smrti pana Touchetta se ukáže, že odkázal Isabel sedmdesát tisíc liber. Za paní Touchettovou přijíždí její přítelkyně Serena Merleová. Isabel okouzlí její hra na klavír a s Merleovou se spřátelí. Paní Touchettová věnuje Gardencourt Ralphovi a odstěhuje se do Florencie, kde má další dům. Isabel i Merleová jedou s ní. Merleová navštíví svého známého a sběratele umění Gilberta Osmonda, jehož dcera Pansy je po smrti manželky vychovávána v klášteře. Bezcitný muž i otec drží Pansy velmi zkrátka. Merleová Osmondovi nabídne Isabel s tím, že je bohatá dědička. Osmond se zprvu zdráhá, ale později Isabel vyzná ze zištných důvodů lásku. Isabel odjede na cesty, ale stále na Osmonda vzpomíná. Když se po roce vrátí do Florencie, za Osmonda se provdá. Dozví se to Goodwood a odjede. Ralph přizná, že Isabel miluje a řekne jí, že Osmond je sobecký a povrchní. Je mu líto, že Isabel skončila v kleci. Isabel si to však nemyslí. O tři roky později se v Římě o Osmondovu dceru Pansy začne ucházet Edward Rosier. Pansy ho miluje, ale její otec nesouhlasí s tím, aby si ho vzala. Zakáže jí, aby s ním mluvila. Za nějaký čas přijede do Říma lord Warburton s Ralphem, jehož zdravotní stav se velmi zhoršil. Oba dva vidí, jak se Isabel změnila. Snaží se s Osmondem žít v míru, ale je nešťastná. Osmond se rozhodne, že získá o dvacet let staršího Warburtona pro Pansy. Využije přátelství, které Isabel s Warburtonem pojilo a chce po ní, aby sňatek domluvila. Nedojde k němu a Osmond z neúspěchu obviní Isabel. Zpolíčkuje a ponižuje ji. Warburton odjíždí do Anglie a Isabel si přeje, aby s ním odjeli všichni její známí, včetně na smrt nemocného Ralpha. Řekne mu, že je její nejlepší přítel a že za ním kdykoli přijede. Merleová se s Osmondem pohádají, Merleová mu řekne, že je špatný člověk a že se ho jeho žena bojí. Osmond jí opáčí, že to všechno byl její nápad. Na scéně se znovu objeví Edward Rosier, který prodal svoji vzácnou sbírku a doufá, že je nyní dost bohatý na to, aby si směl Pansy vzít. Osmond dá Pansy do kláštera. Isabel zanedlouho dostane telegram, že Ralph umírá. Chce odjet do Anglie, ale Osmond ji nechce pustit, vyhrožuje jí a psychicky ji mučí...(oficiální text distributora)', '2015-10-31 21:13:18.585', 1996, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35720, '201507/KINGSMAN.m4v', 'Kingsman: Tajna sluzba', 15, 0, 'cz', '  ', 'en', 'Kingsman: The Secret Service', 'http://www.csfd.cz/film/342878-kingsman-tajna-sluzba/', 'KINGSMAN.jpg', 0, 'Tajní agenti organizace Kingsman nepotřebují mít na konci svých jmen žádné nuly. Tato špionážní služba je oproti jiným britským službám rozhodně nevládní a pochopitelně supertajná. Počet jejích agentů je pevně daný a za každého, který odešel (většinou nedobrovolně a bolestně), nastoupí pečlivě vycvičený a otestovaný nováček, kterého doporučí některý z agentů ve službě. Právě díky tomu se do výběrového systému dostávají hlavně synové významných rodin. Výjimkou z pravidla se stane až Gary „Eggsy" Price (Taron Egerton), který je obyčejným klukem vybaveným vysokým IQ, nedokončeným výcvikem u mariňáků a oceněný krajskou cenou za gymnastiku. Nyní ale tráví většinu času na ulici, je nezaměstnaný a má nakročeno do kriminálu. V pravou chvíli mu nabídne novou životní šanci jeden z agentů Kingsman – superšpión Harry Hart (Colin Firth), který nejen vypadá, ale také se chová jako pravý anglický gentleman. Aspoň většinou. Někdy to prostě nelze. A protože se jedná nejen o vtipný, ale i napínavý film, potřebujeme pořádného hlavního padoucha. Je jím Richmond Valentine (Samuel L. Jackson), miliardář a bojovník za ekologii zbavený iluzí. Nesnáší pohled na krev a chystá se odrovnat opravdu velké množství obyvatel naší planety. Díky tomu ji chce zachránit a obětavě při tom nehledí na ztrátu žádného cizího života. Pomáhat mu s tím bude Gazelle (Sofia Boutella), inteligentní krasavice s amputovanýma nohama, místo kterých má smrtící běžecké čepele. Podaří se Harrymu a Eggsymu včas odhalit pravdu o Valentinově důmyslném ďábelském plánu a zastavit ho?(CinemArt)', '2015-07-11 03:10:02.799', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35760, '201508/PRINCESS_AND_THE_FROG.m4v', 'Princezna a zaba', 3, 0, 'sk', '  ', 'en', 'Princess and the Frog', 'http://www.csfd.cz/film/239676-princezna-a-zabak/', 'PRINCESS_AND_THE_FROG.jpg', 0, 'Walt Disney Animation Studios uvádějí muzikál Princezna a žabák, animovanou komedii zasazenou do velkého města New Orleansu. Od tvůrců filmu „Malá mořská víla“ a „Aladdin“ přichází klasická pohádka s moderní zápletkou. Zachycuje krásnou dívku jménem Tiana (ANIKA NONI ROSE), žabího prince, který zoufale touží stát se opět člověkem a osudný polibek, který oba dovede do veselého dobrodružství tajuplnými bažinami Louisiany.(oficiální text distributora)', '2015-08-10 04:30:22.912', 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (36030, '201509/TANKOVY_PRAPOR.m4v', 'Tankovy prapor', 9, 0, 'cz', '  ', 'cz', 'Tankovy prapor', 'http://www.csfd.cz/film/7058-tankovy-prapor/', 'TANKOVY_PRAPOR.jpg', 0, 'Hrdinou neradostného i když převážně humorného pohledu na vojenský život v roce 1953 je rotný Dany Smiřický (s autobiografickými rysy J. Škvoreckého), potýkající se společně se svými kamarády s tupostí a "buzerací" důstojnického sboru. Popisuje Danyho milostnou avantýru s manželkou jednoho z důstojníků, Janinkou Pinkasovou, jeho vzpomínky na nepřístupnou pražskou dívku Lizetku, a především to, jak s přehledem vzdoruje důstojnické blbosti, útočící ze všech stran.(oficiální text distributora)', '2015-09-26 07:11:23.327', 1991, '');
