INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35770, '201508/EMPIRE_OF_THE_SUN.m4v', 'Rise slunce', 15, 0, 'cz', '  ', 'en', 'Empire of the Sun', 'http://www.csfd.cz/film/8636-rise-slunce/', 'EMPIRE_OF_THE_SUN.jpg', 0, 'Ještě patřil mezi děti, ale vydržet musel více, než mnozí dospělí. Píše se 7. prosinec 1941, den kdy Japonci nečekaně zaútočí na přístav Pearl Harbor. Ve stejné době začnou jejich letadla a loď bombardovat a ostřelovat Šanghaj. Syn anglických diplomatů, jedenáctiletý chlapec Jim Graham, se během nečekaného útoku, plného zmatků, šoků a hrůzy, ztratí svým rodičům. Až dosud přitom žil v poklidném a celkem luxusním závětří. V zběsilém rytmu dalších událostí se nedokáže orientovat, najednou musí bojovat o přežití. Ztráta rodičů ale nejvíce poznamenává jeho psychiku. Před téměř jistou smrtí ho v lhostejném nepřátelském prostředí zachraňuje trochu podivný americký zloděj Basie. S ním se Jim dostává do japonského zajateckého tábora Su-čou... Film Říše slunce natočil Steven Spielberg podle stejnojmenného, autobiografického bestselleru J. G. Ballarda. Podařilo se mu vytvořit neobyčejně silný a emotivní příběh, vyprávěný očima hlavního hrdiny, v němž se velkoryse inscenované válečné scény a surová realita zajateckého tábora, prolínají s magicky pojatým dětským pohledem. Ve svém protiválečném snímku stejně působivě vystihl a konfrontoval britskou, americkou a japonskou mentalitu.(oficiální text distributora)', '2015-08-10 04:30:23.108', 1987, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35780, '201508/SLEEPING_WITH_THE_ENEMY.m4v', 'Noci s nepritelem', 15, 0, 'cz', '  ', 'en', 'Sleeping with the Enemy', 'http://www.csfd.cz/film/17807-noci-s-nepritelem/', 'SLEEPING_WITH_THE_ENEMY.jpg', 0, 'Psychologický thriller o ženě, která se pokusila uniknout z manželství horšího než peklo. Když si Laura brala Martina, neměla potuchy o hloubce jeho kruté vášně. Navenek jsou ideální pár. Krásná a dokonalá hospodyňka. Pohledný, úspěšný a svůdný manžel. Ale věci nejsou takové, jak se zdají. Laura je ženou, která žije ve svém nitru s hrozným tajemstvím. Aby unikla strašlivé noční můře, je schopna riskovat cokoli - i svůj život.(oficiální text distributora)', '2015-08-10 04:30:23.148', 1991, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35790, '201508/JUPITER_ASCENDING.m4v', 'Jupiter vychazi', 4, 0, 'cz', '  ', 'en', 'Jupiter Ascending', 'http://www.csfd.cz/film/289178-jupiter-vychazi/', 'JUPITER_ASCENDING.jpg', 0, 'Když se pod noční oblohou narodila Jupiter Jones (Kunis), znamení napovídala, že je předurčena k velkým činům. Nyní již dospělá Jupiter sní o hvězdách, ale probouzí se do chladné reality uklízečky toalet a nekonečného sledu neúspěchů. Až když na Zemi dorazí Caine (Tatum), geneticky upravený bývalý armádní lovec, jehož úkolem je Jupiter sledovat, začne si uvědomovat naplnění svého osudu - její genetická výbava ji předurčuje jako dědičnou následnici královny vesmíru. Toto neobvyklé následnictví by však mohlo ovlivnit rovnováhu ve vesmíru.(Warner Bros. CZ)', '2015-08-10 04:30:23.17', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35820, '201508/HOME.m4v', 'Konecne doma', 3, 0, 'sk', '  ', 'en', 'Home', 'http://www.csfd.cz/film/319987-konecne-doma/', 'HOME.jpg', 0, 'Na naši krásnou modrou planetu dorazili Buvové, což jsou mimozemští vetřelci s mnoha chapadýlky místo nožiček, kteří dosud putovali mezigalaktickým prostorem a hledali pro sebe novou planetu, na které by mohli v klidu a snad i míru žít. Tu jejich původní zničili nepřátelští Gorgové, kteří se na ně z nějakého důvodu naštvali a pronásledují je napříč celým vesmírem. Takto ale nezačíná film o válce světů, ale kouzelná a vtipná animovaná komedie Konečně doma. Mimozemská invaze Buvů na Zemi je celkem vlídná a laskavá – Buvové svůj nový domov začnou předělávat k obrazu svému a začnou tím, že lidi prostě přestěhují do Austrálie, kde jim dopřávají díky své vyspělé buvanské technice velice pohodlný život. Při tomto stěhování jim ale proklouzne mezi prsty holka jménem Tipy, která od té chvíle hledá svou mámu. Tipy má hodně dobrodružnou povahu a kočku, která se jmenuje Čuník. Proč ne, že? Svůj malý domov na Zemi našel i Buvan jménem O, který je tak trochu fialový, strašně upovídaný především hodně přátelský. A proto hned začne připravovat večírek, na který pozve všechny a omylem pošle intergalaktickou pozvánku i Gorgům. Což je opravdu velký průšvih, díky němuž jsou na něj ostatní Buvové hodně naštvaní a někteří by mu chtěli říct i pár slov pěkně od plic. O bere všechna svá chapadýlka na ramena a potřebuje se někam na chvíli schovat, než jejich hlavy trochu vychladnou. Na útěku O narazí na ztracenou Tipy a postupně mezi nimi vzniká intergalaktické přátelství. O se snaží svým roztomilým způsobem napravit vše, co Buvané svým příchodem na Zemi způsobili, díky čemuž zmatek narůstá rychlostí světla. Tipy na oplátku seznámí Oa s humorem, uměním, hudbou a tancem, rodinou i přátelstvím. Ale především se Oh a Tipy snaží porozumět jeden druhému a najít Tipyninu mámu.(CinemArt)', '2015-08-30 20:03:08.707', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35850, '201509/Nic nez pravda.m4v', 'Nothing But the Truth', 15, 0, 'cz', '  ', 'en', 'Nic nez pravda', 'http://www.csfd.cz/film/235403-nic-nez-pravda/', 'Nic nez pravda.jpg', 0, 'Scénárista a režisér Rod Lurie uvádí dramatický film o souboji dvou silných, i když naprosto odlišných žen, které se snaží chránit to, co ve svých životech považují za důležité. Rachel Armstrong (Kate Beckinsale), mladá reportérka významného washingtonského deníku, ve svém výbušném článku odhalila identitu tajné agentky CIA Eriky Van Doren (Vera Farmiga) a upozornila na nečisté praktiky americké administrativy. Zatímco Erica musí bojovat o svou pověst v agentuře, Rachel se za každou cenu snaží ochránit svého informátora, ale stojí proti ní příliš silný soupeř – samotná americká vláda. V dalších rolích napínavého thrilleru Nic než pravda se představují Angela Bassett, David Schwimmer, Matt Dillon a Alan Alda.(oficiální text distributora)', '2015-09-06 11:29:40.51', 2008, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35860, '201509/Nocni rvacka.m4v', 'Rigged', 15, 0, 'cz', '  ', 'en', 'Nocni rvacka', 'http://www.csfd.cz/film/242696-nocni-rvacka/', 'Nocni rvacka.jpg', 0, 'Katherine Parker je boxerka, která se účastní ilegálních zápasů a dokáže porazit i muže mnohem větší, než je ona sama. Spojí se s Michaelem Dublinem, který na těchto akcích dokáže řádně vydělat. Jenže má taky spoustu nepřátel a ne zrovna čistou minulost, takže oba čeká pořádně nebezpečné dobrodružství.(H.C.E.)', '2015-09-06 11:29:40.559', 2008, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35870, '201509/Jednotka URO.m4v', 'Uro', 15, 0, 'cz', '  ', 'no', 'Jednotka URO', 'http://www.csfd.cz/film/230669-jednotka-uro/', 'Jednotka URO.jpg', 0, 'Hans Petter (Nicolai Cleve Broch) nastoupí do speciální protidrogové jednotky. Vyrůstal sám se svojí matkou a měl bouřlivé dětství. Asi proto se ve službě chová velmi horlivě a to mu působí problémy. Často pracuje v přestrojení, kdy v různých klubech sbírá informace o drogových dealerech. Jednou večer se však v klubu Front setká s Mette, svou kamarádkou z dětství, která klub vede a má problém s drogami. Kvůli Mette se pustí do nebezpečné hry, která se mu však poněkud začne vymykat z rukou.(oficiální text distributora)', '2015-09-06 11:29:40.58', 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35880, '201509/Vune damy.m4v', 'Vune damy v cernem', 15, 0, 'cz', '  ', 'fr', 'Le parfum de la dame en noir', 'http://www.csfd.cz/film/195145-vune-damy-v-cernem/', 'Vune damy.jpg', 0, 'Novomanželé Mathilda a Robert se vydávají na líbánky do zámku Hercule, který patří jejich přátelům. Zde na ně ale čeká nepříjemné překvapení. Mathilda mizí. A její muž ji chce najít stůj co stůj…(oficiální text distributora)', '2015-09-06 11:29:40.613', 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35890, '201509/Formula 51.m4v', 'Formula 51', 15, 0, 'cz', '  ', 'en', 'The 51st State', 'http://www.csfd.cz/film/14787-51st-state-the/', 'Formula 51.jpg', 0, 'Elmo McElroy (Samuel L. Jackson) příjíždí do Anglie ve skotské sukni, aby zde prodal svou novou drogu na způsob extáze. Na letišti potká ortodoxního fanouška Liverpoolu Felixe DeSouzu (Robert Carlyle), který svou oddanost svému fotbalového týmu prokáže tak, že den před zápasem Liverpool - Manchester vběhne do hospody nacpané fanoušky Manchesteru a nechá jim tam kouřící dýmovnici. Potom poznáváme profesionální vražedkyni a také ex-přítelkyni Felixe Dakotu Philips s krycím jménem Dawn (Emily Mortimer), která je najatá na vraždu McElroye. Ještě se do filmu připlete skupina nácků, kterou si McElroy několikrát výborně a vtipně "ošéfí", a zkorumpovaný detektiv Virgil Kane a tato akční komedie se rozjíždí na plné obátky.(liuk)', '2015-09-06 11:29:40.634', 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (36040, '201509/JOHNWICK.m4v', 'John Wick', 15, 0, 'cz', 'cz', 'en', 'John Wick', 'http://www.csfd.cz/film/358570-john-wick/', 'JOHNWICK.jpg', 0, 'Říká se: "Nedráždi hada bosou nohou". John Wick je zabiják na odpočinku, přesně ten typ chlápka, kterému je lepší se vyhnout. Vloupat se mu do domu, ukrást auto a zabít milovaného psa, to se prostě nemělo stát. Střední zlodějíčkové to nevěděli, jejich bossové v podsvětí ale ano. A musí jednat. Takže zatímco se John Wick vydává na svou drsnou vendetu, stává se zároveň terčem svých bývalých kolegů - nájemných zabijáků.(Bontonfilm CZ)', '2015-09-26 07:11:23.355', 2014, '');
