INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35950, '201509/INCEPTION.m4v', 'Inception - Pocatek', 4, 0, 'cz', 'cz', 'en', 'Inception', 'http://www.csfd.cz/film/254156-pocatek/', 'INCEPTION.jpg', 0, 'Dom Cobb (Leonardo DiCaprio) je zkušený zloděj, absolutní špička v nebezpečném umění extrakce: krádeže cenných tajemství z hloubi podvědomí během snění, kdy je mysl nejzranitelnější. Cobbova vzácná schopnost z něj učinila vyhledávaného hráče v zákeřném světě korporátní špionáže, ale také z něj udělala uprchlíka a stála ho všechno, co kdy miloval. Teď dostane Cobb šanci na vykoupení. Jedna poslední akce mu může vrátit normální život, ale jen pokud se mu podaří provést nemožné – počátek. Místo dokonalé krádeže musí Cobb a jeho tým specialistů zvrátit běh věcí. Jejich úkolem není myšlenku ukrást, ale vnuknout ji. Pokud uspějí, půjde o dokonalý zločin. Ale sebepečlivější plánování a odborná znalost je nemůže připravit na nebezpečného nepřítele, který nejspíš předvídá každý jejich krok. Nepřítel, kterého mohl čekat jen Cobb. Uznávaný režisér Christopher Nolan natočil mezinárodně obsazený sci-fi akční snímek „Počátek“, který vás zavede nejen na různá místa na světě ale také do intimního a nekonečného světa snů.(oficiální text distributora)', '2015-09-26 07:11:23.015', 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35960, '201509/COLD_MOUNTAIN.m4v', 'Navrat do Cold Mountain', 6, 0, 'cz', '  ', 'en', 'Cold Mountain', 'http://www.csfd.cz/film/32940-navrat-do-cold-mountain/', 'COLD_MOUNTAIN.jpg', 0, 'Zraněný voják Konfederace Inman (JUDE LAW) se ze strachu o bezpečí své milované Ady (NICOLE KIDMAN) vrací skrze válkou zpustošený Jih zpět na její farmu na Cold Mountain. Na každém kroku jeho trnité a strastiplné cesty se setkává s otroky a rebely, odráží útoky vojáků a lovců lidí a nachází nenadálé spojence i nebezpečné nepřátele. Stejným utrpením prochází také kdysi bezstarostná Ada, která je donucena chránit za pomoci nedůtklivé tulačky Ruby (RENEE ZELLWEGER) farmu svého otce před útoky zdivočelých obyvatel země. Čím blíže se k sobě Inman a Ada dostávají, tím spletitějším se stává jejich společný příběh o touze po domově, míru a lásce uprostřed pekla Americké občanské války.(oficiální text distributora)', '2015-09-26 07:11:23.134', 2003, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35970, '201509/MILUJ_ME_PROSIM.m4v', 'Miluj me! Prosim...', 6, 0, 'cz', '  ', 'en', 'Wicker Park', 'http://www.csfd.cz/film/133466-miluj-me-prosim/', 'MILUJ_ME_PROSIM.jpg', 0, 'Mladý, talentovaný bankéř je posedlý představou, že krásná dívka, kterou pravidelně potkává v kavárně, je jeho bývalou životní láskou. Zahoří touhou získat ji zpět. Začne ji pravidelně sledovat při jejich návratech domů, postupně se stále více a více přibližuje. Jednoho dne získá potřebnou odvahu a vkrade se až do jejího apartmánu. Jeho touha ho zde přivede až k nebezpečnému odhalení, které může změnit celý jeho život a nejen jeho...(H.C.E.)', '2015-09-26 07:11:23.178', 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35980, '201509/Sin City 2.m4v', 'Sin City: Zenska, pro kterou bych vrazdil', 15, 0, 'cz', 'cz', 'en', 'Sin City: A Dame to Kill For', 'http://www.csfd.cz/film/215200-sin-city-zenska-pro-kterou-bych-vrazdil/', 'Sin City 2.jpg', 0, 'Je další horká noc. Bezvětrná a vyprahlá. Dwight (Josh Brolin) hloubá o tom, na kolik způsobů to v životě zvoral a co by dal za to, kdyby dostal šanci začít znova s čistým štítem. Vtom mu zavolá jeho osudová žena Ava (Eva Green) a požádá ho, aby jí ochránil před násilnickým manželem. Dwight však netuší, že Ava má úplně jiné úmysly. Marv (Mickey Rourke) se probudí na dálnici, obklopen několika mrtvými těly a ani za nic si nemůže vzpomenout, kde se tam vzal. Sebevědomý gambler Johnny (Joseph Gordon-Levitt) chce ve hře porazit největšího padoucha ve městě, senátora Roarka. Zahrává si ale s nebezpečným mužem a události se vymknou kontrole. Striptérka Nancy (Jessica Alba) se jen těžce vyrovnává se smrtí Johna Hartigana (Bruce Willis) a je odhodlaná jeho smrt pomstít za každou cenu. V pokračování kultovního snímku Sin City se v několika propojených povídkách vracíme do města hříchu, plného kriminálníků, zkorumpovaných policistů, zabijáků a sexy žen.(A-Company CZ)', '2015-09-26 07:11:23.207', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (35990, '201509/VRANA.m4v', 'Vrana / The Crow', 4, 0, 'cz', 'cz', 'en', 'The Crow', 'http://www.imdb.com/title/tt0109506/?ref_=fn_al_tt_1', 'VRANA.jpg', 0, 'Directed by Alex Proyas.  With Brandon Lee, Michael Wincott, Rochelle Davis, Ernie Hudson. A man brutally murdered comes back to life as an undead avenger of his and his fiancée''s murder.', '2015-09-26 07:11:23.244', 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (36000, '201509/AMERICAN_SNIPER.m4v', 'Americky odstrelovac', 15, 0, 'cz', 'cz', 'en', 'American Sniper', 'http://www.csfd.cz/film/341884-americky-sniper/', 'AMERICAN_SNIPER.jpg', 0, 'Odstřelovač jednotek SEAL amerického námořnictva Chris Kyle je vyslán do Iráku s jediným úkolem: chránit své bratry ve zbrani. Jeho neomylná přesnost zachrání na bojišti velký počet životů, a když se rozšíří pověsti o jeho odvážných husarských kouscích, vyslouží si přezdívku „Legenda". Avšak jeho pověst se donese až za nepřátelské linie a na jeho hlavu je vypsána odměna a stává se z něj hlavní terč povstalců. Navzdory nebezpečí i obětem ze strany jeho rodiny se Chris se zúčastní čtyř hrůzných misí v Iráku a stává se symbolem kréda jednotek SEAL „neopustit ani jednoho muže". Ale po návratu domů Chris zjišťuje, že na válku nedokáže zapomenout.(Warner Bros. CZ)', '2015-09-26 07:11:23.268', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (36010, '201509/THE_PRESTIGE.m4v', 'Dokonaly trik', 6, 0, 'cz', '  ', 'en', 'The Prestige', 'http://www.csfd.cz/film/223160-dokonaly-trik/', 'THE_PRESTIGE.jpg', 0, 'Existuje tajemství, kvůli kterému byste zabíjeli? V tomto napínavém a vzrušujícím thrilleru od režiséra Christophera Nolana (Batman začíná, Memento) uvidíte Hugh Jackmana a Christiana Balea v rolích dvou soupeřících kouzelníků, jejichž snaha předčít jeden druhého se změní ve smrtelnou past. Scarlett Johansson jako půvabná asistentka se stane figurkou i hráčem v jejich soupeření. Skvěle obsazené vedlejší role v čele s Michaelem Cainem a Davidem Bowiem. Geniální příběh a úžasný zážitek. Přichází Dokonalý trik, pozorně se dívejte.(oficiální text distributora)', '2015-09-26 07:11:23.286', 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (36020, '201509/FAST_AND_FURIOUS_7.m4v', 'Rychle a zbesile 7', 15, 37, 'cz', 'cz', 'en', 'The Fast and The Furious 7', 'http://www.csfd.cz/film/340855-rychle-a-zbesile-7/', 'FAST_AND_FURIOUS_7.jpg', 7, 'I auta můžou létat. Alespoň pro nesmrtelnou sérii Rychle a zběsile to platí. Její hrdinové už stihli prožít a přežít neuvěřitelné věci, teď však narazí na nepřítele, vedle něhož všichni jejich dosavadní soupeři připomínají bandu dětí z mateřské školky. Statham hraje Deckarda Shawa, živý stroj na zabíjení, který vycvičila elitní vojenská jednotka. Pokud vám příjmení téhle postavy něco připomíná, pak se nepletete. Shawův mladší bratr Owen obtěžoval naše hrdiny v šestém díle tak intenzivně, až ho připravili o život. A rozrušený Shaw starší se rozhodne, že na oplátku připraví o život je a všechny jejich blízké. Bez ohledu na to, jak rychlí či zběsilí budou.(CinemArt)', '2015-09-26 07:11:23.306', 2015, '');
