INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (36050, '201509/ROMAN HOLIDAY.m4v', 'Prazdniny v Rime', 6, 0, 'cz', '  ', 'it', 'Roman Holiday', 'http://www.csfd.cz/film/9926-prazdniny-v-rime/', 'ROMAN HOLIDAY.jpg', 0, 'Romantická komedie s A. Hepburn a G. Peckem v hlavních rolích. Prázdniny v Římě byly nominovány na 10 Oscarů a Audrey Hepburn získala Oscara za svou roli novodobé princezny, která se vzpřela svým panovnickým povinnostem a díky americkému novináři (G. Peck) poznala Řím jako obyčejná mladá dívka.(oficiální text distributora)', '2015-09-26 07:11:23.38', 1953, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000000, '201510/HRA.m4v', 'Hra', 15, 0, 'cz', '  ', 'en', 'The Game', 'http://www.csfd.cz/film/2668-hra/', 'HRA.jpg', 0, 'Investiční bankéř a multimilionář Nicholas Van Orten žije ve velkém luxusním domě, osamělým životem. Může si dovolit cokoli, o nic však nemá zájem. Ani o svého bratra Conrada, který se objeví, aby mu popřál k osmačtyřicátým narozeninám. A předal mu dárek, poukázku na Hru společnosti CRS. O smyslu hry mu však odmítne cokoli prozradit. Bankéř se při pracovním jednání náhodou ocitne v sídle CRS, kde s nechutí projde řadou náročných fyzických i psychických testů, ani tady se však o hře samotné nic nedozví. Sotva však podepíše smlouvu, začíná se roztáčet zpočátku nenápadné soukolí, které hrozí rozdrtit jeho kariéru, bohatství, postavení, svobodu a nakonec i život. Jako první se objeví před jeho domem pohozená figurína, která mu připomene otcovu sebevraždu a vzápětí na něj promluví z televize známý komentátor, který ho uvítá do hry, další den má v restauraci spor se servírkou Christinou, která ho poleje. Dívka dostane okamžitě výpověď a když za ní Nicholas vyběhne z hotelu, ocitne se ve víru podivných a znepokojujících náhod. Když se druhý den vydá do hotelu, kde byla nalezena jeho kreditní karta, tvrdí mu recepční, že se tam včera ubytoval. Ve svém pokoji pak objeví na stole dávku kokainu a kompromitující fotografie, na nichž je s neznámou ženou. Vzápětí se v jeho domě objeví zoufalý Conrad. Tvrdí, že se společnosti nedokáže zbavit a je proti své vůli nucen ve Hře pokračovat. Když ho hra málem připraví o život v taxíku, který se zřítí do vody, požádá o zásah policii. Společně s ní vtrhne do sídla CRS, tady však zjistí, že firma zmizela. Podaří se mu najít Christinu, a když jsou napadeni zabijáky pracujícími pro CRS podaří se jim uniknout a ukrýt se na Nicholasově chatě v horách. Dívka tvrdí, že firma, pro kterou pracuje, se specializuje na okrádání bohatých zákazníků, a protože už se dostala ke všem jeho kontům, nepotřebuje ho a nejspíš se ho zbaví. Příliš pozdě si Nicholas všimne, že mu dívka namíchala do čaje nějakou omamnou látku. Probere se v hrobce zapadlého hřbitova kdesi v Mexiku. Bez peněz, bez dokladů, jen ve špinavém obleku. Nicholas se však nehodlá jen tak vzdát a rozhodne vybojovat si svůj život zpátky.(oficiální text distributora)', '2015-10-31 21:13:18.384', 1997, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000001, '201510/CERNYSIP20kMilPodMorom.m4v', '20000 mil pod morem', 3, 0, 'cz', '  ', 'en', '20,000 Leagues Under the Sea', 'http://www.csfd.cz/film/239769-20-000-mil-pod-morem/', 'CERNYSIP20kMilPodMorom.jpg', 0, 'Profesor Aronnax, jeho asistent Conseil a statečný harpunář Ned Land se vydávají na plavbu za objevením tajemného mořskho tvora. Jejich expedice je však zmařena, když je zajme kapitán Nemo, brazdící mořské hlubiny se svou ponorkou Nautilus. Trojice přátel má jen krátký čas na to, aby našla způsob, jak utéci a překazit Nemovi jeho ďábelské plány...(oficiální text distributora)', '2015-10-31 21:13:18.475', 1985, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000002, '201510/Tri dny kondora.m4v', 'Tri dny kondora', 15, 0, 'cz', '  ', 'en', 'Three Days of the Condor', 'http://www.csfd.cz/film/7383-tri-dny-kondora/', 'Tri dny kondora.jpg', 0, 'V oddělení CIA, krytém jako historická literární společnost, pracuje mladý literární vědec Joe Turner. On a jeho spolupracovníci se zaobírají čtením brakové literatury, aby odhalili, zda se někde neobjevují náznaky úniku informací ze CIA, hledají nové informace a nápady. Joe jednoho dne po návratu z oběda najde všechny své spolupracovníky zavražděné. Pod svým krycím jménem Kondor informuje o události centrálu, ale potom mu přijde na rozum, že v ohrození života může být i on...(oficiální text distributora)', '2015-10-31 21:13:18.496', 1975, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000003, '201510/Bily_palac.m4v', 'Bily palac', 6, 0, 'cz', '  ', 'en', 'White Palace', 'http://www.csfd.cz/film/6382-bily-palac/', 'Bily_palac.jpg', 0, 'V práci úspěšný Max (James Spader) je stár teprve 27 let, avšak již vdovec. V jedné restauraci se seznámí s mnohem starší číšnicí Norou (Susan Sarandon). Mezi oběma přese všechny rozdíly přeskočí jiskra... Citlivě natočený milostný příběh vyhýbající se laciné a falešné sentimentalitě.(Pohrobek)', '2015-10-31 21:13:18.53', 1990, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000004, '201510/Spy Kids  Spioni v akci.m4v', 'Spy Kids: Spioni v akci', 3, 0, 'cz', '  ', 'en', 'Spy Kids', 'http://www.csfd.cz/film/7748-spy-kids-spioni-v-akci/', 'Spy Kids  Spioni v akci.jpg', 0, 'Špičkoví mezinárodní špióni Gregorio (Antonio Banderas) a Ingrid (Carla Gugino) Cortezovi se po čase stráveném poklidným rodinným životem mimo aktivní službu vrací do špionážních vod. Vzápětí jsou ale zajati nepřítelem. Kromě nich zmizelo i sedm dalších prvotřídních špiónů. Zdá se, že v tom má prsty techno-génius Fegan Floop se svými nevídanými vynálezy a přeměnami. Všechno nasvědčuje tomu, že osud Cortezových - a možná i celého světa - závisí na odvaze a akceschopnosti jejich dětí: Carmen a Juniho, žáků základní školy, jejichž špiónské vzdělání pochází nanejvýš z počítačových her. Mají však k dispozici například batohy s tryskovým pohonem, nadsvětelná letadla, příruční ponorky, elektrošokové žvýkačky, ale především své vynalézavé hlavy a sílu vzájemné lásky. Nezbývá než doufat, že velcí hrdinové se můžou skrývat i v malých postavách (malém balení nebo vydání). Scénář Roberta Rodrigueze vrací diváka do dob, kdy si velká filmová dobrodružství mohla užívat celá rodina dohromady. SPY KIDS se mohly zrodit pouze ve fantazii muže, který nejenže točí vysoce kvalitní filmy, ale má i další výjimečnou kvalifikaci pro tento úkol - sám je rodičem. Robert Rodriguez chtěl tentokrát vytvořit zábavný a dobrodružný film, na který by mohl vzít i svoje děti. Ve skutečnosti, se svou vášní k pohádkám a neskutečným věcem, snil o ambiciózním dobrodružném filmu pro celou rodinu už roky. A tak se ve SPY KIDS morální konvence klasického rodinného filmu doslova vřítily do budoucnosti společně s neuvěřitelnými speciálními efekty a nespoutanou fantazií.(oficiální text distributora)', '2015-10-31 21:13:18.55', 2001, '');
