INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000006, '201510/FIGHTER.m4v', 'Fighter / Boxer', 15, 0, 'cz', '  ', 'en', 'Fighter', 'http://www.csfd.cz/film/230220-fighter/', 'FIGHTER.jpg', 0, 'Hvězdně obsazené biografické drama The Fighter v hlavních rolích s Markem Wahlbergem a Christianem Balem vypráví skutečný příběh o nepravděpodobné cestě Mickyho Warda za boxerským titulem v kategorii velterovy váhy. V jeho rockyovské cestě jej vedl nevlastní bratr Dicky, bývalý šampión, který kdysi úspěšnou kariéru vyměnil za drogy a kriminální činnost. Touha porazit soupeře v ringu se mění i v boj za rodinu a vlastní hrdost.(oficiální text distributora)', '2015-10-31 21:13:18.577', 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000008, '201510/Darek z lasky.m4v', 'Darek z lasky / Noel', 6, 0, 'cz', '  ', 'en', 'Noel', 'http://www.csfd.cz/film/189564-darek-z-lasky/', 'Darek z lasky.jpg', 0, 'V New Yorku se náhodně na Štědrý den setkává 5 lidí. Jsou propojeni sérií nevysvětlitelných příhod a s malou vzájemnou pomocí si uvědomují nový smysl svých životů.(oficiální text distributora)', '2015-10-31 21:13:18.593', 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000009, '201510/REIGN_OF_FIRE.m4v', 'Vlada ohna / Reign of Fire', 15, 0, 'cz', '  ', 'en', 'Reign of Fire', 'http://www.csfd.cz/film/18204-kralovstvi-ohne/', 'REIGN_OF_FIRE.jpg', 0, 'V dnešním Londýně pozoruje dvanáctiletý chlapec svoji matku, stavební inženýrku, jak neúmyslně probudí ze spánku trvajícího staletí obrovského oheň chrlícího draka. O dvacet let později je náš svět těmito obludami a jejich mláďaty zamořen a téměř zničen. Vrchní požárník Quinn (Christian Bale) má za úkol toto nebezpečí odvrátit a udržet malou komunitu při životě. Do jejich středu přijde Američan Van Zan který tvrdí, že ví, jak tyto obludy zabít a jak zachránit lidstvo.(oficiální text distributora)', '2015-10-31 21:13:18.626', 2002, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000010, '201510/ANGEL_HEART.m4v', 'Angel Heart', 6, 0, 'cz', '  ', 'en', 'Angel Heart', 'http://www.csfd.cz/film/7109-angel-heart/', 'ANGEL_HEART.jpg', 0, 'Amerika roku 1955, jazz a místa v autobusech vyhrazená jen pro bílé. Soukromé očko Harry Angel (Mickey Rourke) dostane neobvyklý kšeft - pro podivínského právníka jménem Louis Cyphre vypátrat jistého Johnyho Favoritea, kterého 12 let nikdo neviděl. Angel se vydává za stopou a místo odpovědí nachází jen další otázky. Poté, co kolem něj začnou umírat svědkové, Angel pochopí, že tohle není jen tak lecjaká práce a že mu začíná jít o krk...(ČSFD.cz)', '2015-10-31 21:13:18.634', 1987, '');
