INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000011, '201510/Stvoreni_pro_lasku.m4v', 'Stvoreni pro lasku', 6, 0, 'zh', 'cz', '  ', 'Fa yeung nin wa: In the Mood for Love', 'http://www.csfd.cz/film/72032-stvoreni-pro-lasku/', 'Stvoreni_pro_lasku.jpg', 0, 'Příběh se odehrává v šedesátých letech, kdy se do hongkongského bytu nezávisle na sobě stěhují dva manželké páry: novinář Chow se svojí ženou a krásná sekretářka Li-zhen s manželem. Jejich protějšky tráví mnoho času na cestách a tak si Chow a Li-zhen společně zkracují osamělé večery. Dokud se neobjeví podezření na mimomanželské aktivity jejich partnerů.(H.C.E.)', '2015-10-31 21:13:18.682', 2000, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000012, '201510/Ray.m4v', 'Ray', 15, 0, 'cz', '  ', 'en', 'Ray', 'http://www.csfd.cz/film/116531-ray/', 'Ray.jpg', 0, 'Jestliže lze život chápat jako souhrn jeho jednotlivých částí, pak příběh Ray Charlese je možno považovat za souhrn osobních vzestupů a pádů v průběhu dlouhé a slávou ověnčené kariéry ve světě hudby. Ray zažil bolest i osobní tragédie. Množství jeho hudebních stylů (jazz, rhytm & blues, rock-and-roll, gospel, country & western) je sledem a souhrnem celistvé inspirativní cesty génia s jedinečnou vizí, génia, který světu daroval možnost zcela nového způsobu vnímání hudby. RAY je dosud nevyprávěný příběh dramatického osudu americké hudební legendy, jehož vzniku předcházel patnáctiletý sběr materiálu a sledování Rayovy tvorby uznávaným filmařem Taylorem Hackfordem. S hercem Jamiem Foxxem v titulní roli prožíváme šíři a autentičnost Rayova osudu v jeho nejvýbušnějším období. Všechno začalo v okamžiku, kdy černý slepý teenager sám nastoupil do floridského autobusu a cestoval přes Státy, aby svůj styl vybrousil na jazzové scéně v Seattlu. Od Rayových počátečních bojů o to, aby se s ním zacházelo tak, jak si zaslouží, a aby mohl jít svou cestou, přes jeho objevení společností Atlantic Records a bleskový vzestup ke globální popularitě, přes boj s drogovou závislostí a vyrovnávání se s milostnými vzplanutími nás vyprávění vede zpět až do dětství. Než se stal jedním z největších hudebních idolů své generace, musel se vyrovnat s pátým rokem svého života, kdy se utopil jeho bratr George a on začal ztrácet svůj zrak. Byla to doba, která nesmazatelně ovlivnila hloubku Rayových citů a probudila v něm onu nesmrtelnou hudbu. Film není jen pietní poctou idolu jazzmanů, ale je především filmovou lahůdkou, která sklidila ovace kritiky a která pravděpodobně postoupí do závěrečného boje o ceny Americké filmové akademie.(oficiální text distributora)', '2015-10-31 21:13:18.69', 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000013, '201510/EXODUS_GODS_AND_KINGS.m4v', 'Exodus: Bohovia a krali', 15, 0, 'cz', '  ', 'en', 'Exodus: Gods and Kings', 'http://www.csfd.cz/film/343105-exodus-bohove-a-kralove/', 'EXODUS_GODS_AND_KINGS.jpg', 0, 'Ve starověkém Egyptě se vládce rovnal Bohu a jinak tomu nebylo ani v případě Ramsese (Joel Edgerton). Mocný vládce měl jediného přítele, Mojžíše (Christian Bale), který s ním vyrůstal od dětství, když se ho coby židovského nalezence ujala královská rodina. Z přítele se však stal úhlavní nepřítel, když se Mojžíšovi zjevil „jiný" Bůh a pověřil ho nelehkým úkolem – vysvobodit svůj lid z egyptského otroctví, přivést jej do zaslíbené země. Být jeho vůdcem i prorokem. Mojžíš tento úkol přijme. Než se však bude moci v čele zástupu 600 tisíc židovských otroků vydat na strastiplnou cestu do jejich nové domoviny, zažije deset egyptských ran, kterými rozezlený Bůh sešle na Egypt nesmírné útrapy, krupobití, zkázu ohněm, děsivé epidemie a smrt. Nic z toho však faraónu Ramsesovi nezabrání ve snaze Mojžíše a jeho lid za každou cestu zastavit a zničit.(CinemArt)', '2015-10-31 21:13:18.699', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000014, '201510/Marvinuv pokoj.m4v', 'Marvinuv pokoj', 6, 0, 'cz', '  ', 'en', 'Marvin''s Room', 'http://www.csfd.cz/film/11979-marvinuv-pokoj/', 'Marvinuv pokoj.jpg', 0, 'Příběh dvou sester, Lee (Meryl Streep) a Bessie (Diane Keaton), které po dvaceti letech odloučení opět sblíží Bessiina nemoc, jejiž léčení vyžaduje transplantaci kostní dřeně od příbuzného. Lee tak přijíždí na Floridu spolu se svými dvěma syny Hankem (Leonardo DiCaprio) a Charliem. Po tolika letech dochází mezi Lee a Bessie ke konfrontaci jejich velice odlišných životních stylů a názorů. Lékař (Robert de Niro) Lee sdělí, že nemoc je bohužel vážnější než se domnívala...(oficiální text distributora)', '2015-10-31 21:13:18.707', 1996, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000015, '201510/AMERICAN_HUSTLE.m4v', 'Spinavy trik - American Hustle', 15, 0, 'cz', '  ', 'en', 'American Hustle', 'http://www.csfd.cz/film/335616-spinavy-trik/', 'AMERICAN_HUSTLE.jpg', 0, 'Fiktivní snímek Špinavý trik, který je založen na reáliích jednoho z nejpozoruhodnějších skandálů v dějinách Spojených států, vypráví příběh špičkového podvodníka Irvinga Rosenfelda (Christian Bale), který je společně se svou obdobně vychytralou britskou partnerkou a milenkou Sydney Prosserovou (Amy Adams) přinucen ke spolupráci s agentem FBI Richiem DiMasem (Bradley Cooper), který se zcela vymkl kontrole. DiMaso je s sebou strhává do světa jerseyských politických machinátorů a mafiánů, který je nebezpečný i lákavý současně. Jeremy Renner představuje Carmena Polita, zaníceného, ale prchlivého politika, který se ocitl v pasti mezi podvodníky a FBI. Irvingova nevypočitatelná manželka Rosalyn (Jennifer Lawrence) by se přitom mohla stát onou příslovečnou poslední kapkou, která způsobí, že se mocné hráze intrik a podvodů definitivně protrhnou. Stejně jako předchozí filmy Davida O. Russella je i film Špinavý trik snímkem, který nelze jednoduše přiřadit k žádnému konkrétnímu žánru a který staví na syrových emocích a situacích, ve kterých se rozhoduje mezi životem a smrtí.(Falcon)', '2015-10-31 21:13:18.715', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000016, '201511/6_RETURN_OF_THE_JEDI.m4v', 'Navrat Jediho - Return of the Jedi', 4, 42000023, 'cz', 'cz', 'en', 'Return of the Jedi', 'http://www.csfd.cz/film/6474-star-wars-epizoda-vi-navrat-jediho/', '6_RETURN_OF_THE_JEDI.jpg', 6, 'Luke Skywalker se vrátil na svou rodnou planetu Tatooine ve snaze zachránit svého přítele Hana Sola z pazourů zlého gangstera Jabba Hutta. Málo však Luke ví o tom, že GALAKTICKÉ IMPÉRIUM začalo tajně stavět novou obrněnou bojovou vesmírnou stanici, která je ještě silnější než ta první - obávaná Hvězda Smrti. Po dokončení by tato zbraň přinesla jistou zkázu malé skupině rebelů, bojujících za znovuobnovení svobody v Galaxii...(oficiální text distributora)', '2015-11-18 19:31:49.739', 1983, '');
