INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000018, '201511/4_A_NEW_HOPE.m4v', 'Nova nadej - A New Hope', 4, 42000023, 'cz', 'cz', 'en', 'A New Hope', 'http://www.csfd.cz/film/6169-star-wars-epizoda-iv-nova-nadeje/', '4_A_NEW_HOPE.jpg', 4, 'Rytíři Jedi byli vyhlazeni a Impérium vládne galaxii pevnou rukou. Malá skupina povstalců se odváží vzdorovat a ukradne plány k nejmocnější zbrani Impéria, Hvězdě smrti. Imperátorův nejvěrnější služebník, Darth Vader, musí najít plány a skrytou základnu povstalců. Zpráva o princezně Lei a vůdci rebelů se dostane až k obyčejnému farmáři, Lukovi Skywalkerovi. Ten se řídí svým osudem, zachraňuje princeznu a pomáhá povstalcům svrhnout Impérium společně s takovými nezapomenutelnými spojenci jako: Obi-Wan Kenobi, domýšlivý Han Solo, loajální Chewbacca a droidové R2-D2 a C3PO.(oficiální text distributora)', '2015-11-18 19:31:49.965', 1977, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000019, '201511/3_REVENGE_OF_THE_SITH.m4v', 'Pomsta Sithov - Revenge of the Sith', 4, 42000023, 'cz', 'cz', 'en', 'Revenge of the Sith', 'http://www.csfd.cz/film/6172-star-wars-epizoda-iii-pomsta-sithu/', '3_REVENGE_OF_THE_SITH.jpg', 3, 'V třetí epizodě ságy Star Wars zuří Klonové války, které prohloubily rozpory mezi kancléřem Palpatinem a Radou Jediů. Mladý rytíř Jedi Anakin Skywalker se musí rozhodnout, na čí straně bude stát. Podlehne slibům moci a pokušení Temné strany, vstoupí do služeb zlého Darth Sidiouse a stane se z něj Darth Vader. Sithští lordové se chtějí společně pomstít a prvním krokem jejich plánu je likvidace Jediů. Pouze Yoda a Obi-Wan přežijí a musejí se Sithům postavit, což vede k dramatickému souboji světelnými meči mezi Anakinem a Obi-Wanem, který rozhodne o osudu galaxie.(oficiální text distributora)', '2015-11-18 19:31:49.985', 2005, '');
